import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';

const projects = [
  { 
    url: 'https://picsum.photos/600/800?random=1',
    title: 'EXPERIENCE',
    subtitle: 'WEBSITE',
    year: '2024'
  },
  { 
    url: 'https://picsum.photos/600/800?random=2',
    title: 'ILLUSTRATION', 
    subtitle: 'CAMPAIGN',
    year: '2024'
  },
  { 
    url: 'https://picsum.photos/600/800?random=3',
    title: 'PRODUCT',
    subtitle: 'DESIGN',
    year: '2024'
  },
  { 
    url: 'https://picsum.photos/600/800?random=4',
    title: 'BRANDING',
    subtitle: 'IDENTITY',
    year: '2023'
  },
  { 
    url: 'https://picsum.photos/600/800?random=5',
    title: 'DIGITAL',
    subtitle: 'EXPERIENCE',
    year: '2024'
  },
  { 
    url: 'https://picsum.photos/600/800?random=6',
    title: 'CREATIVE',
    subtitle: 'DIRECTION',
    year: '2023'
  },
  { 
    url: 'https://picsum.photos/600/800?random=7',
    title: 'TECHNOLOGY',
    subtitle: 'INNOVATION',
    year: '2024'
  },
  { 
    url: 'https://picsum.photos/600/800?random=8',
    title: 'VISUAL',
    subtitle: 'STORYTELLING',
    year: '2023'
  },
  { 
    url: 'https://picsum.photos/600/800?random=9',
    title: 'STRATEGIC',
    subtitle: 'THINKING',
    year: '2024'
  },
  { 
    url: 'https://picsum.photos/600/800?random=10',
    title: 'IMMERSIVE',
    subtitle: 'MEDIA',
    year: '2024'
  },
  { 
    url: 'https://picsum.photos/600/800?random=11',
    title: 'INTERACTIVE',
    subtitle: 'DESIGN',
    year: '2023'
  },
  { 
    url: 'https://picsum.photos/600/800?random=12',
    title: 'FUTURE',
    subtitle: 'VISION',
    year: '2024'
  }
];

const VRGallery = () => {
  const mountRef = useRef();
  const sceneRef = useRef();
  const rendererRef = useRef();
  const cameraRef = useRef();
  const meshesRef = useRef([]);

  useEffect(() => {
    const width = mountRef.current.clientWidth;
    const height = mountRef.current.clientHeight;

    // Сцена - глубокий черный космос
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x000000);
    sceneRef.current = scene;

    // Камера
    const camera = new THREE.PerspectiveCamera(60, width / height, 0.1, 1000);
    camera.position.set(0, 0, 0);
    cameraRef.current = camera;

    // Рендерер
    const renderer = new THREE.WebGLRenderer({ 
      antialias: true,
      alpha: false
    });
    renderer.setSize(width, height);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    mountRef.current.appendChild(renderer.domElement);
    rendererRef.current = renderer;

    // Минимальное освещение
    const ambientLight = new THREE.AmbientLight(0x404040, 0.4);
    scene.add(ambientLight);

    // Создание галереи
    const meshes = [];
    const loader = new THREE.TextureLoader();

    projects.forEach((project, i) => {
      const radius = 25;
      const angle = (i / projects.length) * Math.PI * 2;
      const x = Math.cos(angle) * radius;
      const z = Math.sin(angle) * radius;
      const y = 0;

      const geometry = new THREE.PlaneGeometry(6, 8);
      
      loader.load(project.url, (texture) => {
        const material = new THREE.MeshBasicMaterial({
          map: texture,
          side: THREE.FrontSide
        });

        const mesh = new THREE.Mesh(geometry, material);
        mesh.position.set(x, y, z);
        mesh.lookAt(0, 0, 0);
        
        scene.add(mesh);
        meshes.push(mesh);
      });
    });

    meshesRef.current = meshes;

    // Управление мышью
    let isMouseDown = false;
    let mouseX = 0;
    let mouseY = 0;
    let targetRotationY = 0;
    let targetRotationX = 0;
    let currentRotationY = 0;
    let currentRotationX = 0;

    const handleMouseMove = (event) => {
      if (isMouseDown) {
        const deltaX = event.clientX - mouseX;
        const deltaY = event.clientY - mouseY;
        
        targetRotationY += deltaX * 0.005;
        targetRotationX -= deltaY * 0.005;
        
        // Ограничение по вертикали
        targetRotationX = Math.max(-Math.PI / 3, Math.min(Math.PI / 3, targetRotationX));
        
        mouseX = event.clientX;
        mouseY = event.clientY;
      }
    };

    const handleMouseDown = (event) => {
      isMouseDown = true;
      mouseX = event.clientX;
      mouseY = event.clientY;
    };

    const handleMouseUp = () => {
      isMouseDown = false;
    };

    // Анимация
    const animate = () => {
      requestAnimationFrame(animate);

      // Плавное вращение камеры
      currentRotationY += (targetRotationY - currentRotationY) * 0.05;
      currentRotationX += (targetRotationX - currentRotationX) * 0.05;

      camera.rotation.y = currentRotationY;
      camera.rotation.x = currentRotationX;

      renderer.render(scene, camera);
    };

    animate();

    // События
    renderer.domElement.addEventListener('mousemove', handleMouseMove);
    renderer.domElement.addEventListener('mousedown', handleMouseDown);
    renderer.domElement.addEventListener('mouseup', handleMouseUp);
    renderer.domElement.addEventListener('mouseleave', handleMouseUp);

    // Изменение размера
    const handleResize = () => {
      const width = mountRef.current.clientWidth;
      const height = mountRef.current.clientHeight;
      
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
      renderer.setSize(width, height);
    };

    window.addEventListener('resize', handleResize);

    // Очистка
    return () => {
      renderer.domElement.removeEventListener('mousemove', handleMouseMove);
      renderer.domElement.removeEventListener('mousedown', handleMouseDown);
      renderer.domElement.removeEventListener('mouseup', handleMouseUp);
      renderer.domElement.removeEventListener('mouseleave', handleMouseUp);
      window.removeEventListener('resize', handleResize);
      
      meshes.forEach(mesh => {
        scene.remove(mesh);
        if (mesh.geometry) mesh.geometry.dispose();
        if (mesh.material) {
          if (mesh.material.map) mesh.material.map.dispose();
          mesh.material.dispose();
        }
      });
      
      renderer.dispose();
      if (mountRef.current) {
        mountRef.current.innerHTML = '';
      }
    };
  }, []);

  return (
    <div className="w-full h-screen bg-black overflow-hidden">
      <div
        ref={mountRef}
        className="w-full h-full cursor-grab active:cursor-grabbing"
      />
    </div>
  );
};

export default VRGallery;
