import React from 'react';
import styled from 'styled-components';

const videos = [
  { url: '/assets/video1.mp4', title: 'Perfume 1' },
  { url: '/assets/video2.mp4', title: 'Perfume 2' },
  { url: '/assets/video3.mp4', title: 'Perfume 3' },
  { url: '/assets/video4.mp4', title: 'Perfume 4' },
  { url: '/assets/video5.mp4', title: 'Perfume 5' },
];

const GalleryWrapper = styled.div`
  background: #0f0f0f;
  min-height: 100vh;
  padding: 30px;
  display: flex;
  justify-content: center;
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 20px;
  width: 100%;
  max-width: 1400px;
`;

const Card = styled.div`
  background: #1a1a1a;
  border-radius: 14px;
  overflow: hidden;
  box-shadow: 0 8px 24px rgba(255, 255, 255, 0.04);
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-6px);
    box-shadow: 0 10px 30px rgba(255, 255, 255, 0.08);
  }
`;

const Video = styled.video`
  width: 100%;
  height: 180px;
  object-fit: cover;
  background: black;
`;

const Label = styled.div`
  padding: 10px 14px;
  font-size: 0.95rem;
  text-align: center;
  color: #ccc;
`;

const SimpleVideoGallery = () => {
  return (
    <GalleryWrapper>
      <Grid>
        {videos.map((video, index) => (
          <Card key={index}>
            <Video src={video.url} controls muted playsInline />
            <Label>{video.title}</Label>
          </Card>
        ))}
      </Grid>
    </GalleryWrapper>
  );
};

export default SimpleVideoGallery;
