import React, { Suspense, useEffect, useRef, useState } from 'react';
import * as THREE from 'three';

// üß© –ú–∞—Å—Å–∏–≤ –º–µ–¥–∏–∞ (–∏—Å–ø–æ–ª—å–∑—É–µ–º placeholder –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è)
const media = [
  { type: 'image', url: 'https://picsum.photos/300/200?random=1' },
  { type: 'image', url: 'https://picsum.photos/300/200?random=2' },
  { type: 'image', url: 'https://picsum.photos/300/200?random=3' },
  { type: 'image', url: 'https://picsum.photos/300/200?random=4' },
  { type: 'image', url: 'https://picsum.photos/300/200?random=5' },
  { type: 'image', url: 'https://picsum.photos/300/200?random=6' },
];

const Carousel3D = () => {
  const mountRef = useRef(null);
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const cameraRef = useRef(null);
  const meshesRef = useRef([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!mountRef.current) return;

    // –°–æ–∑–¥–∞–µ–º —Å—Ü–µ–Ω—É
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x000000);
    sceneRef.current = scene;

    // –°–æ–∑–¥–∞–µ–º –∫–∞–º–µ—Ä—É
    const camera = new THREE.PerspectiveCamera(
      60,
      mountRef.current.clientWidth / mountRef.current.clientHeight,
      0.1,
      1000
    );
    camera.position.set(0, 0, 14);
    cameraRef.current = camera;

    // –°–æ–∑–¥–∞–µ–º —Ä–µ–Ω–¥–µ—Ä–µ—Ä
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(mountRef.current.clientWidth, mountRef.current.clientHeight);
    renderer.setClearColor(0x000000);
    mountRef.current.appendChild(renderer.domElement);
    rendererRef.current = renderer;

    // –î–æ–±–∞–≤–ª—è–µ–º –æ—Å–≤–µ—â–µ–Ω–∏–µ
    const ambientLight = new THREE.AmbientLight(0xffffff, 1.2);
    scene.add(ambientLight);

    // –°–æ–∑–¥–∞–µ–º –≥–µ–æ–º–µ—Ç—Ä–∏—é –¥–ª—è –ø–ª–æ—Å–∫–æ—Å—Ç–µ–π
    const geometry = new THREE.PlaneGeometry(3, 2);
    const loader = new THREE.TextureLoader();
    const meshes = [];

    // –ü–∞—Ä–∞–º–µ—Ç—Ä—ã —Å–µ—Ç–∫–∏
    const itemsPerRow = 5;
    const radius = 9;

    // –ó–∞–≥—Ä—É–∂–∞–µ–º –∏ —Å–æ–∑–¥–∞–µ–º –º–µ—à–∏
    Promise.all(
      media.map((item, i) => {
        return new Promise((resolve) => {
          loader.load(
            item.url,
            (texture) => {
              const material = new THREE.MeshBasicMaterial({
                map: texture,
                transparent: true,
                toneMapped: false
              });

              const mesh = new THREE.Mesh(geometry, material);

              // –í—ã—á–∏—Å–ª—è–µ–º –ø–æ–∑–∏—Ü–∏—é
              const row = Math.floor(i / itemsPerRow);
              const col = i % itemsPerRow;
              const angle = ((col - (itemsPerRow - 1) / 2) / itemsPerRow) * Math.PI;
              const x = Math.sin(angle) * radius;
              const z = Math.cos(angle) * radius;
              const y = 1.5 - row * 3;

              mesh.position.set(x, y, z);
              mesh.rotation.set(0, angle, 0);

              meshes.push(mesh);
              scene.add(mesh);
              resolve(mesh);
            },
            undefined,
            (error) => {
              console.warn('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ç–µ–∫—Å—Ç—É—Ä—ã:', error);
              resolve(null);
            }
          );
        });
      })
    ).then(() => {
      setIsLoading(false);
    });

    meshesRef.current = meshes;

    // –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –º—ã—à—å—é
    let isMouseDown = false;
    let mouseX = 0;
    let mouseY = 0;
    let targetRotationX = 0;
    let targetRotationY = 0;
    let currentRotationX = 0;
    let currentRotationY = 0;

    const handleMouseDown = (event) => {
      isMouseDown = true;
      mouseX = event.clientX;
      mouseY = event.clientY;
    };

    const handleMouseUp = () => {
      isMouseDown = false;
    };

    const handleMouseMove = (event) => {
      if (!isMouseDown) return;

      const deltaX = event.clientX - mouseX;
      const deltaY = event.clientY - mouseY;

      targetRotationY += deltaX * 0.01;
      targetRotationX += deltaY * 0.01;

      // –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º –≤–µ—Ä—Ç–∏–∫–∞–ª—å–Ω–æ–µ –≤—Ä–∞—â–µ–Ω–∏–µ
      targetRotationX = Math.max(-Math.PI / 3, Math.min(Math.PI / 3, targetRotationX));

      mouseX = event.clientX;
      mouseY = event.clientY;
    };

    renderer.domElement.addEventListener('mousedown', handleMouseDown);
    window.addEventListener('mouseup', handleMouseUp);
    window.addEventListener('mousemove', handleMouseMove);

    // –ê–Ω–∏–º–∞—Ü–∏—è
    const animate = () => {
      requestAnimationFrame(animate);

      // –ü–ª–∞–≤–Ω–æ–µ –≤—Ä–∞—â–µ–Ω–∏–µ
      currentRotationX += (targetRotationX - currentRotationX) * 0.05;
      currentRotationY += (targetRotationY - currentRotationY) * 0.05;

      // –ü—Ä–∏–º–µ–Ω—è–µ–º –≤—Ä–∞—â–µ–Ω–∏–µ –∫ –≥—Ä—É–ø–ø–µ –º–µ—à–µ–π
      meshesRef.current.forEach((mesh) => {
        if (mesh) {
          const originalPosition = mesh.userData.originalPosition || mesh.position.clone();
          if (!mesh.userData.originalPosition) {
            mesh.userData.originalPosition = originalPosition;
          }

          // –í—Ä–∞—â–∞–µ–º –ø–æ–∑–∏—Ü–∏—é –≤–æ–∫—Ä—É–≥ —Ü–µ–Ω—Ç—Ä–∞
          const rotatedPosition = originalPosition.clone();
          rotatedPosition.applyAxisAngle(new THREE.Vector3(0, 1, 0), currentRotationY);
          rotatedPosition.applyAxisAngle(new THREE.Vector3(1, 0, 0), currentRotationX);
          
          mesh.position.copy(rotatedPosition);
          
          // –ü–æ–≤–æ—Ä–∞—á–∏–≤–∞–µ–º –º–µ—à –∫ —Ü–µ–Ω—Ç—Ä—É
          mesh.lookAt(0, mesh.position.y, 0);
        }
      });

      renderer.render(scene, camera);
    };

    animate();

    // –û–±—Ä–∞–±–æ—Ç–∫–∞ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ä–∞–∑–º–µ—Ä–∞ –æ–∫–Ω–∞
    const handleResize = () => {
      if (!mountRef.current) return;
      camera.aspect = mountRef.current.clientWidth / mountRef.current.clientHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(mountRef.current.clientWidth, mountRef.current.clientHeight);
    };

    window.addEventListener('resize', handleResize);

    // –û—á–∏—Å—Ç–∫–∞
    return () => {
      renderer.domElement.removeEventListener('mousedown', handleMouseDown);
      window.removeEventListener('mouseup', handleMouseUp);
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('resize', handleResize);
      
      if (mountRef.current && renderer.domElement) {
        mountRef.current.removeChild(renderer.domElement);
      }
      renderer.dispose();
    };
  }, []);

  return (
    <div style={{
      width: '100%',
      height: '100vh',
      background: '#000',
      overflow: 'hidden',
      cursor: isLoading ? 'default' : 'grab',
      position: 'relative'
    }}>
      {isLoading && (
        <div style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          color: 'white',
          fontSize: '18px',
          zIndex: 10
        }}>
          –ó–∞–≥—Ä—É–∑–∫–∞...
        </div>
      )}
      <div ref={mountRef} style={{ width: '100%', height: '100%' }} />
    </div>
  );
};

export default Carousel3D;
