// src/components/Carousel3D.jsx
import React, { Suspense, useEffect, useRef } from 'react';
import { Canvas, useThree } from '@react-three/fiber';
import { OrbitControls, Html, useTexture } from '@react-three/drei';
import * as THREE from 'three';
import styled from 'styled-components';

const media = [
  { type: 'image', url: '/assets/img1.jpg', position: [4, 1.5, 0] },
  { type: 'image', url: '/assets/img2.jpg', position: [-4, 1.5, 0] },
  { type: 'image', url: '/assets/img3.jpg', position: [0, 1.5, -4] },
  { type: 'image', url: '/assets/img4.jpg', position: [0, 1.5, 4] },
  { type: 'video', url: '/assets/video1.mp4', position: [2, 1.5, 2] },
];

function MediaPlane({ media }) {
  const { type, url, position } = media;

  const meshRef = useRef();

  if (type === 'image') {
    const texture = useTexture(url);
    return (
      <mesh ref={meshRef} position={position}>
        <planeGeometry args={[3, 2]} />
        <meshBasicMaterial map={texture} toneMapped={false} />
      </mesh>
    );
  }

  if (type === 'video') {
    const video = document.createElement('video');
    video.src = url;
    video.crossOrigin = 'Anonymous';
    video.loop = true;
    video.muted = true;
    video.playsInline = true;
    video.autoplay = true;

    useEffect(() => {
      video.play();
    }, []);

    const texture = new THREE.VideoTexture(video);

    return (
      <mesh ref={meshRef} position={position}>
        <planeGeometry args={[3, 2]} />
        <meshBasicMaterial map={texture} toneMapped={false} />
      </mesh>
    );
  }

  return null;
}

const Carousel3D = () => {
  return (
    <Wrapper>
      <Canvas camera={{ position: [0, 1.5, 6], fov: 60 }}>
        <ambientLight intensity={1.5} />
        <Suspense fallback={<Html center>Loading...</Html>}>
          {media.map((m, i) => (
            <MediaPlane key={i} media={m} />
          ))}
        </Suspense>
        <OrbitControls enableZoom={false} enablePan={false} />
      </Canvas>
    </Wrapper>
  );
};

export default Carousel3D;

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: radial-gradient(circle, #000000, #0a0a0a);
  cursor: grab;
`;
