// src/App.jsx
import React, { useState } from "react";
import VRExperience from './components/VRExperience';
import Preloader from "./components/Preloader";
import Home from "./pages/Home";

const App = () => {
  const [isLoading, setIsLoading] = useState(true);

  const handlePreloadFinish = () => {
    console.log("Preloader finished"); // добавь это
    setIsLoading(false);
  };

  return (
    <>
      <VRExperience />
      {isLoading ? <Preloader onFinish={handlePreloadFinish} /> : <Home />}
    </>
  );
};

export default App;
