import React, { useEffect, useRef, useState } from 'react';
import * as THREE from 'three';
import { CSS2DRenderer, CSS2DObject } from 'three/examples/jsm/renderers/CSS2DRenderer';

const media = [
  { type: 'image', url: 'https://picsum.photos/300/200?random=1', title: 'Project One' },
  { type: 'image', url: 'https://picsum.photos/300/200?random=2', title: 'Project Two' },
  { type: 'image', url: 'https://picsum.photos/300/200?random=3', title: 'Project Three' },
  { type: 'image', url: 'https://picsum.photos/300/200?random=4', title: 'Project Four' },
  { type: 'image', url: 'https://picsum.photos/300/200?random=5', title: 'Project Five' },
  { type: 'image', url: 'https://picsum.photos/300/200?random=6', title: 'Project Six' },
];

const Carousel3D = () => {
  const mountRef = useRef();
  const [hoveredMesh, setHoveredMesh] = useState(null);

  useEffect(() => {
    const width = mountRef.current.clientWidth;
    const height = mountRef.current.clientHeight;

    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x000000);
    scene.fog = new THREE.Fog(0x000000, 15, 40);

    const camera = new THREE.PerspectiveCamera(60, width / height, 0.1, 1000);
    camera.position.set(0, 0, 20);

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(width, height);
    mountRef.current.appendChild(renderer.domElement);

    const labelRenderer = new CSS2DRenderer();
    labelRenderer.setSize(width, height);
    labelRenderer.domElement.style.position = 'absolute';
    labelRenderer.domElement.style.top = '0';
    mountRef.current.appendChild(labelRenderer.domElement);

    const ambientLight = new THREE.AmbientLight(0xffffff, 1.5);
    scene.add(ambientLight);

    const textureLoader = new THREE.TextureLoader();
    const geometry = new THREE.PlaneGeometry(4, 2.5);
    const meshes = [];

    const itemsPerRow = 5;
    const radius = 12;

    media.forEach((item, i) => {
      textureLoader.load(item.url, (texture) => {
        const material = new THREE.MeshBasicMaterial({ map: texture });
        const mesh = new THREE.Mesh(geometry, material);

        const row = Math.floor(i / itemsPerRow);
        const col = i % itemsPerRow;
        const angle = ((col - (itemsPerRow - 1) / 2) / itemsPerRow) * Math.PI;
        const x = Math.sin(angle) * radius;
        const z = Math.cos(angle) * radius;
        const y = 2 - row * 3;

        mesh.position.set(x, y, z);
        mesh.rotation.y = angle;
        mesh.userData.originalPosition = mesh.position.clone();
        mesh.userData.originalScale = mesh.scale.clone();

        const div = document.createElement('div');
        div.className = 'label';
        div.textContent = item.title;
        div.style.color = 'white';
        div.style.fontSize = '14px';
        const label = new CSS2DObject(div);
        label.position.set(0, -1.6, 0);
        mesh.add(label);

        scene.add(mesh);
        meshes.push(mesh);
      });
    });

    let isDragging = false;
    let prevMouseX = 0;
    let prevMouseY = 0;
    let rotationY = 0;
    let rotationX = 0;
    let targetY = 0;
    let targetX = 0;

    const onMouseDown = (e) => {
      isDragging = true;
      prevMouseX = e.clientX;
      prevMouseY = e.clientY;
    };

    const onMouseUp = () => {
      isDragging = false;
    };

    const onMouseMove = (e) => {
      if (!isDragging) return;
      const dx = e.clientX - prevMouseX;
      const dy = e.clientY - prevMouseY;
      targetY += dx * 0.01;
      targetX += dy * 0.005;
      targetX = Math.max(-Math.PI / 6, Math.min(Math.PI / 6, targetX));
      prevMouseX = e.clientX;
      prevMouseY = e.clientY;
    };

    const animate = () => {
      requestAnimationFrame(animate);

      rotationY += (targetY - rotationY) * 0.05;
      rotationX += (targetX - rotationX) * 0.05;

      meshes.forEach((mesh) => {
        const p = mesh.userData.originalPosition.clone();
        p.applyAxisAngle(new THREE.Vector3(0, 1, 0), rotationY);
        p.applyAxisAngle(new THREE.Vector3(1, 0, 0), rotationX);
        mesh.position.copy(p);
        mesh.lookAt(0, mesh.position.y, 0);
      });

      renderer.render(scene, camera);
      labelRenderer.render(scene, camera);
    };

    animate();

    renderer.domElement.addEventListener('mousedown', onMouseDown);
    window.addEventListener('mouseup', onMouseUp);
    window.addEventListener('mousemove', onMouseMove);

    const onResize = () => {
      const width = mountRef.current.clientWidth;
      const height = mountRef.current.clientHeight;
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
      renderer.setSize(width, height);
      labelRenderer.setSize(width, height);
    };

    window.addEventListener('resize', onResize);

    return () => {
      window.removeEventListener('mouseup', onMouseUp);
      window.removeEventListener('mousemove', onMouseMove);
      window.removeEventListener('resize', onResize);
    };
  }, []);

  return (
    <div
      ref={mountRef}
      style={{
        width: '100%',
        height: '100vh',
        background: '#000',
        overflow: 'hidden',
        position: 'relative',
        cursor: 'grab',
      }}
    />
  );
};

export default Carousel3D;
