import React, { useState } from "react";

const FooterNav = () => {
  const [active, setActive] = useState("Work");
  const [hovered, setHovered] = useState(null);
  
  const navItems = [
    { name: "Work", icon: "ðŸ’¼", color: "from-purple-500 to-pink-500" },
    { name: "About", icon: "ðŸ‘‹", color: "from-blue-500 to-cyan-500" },
    { name: "Careers", icon: "ðŸš€", color: "from-green-500 to-emerald-500" }
  ];

  return (
    <div className="fixed bottom-8 left-0 right-0 flex justify-center z-50 px-4">
      <nav className="relative flex bg-black/30 backdrop-blur-2xl border border-white/20 rounded-full p-2 gap-2 shadow-[0_25px_50px_-12px_rgba(0,0,0,0.25)] hover:shadow-[0_35px_60px_-12px_rgba(0,0,0,0.4)] transition-all duration-500">
        
        {/* Floating orb background */}
        <div className="absolute inset-0 rounded-full bg-gradient-to-r from-purple-500/10 via-blue-500/10 to-green-500/10 animate-spin-slow opacity-50"></div>
        
        {/* Glowing ring */}
        <div className="absolute -inset-1 rounded-full bg-gradient-to-r from-purple-500/20 via-blue-500/20 to-green-500/20 blur-sm animate-pulse"></div>
        
        {navItems.map((item, index) => (
          <button
            key={item.name}
            onClick={() => setActive(item.name)}
            onMouseEnter={() => setHovered(item.name)}
            onMouseLeave={() => setHovered(null)}
            className={`
              relative px-6 py-4 rounded-full font-semibold text-sm transition-all duration-500 ease-out overflow-hidden group
              ${active === item.name 
                ? 'bg-white text-gray-900 shadow-2xl transform scale-110 rotate-1' 
                : 'bg-transparent text-white/90 hover:bg-white/10 hover:text-white hover:scale-105 hover:-rotate-1'
              }
            `}
            style={{
              animationDelay: `${index * 100}ms`
            }}
          >
            
            {/* Animated background gradient */}
            <div className={`
              absolute inset-0 bg-gradient-to-r ${item.color} opacity-0 transition-all duration-300 group-hover:opacity-20
              ${active === item.name ? 'opacity-30' : ''}
            `}></div>
            
            {/* Ripple effect */}
            {hovered === item.name && (
              <div className="absolute inset-0 rounded-full animate-ping bg-white/30"></div>
            )}
            
            {/* Floating particles */}
            {active === item.name && (
              <>
                <div className="absolute -top-1 -right-1 w-2 h-2 bg-yellow-400 rounded-full animate-bounce opacity-80"></div>
                <div className="absolute -bottom-1 -left-1 w-1.5 h-1.5 bg-pink-400 rounded-full animate-bounce opacity-60" style={{animationDelay: '0.5s'}}></div>
                <div className="absolute top-1 left-1 w-1 h-1 bg-blue-400 rounded-full animate-bounce opacity-70" style={{animationDelay: '1s'}}></div>
              </>
            )}
            
            {/* Content */}
            <span className="relative z-10 flex items-center gap-2">
              <span className={`text-lg transition-transform duration-300 ${active === item.name ? 'animate-bounce' : ''}`}>
                {item.icon}
              </span>
              <span className="tracking-wide">{item.name}</span>
            </span>
            
            {/* Shine effect */}
            <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent -skew-x-12 transform translate-x-[-100%] group-hover:translate-x-[100%] transition-transform duration-1000 ease-out"></div>
            
            {/* Border glow */}
            {active === item.name && (
              <div className={`absolute -inset-0.5 rounded-full bg-gradient-to-r ${item.color} opacity-50 blur-sm animate-pulse`}></div>
            )}
          </button>
        ))}
        
        {/* Floating background elements */}
        <div className="absolute -top-4 left-4 w-2 h-2 bg-purple-400/30 rounded-full animate-float"></div>
        <div className="absolute -bottom-3 right-8 w-1.5 h-1.5 bg-blue-400/30 rounded-full animate-float-delayed"></div>
        <div className="absolute top-0 right-4 w-1 h-1 bg-green-400/30 rounded-full animate-float-slow"></div>
      </nav>
      
      <style jsx>{`
        @keyframes spin-slow {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }
        @keyframes float {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-10px); }
        }
        @keyframes float-delayed {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-8px); }
        }
        @keyframes float-slow {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-6px); }
        }
        .animate-spin-slow { animation: spin-slow 20s linear infinite; }
        .animate-float { animation: float 3s ease-in-out infinite; }
        .animate-float-delayed { animation: float-delayed 3s ease-in-out infinite 1s; }
        .animate-float-slow { animation: float-slow 4s ease-in-out infinite 0.5s; }
      `}</style>
    </div>
  );
};

export default FooterNav;
