// src/pages/Home.jsx
import React, { useRef, useEffect } from "react";
import Carousel3D from "../components/Carousel3D";
import EnhancedVideoGallery from "../components/Carousel3D";
import styled from "styled-components";
import Navbar from "../components/Navbar";
import FooterNav from "../components/FooterNav";
import About from "./About";

const Wrapper = styled.div`
  background: black;
  color: white;
  min-height: 100vh;
  overflow-x: hidden;
  padding-bottom: 100px;
`;

const Home = ({ activeTab, setActiveTab, audioSrc }) => {
  const backgroundAudioRef = useRef(null);

  useEffect(() => {
    const audio = backgroundAudioRef.current;
    if (!audio || !audioSrc) return;

    audio.pause();
    audio.src = audioSrc;
    audio.load();

    const play = async () => {
      try {
        await audio.play();
      } catch (err) {
        console.log("Autoplay blocked:", err);
      }
    };

    play();
  }, [audioSrc]);

  return (
    <Wrapper>
      {/* Фоновая музыка */}
      <audio ref={backgroundAudioRef} autoPlay loop>
        <source src={audioSrc} type="audio/mpeg" />
        Your browser does not support the audio element.
      </audio>

      <Navbar audioSrc={audioSrc} />

      {activeTab === "Work" && <Carousel3D />}

      {activeTab === "About" && <About backgroundAudioRef={backgroundAudioRef} />}

      {activeTab === "Careers" && (
        <div className="text-center text-white mt-20">
          <h2 className="text-3xl font-semibold mb-4">Careers</h2>
          <p className="text-gray-400">We're hiring soon!</p>
        </div>
      )}

      <FooterNav active={activeTab} setActive={setActiveTab} />
    </Wrapper>
  );
};

export default Home;
