import React, { useEffect, useRef, useState, useCallback } from "react";

const About = () => {
  const headerRef = useRef(null);
  const sectionsRef = useRef([]);
  const audioRef = useRef(null);
  const videoRef = useRef(null);
  const backgroundRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [scrollProgress, setScrollProgress] = useState(0);

  const updateMousePosition = useCallback((e) => {
    setMousePosition({
      x: (e.clientX / window.innerWidth) * 100,
      y: (e.clientY / window.innerHeight) * 100,
    });
  }, []);

  const updateScrollProgress = useCallback(() => {
    const scrollTop = window.scrollY;
    const docHeight = document.documentElement.scrollHeight - window.innerHeight;
    const progress = Math.min(scrollTop / docHeight, 1);
    setScrollProgress(progress);
  }, []);

  useEffect(() => {
    window.addEventListener('mousemove', updateMousePosition);
    window.addEventListener('scroll', updateScrollProgress);

    return () => {
      window.removeEventListener('mousemove', updateMousePosition);
      window.removeEventListener('scroll', updateScrollProgress);
    };
  }, [updateMousePosition, updateScrollProgress]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.style.opacity = '1';
            entry.target.style.transform = 'translateY(0)';

            if (entry.target.classList.contains('text-reveal')) {
              const spans = entry.target.querySelectorAll('.word-span');
              spans.forEach((span, index) => {
                setTimeout(() => {
                  span.style.opacity = '1';
                  span.style.transform = 'translateY(0)';
                }, index * 100);
              });
            }
          }
        });
      },
      { threshold: 0.1, rootMargin: '0px 0px -50px 0px' }
    );

    if (headerRef.current) {
      observer.observe(headerRef.current);
    }

    sectionsRef.current.forEach(section => {
      if (section) observer.observe(section);
    });

    return () => observer.disconnect();
  }, []);

  const handleVideoClick = () => {
    const audio = audioRef.current;
    const video = videoRef.current;

    if (!audio || !video) return;

    if (!isPlaying) {
      audio.pause();
      video.muted = false;
      video.play();
    } else {
      audio.play();
      video.pause();
      video.muted = true;
    }

    setIsPlaying(!isPlaying);
  };

  const createAnimatedText = (text, className = "") => {
    return text.split(' ').map((word, index) => (
      <span
        key={index}
        className={`word-span inline-block opacity-0 transform translate-y-4 transition-all duration-500 ${className}`}
        style={{ transitionDelay: `${index * 50}ms` }}
      >
        {word}&nbsp;
      </span>
    ));
  };

  return (
    <div className="min-h-screen bg-black text-white overflow-hidden relative">
      {/* Прогресс бар */}
      <div className="fixed top-0 left-0 right-0 z-50 h-1 bg-gray-800">
        <div
          className="h-full bg-gradient-to-r from-purple-500 to-blue-500 transition-all duration-150 ease-out"
          style={{ width: `${scrollProgress * 100}%` }}
        ></div>
      </div>

      {/* Динамический фон с параллаксом */}
      <div
        ref={backgroundRef}
        className="fixed inset-0 pointer-events-none transition-all duration-700 ease-out"
        style={{
          transform: `translate(${(mousePosition.x - 50) * 0.02}px, ${(mousePosition.y - 50) * 0.02}px)`
        }}
      >
        <div className="absolute top-1/4 -right-1/4 w-96 h-96 bg-gradient-to-r from-purple-500/20 to-blue-500/20 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-1/4 -left-1/4 w-80 h-80 bg-gradient-to-r from-pink-500/20 to-orange-500/20 rounded-full blur-3xl animate-pulse" style={{ animationDelay: '1s' }}></div>
        <div className="absolute top-1/2 left-1/2 w-64 h-64 bg-gradient-to-r from-cyan-500/10 to-teal-500/10 rounded-full blur-2xl animate-pulse" style={{ transform: `translate(-50%, -50%) scale(${1 + Math.sin(scrollProgress * Math.PI) * 0.5})`, animationDelay: '2s' }}></div>
      </div>

      <div className="relative z-10 px-6 md:px-16 lg:px-24 xl:px-32">
        {/* остальной код без изменений */}

        {/* Видео секция */}
        <section className="mt-24 flex justify-center items-center opacity-0 transform translate-y-8 transition-all duration-1000 ease-out delay-[1000ms]" ref={el => sectionsRef.current[4] = el}>
          <div className="relative w-full max-w-4xl">
            <div className="relative aspect-video rounded-xl overflow-hidden shadow-2xl border border-gray-700 cursor-pointer group hover:shadow-3xl hover:shadow-purple-500/20 transition-all duration-500" onClick={handleVideoClick}>
              <video
                ref={videoRef}
                className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-700"
                muted
                loop
                playsInline
              >
                <source src="/assets/astana.mp4" type="video/mp4" />
                Your browser does not support the video tag.
              </video>
              {/* Оверлей и элементы управления без изменений */}
            </div>

            <audio ref={audioRef} loop>
              <source src="/assets/music.mp3" type="audio/mpeg" />
              Your browser does not support the audio element.
            </audio>
          </div>
        </section>
      </div>
    </div>
  );
};

export default About;
