import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { CSS2DRenderer, CSS2DObject } from 'three/examples/jsm/renderers/CSS2DRenderer';

const media = [
  { url: '/assets/img1.jpg', title: 'Work 1' },
  { url: '/assets/img2.jpg', title: 'Work 2' },
  { url: '/assets/img3.jpg', title: 'Work 3' },
  { url: '/assets/img4.jpg', title: 'Work 4' },
  { url: '/assets/img5.jpg', title: 'Work 5' },
  { url: '/assets/img6.jpg', title: 'Work 6' }
];

const Carousel3D = () => {
  const mountRef = useRef();

  useEffect(() => {
    const width = mountRef.current.clientWidth;
    const height = mountRef.current.clientHeight;

    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x000000);
    scene.fog = new THREE.FogExp2(0x000000, 0.03);

    const camera = new THREE.PerspectiveCamera(60, width / height, 0.1, 1000);
    camera.position.set(0, 0, 20);

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(width, height);
    renderer.setPixelRatio(window.devicePixelRatio);
    mountRef.current.appendChild(renderer.domElement);

    const labelRenderer = new CSS2DRenderer();
    labelRenderer.setSize(width, height);
    labelRenderer.domElement.style.position = 'absolute';
    labelRenderer.domElement.style.top = '0';
    labelRenderer.domElement.style.pointerEvents = 'none';
    mountRef.current.appendChild(labelRenderer.domElement);

    // LIGHT
    const ambientLight = new THREE.AmbientLight(0xffffff, 1.3);
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
    directionalLight.position.set(5, 10, 7.5);
    scene.add(directionalLight);

    const raycaster = new THREE.Raycaster();
    const mouse = new THREE.Vector2();
    const meshes = [];

    const loader = new THREE.TextureLoader();
    const geometry = new THREE.PlaneGeometry(3.6, 2.3);
    const itemsPerRow = 3;
    const radius = 10;

    media.forEach((item, i) => {
      loader.load(item.url, (texture) => {
        texture.colorSpace = THREE.SRGBColorSpace;
        const material = new THREE.MeshBasicMaterial({ map: texture });
        const mesh = new THREE.Mesh(geometry, material);

        const row = Math.floor(i / itemsPerRow);
        const col = i % itemsPerRow;
        const angle = ((col - (itemsPerRow - 1) / 2) / itemsPerRow) * Math.PI;
        const x = Math.sin(angle) * radius;
        const z = Math.cos(angle) * radius;
        const y = 2 - row * 3;

        mesh.position.set(x, y, z);
        mesh.rotation.y = angle;
        mesh.userData.originalPosition = mesh.position.clone();
        mesh.userData.originalScale = mesh.scale.clone();
        mesh.userData.originalRotation = mesh.rotation.clone();

        // Label
        const labelDiv = document.createElement('div');
        labelDiv.textContent = item.title;
        labelDiv.style.color = 'white';
        labelDiv.style.fontSize = '13px';
        labelDiv.style.fontFamily = 'sans-serif';
        const label = new CSS2DObject(labelDiv);
        label.position.set(0, -1.5, 0);
        mesh.add(label);

        scene.add(mesh);
        meshes.push(mesh);
      });
    });

    // Управление
    let isMouseDown = false;
    let prevX = 0;
    let prevY = 0;
    let targetRotationX = 0;
    let targetRotationY = 0;
    let rotationX = 0;
    let rotationY = 0;
    let hovered = null;

    const handleMouseDown = (e) => {
      isMouseDown = true;
      prevX = e.clientX;
      prevY = e.clientY;
    };

    const handleMouseMove = (e) => {
      const rect = renderer.domElement.getBoundingClientRect();
      mouse.x = ((e.clientX - rect.left) / rect.width) * 2 - 1;
      mouse.y = -((e.clientY - rect.top) / rect.height) * 2 + 1;

      if (!isMouseDown) return;
      const dx = e.clientX - prevX;
      const dy = e.clientY - prevY;
      targetRotationY += dx * 0.01;
      targetRotationX += dy * 0.008;
      targetRotationX = Math.max(-Math.PI / 4, Math.min(Math.PI / 4, targetRotationX));
      prevX = e.clientX;
      prevY = e.clientY;
    };

    const handleMouseUp = () => {
      isMouseDown = false;
    };

    const animate = () => {
      requestAnimationFrame(animate);
      rotationX += (targetRotationX - rotationX) * 0.05;
      rotationY += (targetRotationY - rotationY) * 0.05;

      meshes.forEach((mesh) => {
        const p = mesh.userData.originalPosition.clone();
        p.applyAxisAngle(new THREE.Vector3(0, 1, 0), rotationY);
        p.applyAxisAngle(new THREE.Vector3(1, 0, 0), rotationX);
        mesh.position.copy(p);
        mesh.lookAt(0, mesh.position.y, 0);
      });

      // HOVER
      raycaster.setFromCamera(mouse, camera);
      const intersects = raycaster.intersectObjects(meshes);
      if (intersects.length > 0) {
        const obj = intersects[0].object;
        if (hovered !== obj) {
          if (hovered) hovered.scale.copy(hovered.userData.originalScale);
          hovered = obj;
          hovered.scale.set(1.1, 1.1, 1.1);
        }
      } else {
        if (hovered) {
          hovered.scale.copy(hovered.userData.originalScale);
          hovered = null;
        }
      }

      renderer.render(scene, camera);
      labelRenderer.render(scene, camera);
    };

    animate();

    renderer.domElement.addEventListener('mousedown', handleMouseDown);
    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mouseup', handleMouseUp);

    const onResize = () => {
      const width = mountRef.current.clientWidth;
      const height = mountRef.current.clientHeight;
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
      renderer.setSize(width, height);
      labelRenderer.setSize(width, height);
    };

    window.addEventListener('resize', onResize);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
      window.removeEventListener('resize', onResize);
    };
  }, []);

  return (
    <div
      ref={mountRef}
      style={{
        width: '100%',
        height: '100vh',
        background: '#000',
        overflow: 'hidden',
        position: 'relative',
        cursor: 'grab',
      }}
    />
  );
};

export default Carousel3D;
