import React, { useState } from "react";
import styled from "styled-components";

// Кастомные иконки (точки или прямоугольники)
const Dot = styled.div`
  width: 5px;
  height: 5px;
  background-color: white;
  border-radius: 50%;
  margin-right: 8px;
`;

const Nav = styled.div`
  position: fixed;
  top: 0;
  width: 100%;
  padding: 1rem 2rem;
  display: flex;
  justify-content: space-between;
  z-index: 100;
`;

const Logo = styled.img`
  width: 80px;
  height: auto;
  cursor: pointer;
`;

const Button = styled.button`
  background: white;
  color: black;
  border-radius: 999px;
  padding: 0.5rem 1rem;
  font-size: 0.9rem;
  font-weight: bold;
  border: none;
  cursor: pointer;
`;

const SoundButton = styled.div`
  display: flex;
  align-items: center;
  justify-content: center; /* Центрируем содержимое */
  cursor: pointer;
  font-size: 0.9rem;
  color: white;
  letter-spacing: 1px;
`;

const Navbar = () => {
  const [isSoundOn, setIsSoundOn] = useState(true); // Состояние для переключателя звука

  const handleSoundToggle = () => {
    setIsSoundOn(!isSoundOn); // Переключаем состояние
  };

  const handleLogoClick = (event) => {
    event.preventDefault(); // Предотвращает перезагрузку страницы
    window.history.pushState({}, "", "/"); // Изменяет URL без перезагрузки
  };

  return (
    <Nav>
      <a href="/" onClick={handleLogoClick}>
        <Logo src="/assets/logo.png" alt="logo" />
      </a>
      <div style={{ display: "flex", alignItems: "center" }}>
        <SoundButton onClick={handleSoundToggle}>
          <Dot /> {/* Кастомная точка как иконка */}
          <span>{isSoundOn ? "Sound [ON]" : "Sound [OFF]"}</span>
        </SoundButton>
        <Button>Let’s Talk</Button>
      </div>
    </Nav>
  );
};

export default Navbar;
