import React, { useState, useEffect } from "react";
import styled, { keyframes, css } from "styled-components";

const pulseGlow = keyframes`
  0%, 100% { 
    transform: scale(1);
    opacity: 0.8;
  }
  50% { 
    transform: scale(1.1);
    opacity: 1;
  }
`;

const Nav = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  padding: ${props => props.scrolled ? '16px 48px' : '24px 48px'};
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 1000;
  background: ${props => props.scrolled ? 'rgba(0, 0, 0, 0.95)' : 'rgba(0, 0, 0, 0.85)'};
  backdrop-filter: blur(24px);
  -webkit-backdrop-filter: blur(24px);
  border-bottom: 1px solid ${props => props.scrolled ? 'rgba(255, 255, 255, 0.08)' : 'rgba(255, 255, 255, 0.05)'};
  transition: all 0.4s cubic-bezier(0.23, 1, 0.32, 1);

  @media (max-width: 768px) {
    padding: ${props => props.scrolled ? '12px 24px' : '18px 24px'};
  }
`;

const LogoLink = styled.a`
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
  font-size: 22px;
  font-weight: 600;
  color: #ffffff;
  text-decoration: none;
  letter-spacing: -0.01em;
  position: relative;
  transition: all 0.3s cubic-bezier(0.23, 1, 0.32, 1);

  &:hover {
    transform: translateY(-1px);
    color: #f8f8f8;
  }

  @media (max-width: 768px) {
    font-size: 19px;
  }
`;

const NavRight = styled.div`
  display: flex;
  align-items: center;
  gap: 40px;

  @media (max-width: 768px) {
    gap: 24px;
  }
`;

const Button = styled.button`
  background: #ffffff;
  color: #000000;
  border: none;
  border-radius: 24px;
  padding: 10px 20px;
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
  font-size: 13px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.23, 1, 0.32, 1);
  position: relative;
  overflow: hidden;
  min-width: 90px;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.4), transparent);
    transition: left 0.6s ease;
  }

  &:hover::before {
    left: 100%;
  }

  &:hover {
    transform: translateY(-1px);
    box-shadow: 0 4px 20px rgba(255, 255, 255, 0.15);
    background: #f8f8f8;
  }

  &:active {
    transform: translateY(0);
  }

  @media (max-width: 768px) {
    padding: 8px 16px;
    font-size: 12px;
    min-width: 80px;
  }
`;

const SoundWrapper = styled.div`
  display: flex;
  align-items: center;
  cursor: pointer;
  user-select: none;
  color: #ffffff;
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
  font-size: 10px;
  font-weight: 500;
  letter-spacing: 0.1em;
  text-transform: uppercase;
  transition: all 0.3s cubic-bezier(0.23, 1, 0.32, 1);
  position: relative;
  opacity: 0.8;

  &:hover {
    opacity: 1;
    transform: translateY(-1px);
  }

  @media (max-width: 768px) {
    font-size: 9px;
  }
`;

const SoundIcon = styled.div`
  margin-right: 10px;
  position: relative;
  width: 18px;
  height: 12px;
  display: flex;
  align-items: center;

  @media (max-width: 768px) {
    width: 16px;
    height: 10px;
    margin-right: 8px;
  }
`;

const SpeakerBox = styled.div`
  width: 4px;
  height: 6px;
  background: #ffffff;
  border-radius: 1px 0 0 1px;
  position: relative;
  transition: all 0.3s ease;

  &::before {
    content: '';
    position: absolute;
    right: -2px;
    top: -1px;
    width: 0;
    height: 0;
    border-left: 3px solid #ffffff;
    border-top: 4px solid transparent;
    border-bottom: 4px solid transparent;
  }

  ${SoundWrapper}:hover & {
    background: #ffffff;
  }

  ${SoundWrapper}:hover &::before {
    border-left-color: #ffffff;
  }

  @media (max-width: 768px) {
    width: 3px;
    height: 5px;
    
    &::before {
      right: -1.5px;
      border-left-width: 2px;
      border-top-width: 3px;
      border-bottom-width: 3px;
    }
  }
`;

const SoundWave = styled.div`
  position: absolute;
  right: 0;
  border: 1px solid #ffffff;
  border-left: none;
  border-radius: 0 50px 50px 0;
  transition: all 0.3s ease;
  opacity: ${props => props.isSoundOn ? 0.6 : 0.2};

  ${SoundWrapper}:hover & {
    border-color: #ffffff;
    opacity: ${props => props.isSoundOn ? 1 : 0.3};
  }

  ${props => props.isSoundOn && css`
    animation: ${pulseGlow} 2s ease-in-out infinite;
    animation-delay: ${props.delay}s;
  `}

  @media (max-width: 768px) {
    border-width: 0.5px;
  }
`;

const WaveOne = styled(SoundWave)`
  width: 6px;
  height: 6px;
  right: 2px;
  top: 3px;

  @media (max-width: 768px) {
    width: 5px;
    height: 5px;
    right: 1.5px;
    top: 2.5px;
  }
`;

const WaveTwo = styled(SoundWave)`
  width: 9px;
  height: 9px;
  right: 0px;
  top: 1.5px;

  @media (max-width: 768px) {
    width: 7px;
    height: 7px;
    right: 0px;
    top: 1.5px;
  }
`;

const StatusText = styled.span`
  opacity: 0.7;
  transition: opacity 0.3s ease;

  ${SoundWrapper}:hover & {
    opacity: 1;
  }
`;

const Navbar = () => {
  const [isSoundOn, setIsSoundOn] = useState(true);
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };
    
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleLogoClick = (e) => {
    e.preventDefault();
    window.history.pushState({}, "", "/");
  };

  const toggleSound = () => {
    setIsSoundOn(!isSoundOn);
  };

  return (
    <Nav scrolled={isScrolled}>
      <LogoLink href="/" onClick={handleLogoClick}>
        PHANTOM
      </LogoLink>

      <NavRight>
        <SoundWrapper onClick={toggleSound}>
          <SoundIcon>
            <SpeakerBox />
            <WaveOne isSoundOn={isSoundOn} delay={0} />
            <WaveTwo isSoundOn={isSoundOn} delay={0.2} />
          </SoundIcon>
          <StatusText>SOUND [{isSoundOn ? "ON" : "OFF"}]</StatusText>
        </SoundWrapper>
        <Button>Let's Talk</Button>
      </NavRight>
    </Nav>
  );
};

export default Navbar;
