import React, { useEffect, useRef, useState, useCallback } from 'react';
import * as THREE from 'three';

const videos = [
  { 
    url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4', 
    title: 'STRONGER WITH YOU', 
    year: '2024',
    description: 'Powerful and magnetic fragrance',
    price: '$89',
    category: 'woody',
    intensity: 85
  },
  { 
    url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ElephantsDream.mp4', 
    title: 'INTENSO', 
    year: '2023',
    description: 'Deep and mysterious scent',
    price: '$95',
    category: 'oriental',
    intensity: 90
  },
  { 
    url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerBlazes.mp4', 
    title: 'THE SCENT', 
    year: '2024',
    description: 'Seductive and captivating',
    price: '$78',
    category: 'floral',
    intensity: 75
  },
  { 
    url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerEscapes.mp4', 
    title: 'SAUVAGE', 
    year: '2022',
    description: 'Wild and untamed essence',
    price: '$92',
    category: 'fresh',
    intensity: 80
  },
  { 
    url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerFun.mp4', 
    title: 'LE MALE', 
    year: '2023',
    description: 'Classic masculine appeal',
    price: '$85',
    category: 'woody',
    intensity: 70
  },
  { 
    url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerJoyrides.mp4', 
    title: 'BLEU DE CHANEL', 
    year: '2024',
    description: 'Elegant and sophisticated',
    price: '$105',
    category: 'fresh',
    intensity: 65
  },
];

const environments = [
  { name: 'Cosmic Gallery', id: 'cosmic' },
  { name: 'Underwater Palace', id: 'underwater' },
  { name: 'Forest Sanctuary', id: 'forest' },
  { name: 'City Skyline', id: 'city' }
];

const VRPerfumeGallery = () => {
  const mountRef = useRef();
  const sceneRef = useRef();
  const rendererRef = useRef();
  const cameraRef = useRef();
  const audioContextRef = useRef();
  const videoElements = useRef([]);
  const panelGroups = useRef([]);
  const frameId = useRef();
  const raycaster = useRef(new THREE.Raycaster());
  const mouse = useRef(new THREE.Vector2());
  const particleSystems = useRef([]);
  const lights = useRef([]);
  
  // State management
  const [isVRSupported, setIsVRSupported] = useState(false);
  const [isVRActive, setIsVRActive] = useState(false);
  const [currentEnvironment, setCurrentEnvironment] = useState('cosmic');
  const [selectedPerfume, setSelectedPerfume] = useState(null);
  const [achievements, setAchievements] = useState([]);
  const [score, setScore] = useState(0);
  const [questActive, setQuestActive] = useState(false);
  const [wishlist, setWishlist] = useState([]);
  const [cart, setCart] = useState([]);
  const [showShop, setShowShop] = useState(false);
  const [voiceEnabled, setVoiceEnabled] = useState(false);
  const [musicEnabled, setMusicEnabled] = useState(true);
  const [dayNightCycle, setDayNightCycle] = useState(true);
  const [multiplayerMode, setMultiplayerMode] = useState(false);
  const [avatars, setAvatars] = useState([]);

  // VR movement and interaction state
  const vrState = useRef({
    rotation: { x: 0, y: 0 },
    targetRotation: { x: 0, y: 0 },
    position: { x: 0, y: 0, z: 0 },
    velocity: { x: 0, y: 0, z: 0 },
    isMoving: false,
    lastMouseX: 0,
    lastMouseY: 0,
    timeOfDay: 0,
    environmentTransition: 0,
    hoveredObject: null,
    selectedObject: null,
    interaction: { isGrabbing: false, grabbedObject: null },
    physics: { gravity: -9.81, friction: 0.95 }
  });

  // Audio system
  const initAudio = useCallback(() => {
    if (!audioContextRef.current) {
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
    }
  }, []);

  // Speech recognition for voice control
  const initSpeechRecognition = useCallback(() => {
    if ('webkitSpeechRecognition' in window) {
      const recognition = new window.webkitSpeechRecognition();
      recognition.continuous = true;
      recognition.interimResults = true;
      recognition.lang = 'en-US';
      
      recognition.onresult = (event) => {
        const transcript = event.results[event.results.length - 1][0].transcript.toLowerCase();
        
        // Voice commands
        if (transcript.includes('show me')) {
          const perfumeName = videos.find(v => 
            transcript.includes(v.title.toLowerCase().split(' ')[0])
          );
          if (perfumeName) {
            focusOnPerfume(perfumeName);
          }
        }
        
        if (transcript.includes('change environment')) {
          const nextEnv = environments[(environments.findIndex(e => e.id === currentEnvironment) + 1) % environments.length];
          setCurrentEnvironment(nextEnv.id);
        }
        
        if (transcript.includes('add to cart')) {
          if (selectedPerfume) {
            addToCart(selectedPerfume);
          }
        }
      };
      
      if (voiceEnabled) {
        recognition.start();
      }
      
      return recognition;
    }
  }, [voiceEnabled, currentEnvironment, selectedPerfume]);

  // Particle systems for different environments
  const createParticleSystem = useCallback((type, count = 1000) => {
    const geometry = new THREE.BufferGeometry();
    const positions = new Float32Array(count * 3);
    const colors = new Float32Array(count * 3);
    const sizes = new Float32Array(count);
    const velocities = new Float32Array(count * 3);

    for (let i = 0; i < count; i++) {
      // Positions
      positions[i * 3] = (Math.random() - 0.5) * 200;
      positions[i * 3 + 1] = (Math.random() - 0.5) * 200;
      positions[i * 3 + 2] = (Math.random() - 0.5) * 200;

      // Colors based on environment
      const color = new THREE.Color();
      switch (type) {
        case 'cosmic':
          color.setHSL(0.6 + Math.random() * 0.4, 0.8, 0.6);
          break;
        case 'underwater':
          color.setHSL(0.5 + Math.random() * 0.2, 0.9, 0.7);
          break;
        case 'forest':
          color.setHSL(0.3 + Math.random() * 0.2, 0.8, 0.5);
          break;
        case 'city':
          color.setHSL(Math.random(), 0.7, 0.8);
          break;
        default:
          color.setHSL(Math.random(), 0.7, 0.6);
      }
      
      colors[i * 3] = color.r;
      colors[i * 3 + 1] = color.g;
      colors[i * 3 + 2] = color.b;

      // Sizes
      sizes[i] = Math.random() * 0.5 + 0.1;

      // Velocities
      velocities[i * 3] = (Math.random() - 0.5) * 0.1;
      velocities[i * 3 + 1] = (Math.random() - 0.5) * 0.1;
      velocities[i * 3 + 2] = (Math.random() - 0.5) * 0.1;
    }

    geometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));
    geometry.setAttribute('color', new THREE.BufferAttribute(colors, 3));
    geometry.setAttribute('size', new THREE.BufferAttribute(sizes, 1));
    geometry.setAttribute('velocity', new THREE.BufferAttribute(velocities, 3));

    const material = new THREE.PointsMaterial({
      size: 1,
      vertexColors: true,
      transparent: true,
      opacity: 0.8,
      blending: THREE.AdditiveBlending,
      sizeAttenuation: true
    });

    return new THREE.Points(geometry, material);
  }, []);

  // Environment creation
  const createEnvironment = useCallback((type) => {
    const envGroup = new THREE.Group();

    switch (type) {
      case 'cosmic':
        // Stars
        const starGeometry = new THREE.BufferGeometry();
        const starVertices = [];
        for (let i = 0; i < 15000; i++) {
          starVertices.push(
            (Math.random() - 0.5) * 2000,
            (Math.random() - 0.5) * 2000,
            (Math.random() - 0.5) * 2000
          );
        }
        starGeometry.setAttribute('position', new THREE.Float32BufferAttribute(starVertices, 3));
        const stars = new THREE.Points(
          starGeometry,
          new THREE.PointsMaterial({ color: 0xffffff, size: 1 })
        );
        envGroup.add(stars);

        // Nebula
        const nebula = createParticleSystem('cosmic', 2000);
        envGroup.add(nebula);
        break;

      case 'underwater':
        // Water caustics effect
        const waterGeometry = new THREE.PlaneGeometry(500, 500, 100, 100);
        const waterMaterial = new THREE.MeshBasicMaterial({
          color: 0x006994,
          transparent: true,
          opacity: 0.6,
          side: THREE.DoubleSide
        });
        const water = new THREE.Mesh(waterGeometry, waterMaterial);
        water.rotation.x = -Math.PI / 2;
        water.position.y = 50;
        envGroup.add(water);

        // Bubbles
        const bubbles = createParticleSystem('underwater', 500);
        envGroup.add(bubbles);
        break;

      case 'forest':
        // Trees (simplified)
        for (let i = 0; i < 50; i++) {
          const treeGroup = new THREE.Group();
          
          // Trunk
          const trunk = new THREE.Mesh(
            new THREE.CylinderGeometry(0.5, 0.8, 8),
            new THREE.MeshBasicMaterial({ color: 0x8B4513 })
          );
          trunk.position.y = 4;
          treeGroup.add(trunk);

          // Leaves
          const leaves = new THREE.Mesh(
            new THREE.SphereGeometry(4),
            new THREE.MeshBasicMaterial({ color: 0x228B22 })
          );
          leaves.position.y = 10;
          treeGroup.add(leaves);

          treeGroup.position.set(
            (Math.random() - 0.5) * 200,
            0,
            (Math.random() - 0.5) * 200
          );
          
          envGroup.add(treeGroup);
        }

        // Forest particles
        const forestParticles = createParticleSystem('forest', 800);
        envGroup.add(forestParticles);
        break;

      case 'city':
        // Buildings
        for (let i = 0; i < 30; i++) {
          const building = new THREE.Mesh(
            new THREE.BoxGeometry(
              Math.random() * 10 + 5,
              Math.random() * 50 + 20,
              Math.random() * 10 + 5
            ),
            new THREE.MeshBasicMaterial({ 
              color: new THREE.Color().setHSL(0.6, 0.3, 0.3 + Math.random() * 0.3) 
            })
          );
          building.position.set(
            (Math.random() - 0.5) * 300,
            building.geometry.parameters.height / 2,
            (Math.random() - 0.5) * 300
          );
          envGroup.add(building);
        }

        // City lights
        const cityLights = createParticleSystem('city', 1000);
        envGroup.add(cityLights);
        break;
    }

    return envGroup;
  }, [createParticleSystem]);

  // AI Assistant (simplified)
  const AIAssistant = useCallback(() => {
    const geometry = new THREE.SphereGeometry(1, 32, 32);
    const material = new THREE.MeshBasicMaterial({
      color: 0x00ffff,
      transparent: true,
      opacity: 0.8
    });
    const assistant = new THREE.Mesh(geometry, material);
    assistant.position.set(5, 2, 5);
    
    return assistant;
  }, []);

  // Quest system
  const checkQuests = useCallback(() => {
    const viewedCount = videos.filter(v => v.viewed).length;
    const newAchievements = [];

    if (viewedCount >= 3 && !achievements.includes('explorer')) {
      newAchievements.push('explorer');
      setScore(prev => prev + 100);
    }

    if (viewedCount === videos.length && !achievements.includes('completionist')) {
      newAchievements.push('completionist');
      setScore(prev => prev + 500);
    }

    if (newAchievements.length > 0) {
      setAchievements(prev => [...prev, ...newAchievements]);
    }
  }, [achievements]);

  // Shopping functions
  const addToWishlist = useCallback((perfume) => {
    setWishlist(prev => {
      if (!prev.find(p => p.title === perfume.title)) {
        return [...prev, perfume];
      }
      return prev;
    });
  }, []);

  const addToCart = useCallback((perfume) => {
    setCart(prev => {
      const existing = prev.find(p => p.title === perfume.title);
      if (existing) {
        return prev.map(p => 
          p.title === perfume.title 
            ? { ...p, quantity: p.quantity + 1 }
            : p
        );
      }
      return [...prev, { ...perfume, quantity: 1 }];
    });
  }, []);

  const focusOnPerfume = useCallback((perfume) => {
    setSelectedPerfume(perfume);
    
    // Find the perfume panel and move camera to it
    const index = videos.findIndex(v => v.title === perfume.title);
    if (index !== -1 && panelGroups.current[index]) {
      const panel = panelGroups.current[index];
      const targetPosition = panel.position.clone();
      targetPosition.add(new THREE.Vector3(0, 0, 10));
      
      // Smooth camera movement
      const startPos = cameraRef.current.position.clone();
      const duration = 2000;
      const startTime = Date.now();
      
      const animateCamera = () => {
        const elapsed = Date.now() - startTime;
        const progress = Math.min(elapsed / duration, 1);
        const eased = 1 - Math.pow(1 - progress, 3); // Ease out cubic
        
        cameraRef.current.position.lerpVectors(startPos, targetPosition, eased);
        cameraRef.current.lookAt(panel.position);
        
        if (progress < 1) {
          requestAnimationFrame(animateCamera);
        }
      };
      
      animateCamera();
    }
  }, []);

  // Physics simulation
  const updatePhysics = useCallback((deltaTime) => {
    // Apply gravity and friction to velocity
    vrState.current.velocity.y += vrState.current.physics.gravity * deltaTime;
    vrState.current.velocity.x *= vrState.current.physics.friction;
    vrState.current.velocity.z *= vrState.current.physics.friction;

    // Update position based on velocity
    vrState.current.position.x += vrState.current.velocity.x * deltaTime;
    vrState.current.position.y += vrState.current.velocity.y * deltaTime;
    vrState.current.position.z += vrState.current.velocity.z * deltaTime;

    // Ground collision
    if (vrState.current.position.y < 0) {
      vrState.current.position.y = 0;
      vrState.current.velocity.y = 0;
    }
  }, []);

  // Main effect
  useEffect(() => {
    if (!mountRef.current) return;

    const width = mountRef.current.clientWidth;
    const height = mountRef.current.clientHeight;

    // Scene setup
    const scene = new THREE.Scene();
    scene.fog = new THREE.Fog(0x000011, 50, 300);
    sceneRef.current = scene;

    // Camera setup
    const camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
    camera.position.set(0, 2, 0);
    cameraRef.current = camera;

    // Renderer setup
    const renderer = new THREE.WebGLRenderer({ 
      antialias: true,
      alpha: true,
      powerPreference: "high-performance"
    });
    renderer.setSize(width, height);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFSoftShadowMap;
    renderer.toneMapping = THREE.ACESFilmicToneMapping;
    renderer.toneMappingExposure = 1.2;
    rendererRef.current = renderer;

    // Check VR support
    if ('xr' in navigator) {
      navigator.xr.isSessionSupported('immersive-vr').then((supported) => {
        setIsVRSupported(supported);
        if (supported) {
          renderer.xr.enabled = true;
        }
      });
    }

    mountRef.current.appendChild(renderer.domElement);

    // Initialize audio
    initAudio();

    // Create environment
    const environment = createEnvironment(currentEnvironment);
    scene.add(environment);

    // Lighting setup
    const ambientLight = new THREE.AmbientLight(0x404040, 0.4);
    scene.add(ambientLight);
    lights.current.push(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
    directionalLight.position.set(10, 10, 5);
    directionalLight.castShadow = true;
    directionalLight.shadow.mapSize.width = 2048;
    directionalLight.shadow.mapSize.height = 2048;
    scene.add(directionalLight);
    lights.current.push(directionalLight);

    // Add point lights for ambiance
    for (let i = 0; i < 5; i++) {
      const pointLight = new THREE.PointLight(
        new THREE.Color().setHSL(Math.random(), 0.7, 0.5),
        0.5,
        50
      );
      pointLight.position.set(
        (Math.random() - 0.5) * 100,
        Math.random() * 20 + 5,
        (Math.random() - 0.5) * 100
      );
      scene.add(pointLight);
      lights.current.push(pointLight);
    }

    // AI Assistant
    const assistant = AIAssistant();
    scene.add(assistant);

    // Create perfume panels
    const radius = 30;
    videos.forEach((video, index) => {
      const angle = (index / videos.length) * Math.PI * 2;
      const height = Math.sin(index * 0.8) * 8;
      
      const x = Math.cos(angle) * radius;
      const z = Math.sin(angle) * radius;
      const y = height + 5;

      // Video element
      const videoElement = document.createElement('video');
      videoElement.src = video.url;
      videoElement.muted = !musicEnabled;
      videoElement.loop = true;
      videoElement.playsInline = true;
      videoElement.crossOrigin = 'anonymous';
      videoElement.addEventListener('loadeddata', () => {
        videoElement.play().catch(console.error);
      });
      videoElements.current.push(videoElement);

      // Video texture
      const videoTexture = new THREE.VideoTexture(videoElement);
      videoTexture.minFilter = THREE.LinearFilter;
      videoTexture.magFilter = THREE.LinearFilter;

      // Panel group
      const panelGroup = new THREE.Group();
      panelGroup.userData = { perfume: video, index };

      // Holographic frame with intensity-based color
      const frameGeometry = new THREE.RingGeometry(5, 5.5, 64);
      const frameColor = new THREE.Color().setHSL(video.intensity / 100, 0.8, 0.6);
      const frameMaterial = new THREE.MeshBasicMaterial({
        color: frameColor,
        transparent: true,
        opacity: 0.8,
        side: THREE.DoubleSide
      });
      const frame = new THREE.Mesh(frameGeometry, frameMaterial);
      panelGroup.add(frame);

      // Video screen
      const screenGeometry = new THREE.PlaneGeometry(10, 8);
      const screenMaterial = new THREE.MeshBasicMaterial({
        map: videoTexture,
        transparent: true,
        opacity: 0.9
      });
      const screen = new THREE.Mesh(screenGeometry, screenMaterial);
      screen.castShadow = true;
      screen.receiveShadow = true;
      panelGroup.add(screen);

      // Enhanced glow effect
      const glowGeometry = new THREE.PlaneGeometry(11, 9);
      const glowMaterial = new THREE.MeshBasicMaterial({
        color: frameColor,
        transparent: true,
        opacity: 0.3,
        blending: THREE.AdditiveBlending
      });
      const glow = new THREE.Mesh(glowGeometry, glowMaterial);
      glow.position.z = -0.01;
      panelGroup.add(glow);

      // 3D perfume bottle (simplified)
      const bottleGroup = new THREE.Group();
      const bottleGeometry = new THREE.CylinderGeometry(0.8, 1.2, 3, 8);
      const bottleMaterial = new THREE.MeshPhysicalMaterial({
        color: frameColor,
        transparent: true,
        opacity: 0.7,
        transmission: 0.9,
        roughness: 0.1,
        metalness: 0.1
      });
      const bottle = new THREE.Mesh(bottleGeometry, bottleMaterial);
      bottle.position.set(8, 0, 0);
      bottleGroup.add(bottle);

      // Bottle cap
      const capGeometry = new THREE.CylinderGeometry(0.6, 0.6, 0.5, 8);
      const capMaterial = new THREE.MeshBasicMaterial({ color: gold });
      const cap = new THREE.Mesh(capGeometry, capMaterial);
      cap.position.set(8, 2, 0);
      bottleGroup.add(cap);

      panelGroup.add(bottleGroup);

      // Enhanced text with more info
      const canvas = document.createElement('canvas');
      canvas.width = 1024;
      canvas.height = 256;
      const context = canvas.getContext('2d');
      context.fillStyle = '#ffffff';
      context.font = 'bold 48px Arial';
      context.textAlign, 'center';
      context.fillText(video.title, 512, 60);
      
      context.font = '32px Arial';
      context.fillStyle = '#cccccc';
      context.fillText(video.description, 512, 100);
      
      context.font = '28px Arial';
      context.fillStyle = '#888888';
      context.fillText(`${video.year} • ${video.category} • ${video.price}`, 512, 140);
      
      context.fillStyle = frameColor.getStyle();
      context.fillText(`Intensity: ${video.intensity}%`, 512, 180);

      const textTexture = new THREE.CanvasTexture(canvas);
      const textGeometry = new THREE.PlaneGeometry(12, 3);
      const textMaterial = new THREE.MeshBasicMaterial({
        map: textTexture,
        transparent: true
      });
      const textMesh = new THREE.Mesh(textGeometry, textMaterial);
      textMesh.position.y = -6;
      panelGroup.add(textMesh);

      // Particle system for each perfume
      const perfumeParticles = createParticleSystem('cosmic', 100);
      perfumeParticles.position.copy(panelGroup.position);
      scene.add(perfumeParticles);
      particleSystems.current.push(perfumeParticles);

      panelGroup.position.set(x, y, z);
      panelGroup.lookAt(0, y, 0);
      panelGroup.scale.setScalar(0);
      
      panelGroups.current.push(panelGroup);
      scene.add(panelGroup);

      // Staggered animation
      const animateIn = () => {
        const scale = panelGroup.scale.x;
        if (scale < 1) {
          panelGroup.scale.setScalar(Math.min(1, scale + 0.03));
          requestAnimationFrame(animateIn);
        }
      };
      setTimeout(animateIn, index * 300);
    });

    // Controls
    const keys = {};
    let lastTime = 0;

    const handleKeyDown = (event) => {
      keys[event.code] = true;
      
      // Hotkeys
      if (event.code === 'KeyE' && vrState.current.hoveredObject) {
        // Interact with hovered object
        const perfume = vrState.current.hoveredObject.userData.perfume;
        if (perfume) {
          setSelectedPerfume(perfume);
        }
      }
      
      if (event.code === 'KeyQ') {
        setQuestActive(!questActive);
      }
      
      if (event.code === 'KeyM') {
        setMusicEnabled(!musicEnabled);
      }
      
      if (event.code === 'KeyV') {
        setVoiceEnabled(!voiceEnabled);
      }
    };

    const handleKeyUp = (event) => {
      keys[event.code] = false;
    };

    const handlePointerDown = (event) => {
      vrState.current.isMoving = true;
      vrState.current.lastMouseX = event.clientX || event.touches[0].clientX;
      vrState.current.lastMouseY = event.clientY || event.touches[0].clientY;
      
      // Raycast for interaction
      mouse.current.x = ((event.clientX || event.touches[0].clientX) / width) * 2 - 1;
      mouse.current.y = -((event.clientY || event.touches[0].clientY) / height) * 2 + 1;
      
      raycaster.current.setFromCamera(mouse.current, camera);
      const intersects = raycaster.current.intersectObjects(panelGroups.current, true);
      
      if (intersects.length > 0) {
        const intersected = intersects[0].object.parent;
        if (intersected.userData.perfume) {
          setSelectedPerfume(intersected.userData.perfume);
          focusOnPerfume(intersected.userData.perfume);
        }
      }
    };

    const handlePointerMove = (event) => {
      const clientX = event.clientX || (event.touches && event.touches[0].clientX);
      const clientY = event.clientY || (event.touches && event.touches[0].clientY);
      
      // Mouse look
      if (vrState.current.isMoving) {
        const deltaX = clientX - vrState.current.lastMouseX;
        const deltaY = clientY - vrState.current.lastMouseY;

        vrState.current.targetRotation.y += deltaX * 0.005;
        vrState.current.targetRotation.x += deltaY * 0.005;
        vrState.current.targetRotation.x = Math.max(-Math.PI/3, Math.min(Math.PI/3, vrState.current.targetRotation.x));

        vrState.current.lastMouseX = clientX;
        vrState.current.lastMouseY = clientY;
      }
      
      // Update mouse for raycasting
      mouse.current.x = (clientX / width) * 2 - 1;
      mouse.current.y = -(clientY / height) * 2 + 1;
    };

    const handlePointerUp = () => {
      vrState.current.isMoving = false;
    };

    // Event listeners
    const canvas = renderer.domElement;
    canvas.addEventListener('mousedown', handlePointerDown);
    canvas.addEventListener('mousemove', handlePointerMove);
    canvas.addEventListener('mouseup', handlePointerUp);
    canvas.addEventListener('mouseleave', handlePointerUp);
    canvas.addEventListener('touchstart', handlePointerDown, { passive: true });
    canvas.addEventListener('touchmove', handlePointerMove, { passive: true });
    canvas.addEventListener('touchend', handlePointerUp);

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    // Initialize speech recognition
    const recognition = initSpeechRecognition();

    // Animation loop
    const animate = (currentTime) => {
  frameId.current = requestAnimationFrame(animate);const deltaTime = (currentTime - lastTime) / 1000;
      lastTime = currentTime;

      // Update physics
      updatePhysics(deltaTime);

      // Movement controls
      const moveSpeed = 20;
      const rotSpeed = 2;
      
      if (keys['KeyW'] || keys['ArrowUp']) {
        vrState.current.velocity.z -= moveSpeed * deltaTime;
      }
      if (keys['KeyS'] || keys['ArrowDown']) {
        vrState.current.velocity.z += moveSpeed * deltaTime;
      }
      if (keys['KeyA'] || keys['ArrowLeft']) {
        vrState.current.velocity.x -= moveSpeed * deltaTime;
      }
      if (keys['KeyD'] || keys['ArrowRight']) {
        vrState.current.velocity.x += moveSpeed * deltaTime;
      }
      if (keys['Space']) {
        if (vrState.current.position.y === 0) {
          vrState.current.velocity.y = 15;
        }
      }

      // Smooth rotation
      vrState.current.rotation.x += (vrState.current.targetRotation.x - vrState.current.rotation.x) * rotSpeed * deltaTime;
      vrState.current.rotation.y += (vrState.current.targetRotation.y - vrState.current.rotation.y) * rotSpeed * deltaTime;

      // Update camera
      camera.position.set(
        vrState.current.position.x,
        vrState.current.position.y + 2,
        vrState.current.position.z
      );
      camera.rotation.x = vrState.current.rotation.x;
      camera.rotation.y = vrState.current.rotation.y;

      // Day/night cycle
      if (dayNightCycle) {
        vrState.current.timeOfDay += deltaTime * 0.1;
        const timePhase = Math.sin(vrState.current.timeOfDay) * 0.5 + 0.5;
        
        // Update lighting based on time
        if (lights.current[0]) {
          lights.current[0].intensity = 0.2 + timePhase * 0.6;
        }
        if (lights.current[1]) {
          lights.current[1].intensity = 0.4 + timePhase * 0.8;
          lights.current[1].color.setHSL(0.15, 0.3, timePhase);
        }

        // Update fog
        scene.fog.color.setHSL(0.6, 0.2, 0.1 + timePhase * 0.4);
      }

      // Update particle systems
      particleSystems.current.forEach((particles, index) => {
        if (particles.geometry.attributes.position) {
          const positions = particles.geometry.attributes.position.array;
          const velocities = particles.geometry.attributes.velocity.array;
          
          for (let i = 0; i < positions.length; i += 3) {
            positions[i] += velocities[i] * deltaTime * 10;
            positions[i + 1] += velocities[i + 1] * deltaTime * 10;
            positions[i + 2] += velocities[i + 2] * deltaTime * 10;

            // Boundary checks
            if (Math.abs(positions[i]) > 100) velocities[i] *= -1;
            if (Math.abs(positions[i + 1]) > 100) velocities[i + 1] *= -1;
            if (Math.abs(positions[i + 2]) > 100) velocities[i + 2] *= -1;
          }
          
          particles.geometry.attributes.position.needsUpdate = true;
        }
      });

      // Animate perfume panels
      panelGroups.current.forEach((panel, index) => {
        if (panel.scale.x >= 1) {
          // Floating animation
          const time = currentTime * 0.001;
          panel.position.y += Math.sin(time + index) * 0.01;
          panel.rotation.y += 0.005;

          // Intensity-based glow animation
          const perfume = panel.userData.perfume;
          if (perfume) {
            const intensity = (Math.sin(time * 2 + index) * 0.5 + 0.5) * (perfume.intensity / 100);
            const glowMesh = panel.children.find(child => child.material && child.material.blending === THREE.AdditiveBlending);
            if (glowMesh) {
              glowMesh.material.opacity = 0.2 + intensity * 0.3;
            }
          }
        }
      });

      // Raycasting for hover effects
      raycaster.current.setFromCamera(mouse.current, camera);
      const intersects = raycaster.current.intersectObjects(panelGroups.current, true);
      
      // Reset previous hover
      if (vrState.current.hoveredObject && vrState.current.hoveredObject !== (intersects[0] && intersects[0].object.parent)) {
        vrState.current.hoveredObject.scale.setScalar(1);
        vrState.current.hoveredObject = null;
      }
      
      // Set new hover
      if (intersects.length > 0) {
        const intersected = intersects[0].object.parent;
        if (intersected !== vrState.current.hoveredObject) {
          vrState.current.hoveredObject = intersected;
          intersected.scale.setScalar(1.1);
        }
      }

      // Quest checking
      if (questActive) {
        checkQuests();
      }

      // Render
      renderer.render(scene, camera);
    };

    animate(0);

    // Cleanup
    return () => {
      if (frameId.current) {
        cancelAnimationFrame(frameId.current);
      }
      
      // Remove event listeners
      canvas.removeEventListener('mousedown', handlePointerDown);
      canvas.removeEventListener('mousemove', handlePointerMove);
      canvas.removeEventListener('mouseup', handlePointerUp);
      canvas.removeEventListener('mouseleave', handlePointerUp);
      canvas.removeEventListener('touchstart', handlePointerDown);
      canvas.removeEventListener('touchmove', handlePointerMove);
      canvas.removeEventListener('touchend', handlePointerUp);
      
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
      
      if (recognition) {
        recognition.stop();
      }

      // Dispose of resources
      videoElements.current.forEach(video => {
        video.pause();
        video.src = '';
      });

      panelGroups.current.forEach(panel => {
        panel.traverse(child => {
          if (child.geometry) child.geometry.dispose();
          if (child.material) {
            if (Array.isArray(child.material)) {
              child.material.forEach(mat => mat.dispose());
            } else {
              child.material.dispose();
            }
          }
        });
      });

      particleSystems.current.forEach(particles => {
        particles.geometry.dispose();
        particles.material.dispose();
      });

      if (renderer.domElement && mountRef.current) {
        mountRef.current.removeChild(renderer.domElement);
      }
      
      renderer.dispose();
    };
  }, [currentEnvironment, voiceEnabled, musicEnabled, dayNightCycle, questActive]);

  // UI Component
  return (
    <div className="w-full h-screen bg-black relative overflow-hidden">
      {/* 3D Scene */}
      <div 
        ref={mountRef} 
        className="w-full h-full cursor-grab active:cursor-grabbing"
        style={{ touchAction: 'none' }}
      />
      
      {/* HUD Overlay */}
      <div className="absolute inset-0 pointer-events-none">
        {/* Top HUD */}
        <div className="absolute top-4 left-4 right-4 flex justify-between items-start pointer-events-auto">
          <div className="bg-black/70 backdrop-blur-sm rounded-lg p-4 text-white">
            <h1 className="text-2xl font-bold bg-gradient-to-r from-cyan-400 to-purple-500 bg-clip-text text-transparent">
              VR Perfume Gallery
            </h1>
            <p className="text-sm text-gray-300 mt-1">
              Environment: {environments.find(e => e.id === currentEnvironment)?.name}
            </p>
            <p className="text-xs text-gray-400">
              Score: {score} | Achievements: {achievements.length}
            </p>
          </div>
          
          <div className="bg-black/70 backdrop-blur-sm rounded-lg p-4 text-white">
            <div className="flex gap-2">
              {environments.map(env => (
                <button
                  key={env.id}
                  onClick={() => setCurrentEnvironment(env.id)}
                  className={`px-3 py-1 rounded text-xs transition-all ${
                    currentEnvironment === env.id 
                      ? 'bg-cyan-500 text-black' 
                      : 'bg-gray-700 hover:bg-gray-600'
                  }`}
                >
                  {env.name}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Selected Perfume Info */}
        {selectedPerfume && (
          <div className="absolute bottom-4 left-4 right-4 pointer-events-auto">
            <div className="bg-black/80 backdrop-blur-lg rounded-xl p-6 text-white max-w-md mx-auto">
              <h2 className="text-2xl font-bold mb-2">{selectedPerfume.title}</h2>
              <p className="text-gray-300 mb-3">{selectedPerfume.description}</p>
              <div className="grid grid-cols-2 gap-4 text-sm mb-4">
                <div>
                  <span className="text-gray-400">Year:</span>
                  <span className="ml-2">{selectedPerfume.year}</span>
                </div>
                <div>
                  <span className="text-gray-400">Category:</span>
                  <span className="ml-2 capitalize">{selectedPerfume.category}</span>
                </div>
                <div>
                  <span className="text-gray-400">Price:</span>
                  <span className="ml-2 text-green-400">{selectedPerfume.price}</span>
                </div>
                <div>
                  <span className="text-gray-400">Intensity:</span>
                  <span className="ml-2">{selectedPerfume.intensity}%</span>
                </div>
              </div>
              <div className="flex gap-2">
                <button
                  onClick={() => addToCart(selectedPerfume)}
                  className="flex-1 bg-gradient-to-r from-cyan-500 to-blue-500 hover:from-cyan-600 hover:to-blue-600 px-4 py-2 rounded-lg font-semibold transition-all"
                >
                  Add to Cart
                </button>
                <button
                  onClick={() => addToWishlist(selectedPerfume)}
                  className="px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded-lg transition-all"
                >
                  ♡
                </button>
                <button
                  onClick={() => setSelectedPerfume(null)}
                  className="px-4 py-2 bg-red-600 hover:bg-red-700 rounded-lg transition-all"
                >
                  ✕
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Controls Help */}
        <div className="absolute bottom-4 right-4 pointer-events-auto">
          <div className="bg-black/70 backdrop-blur-sm rounded-lg p-3 text-white text-xs">
            <div className="grid grid-cols-2 gap-2">
              <div>WASD: Move</div>
              <div>Mouse: Look</div>
              <div>Space: Jump</div>
              <div>E: Interact</div>
              <div>Q: Quests</div>
              <div>M: Music</div>
              <div>V: Voice</div>
              <div>Click: Select</div>
            </div>
          </div>
        </div>

        {/* VR Button */}
        {isVRSupported && (
          <div className="absolute top-4 right-4 pointer-events-auto">
            <button
              onClick={() => {
                if (!isVRActive) {
                  rendererRef.current.xr.getSession().then(() => {
                    setIsVRActive(true);
                  });
                }
              }}
              className="bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 px-4 py-2 rounded-lg text-white font-semibold transition-all"
            >
              {isVRActive ? 'Exit VR' : 'Enter VR'}
            </button>
          </div>
        )}

        {/* Settings Panel */}
        <div className="absolute top-20 right-4 pointer-events-auto">
          <div className="bg-black/70 backdrop-blur-sm rounded-lg p-3 text-white">
            <div className="space-y-2 text-sm">
              <label className="flex items-center gap-2">
                <input
                  type="checkbox"
                  checked={musicEnabled}
                  onChange={(e) => setMusicEnabled(e.target.checked)}
                />
                Music
              </label>
              <label className="flex items-center gap-2">
                <input
                  type="checkbox"
                  checked={voiceEnabled}
                  onChange={(e) => setVoiceEnabled(e.target.checked)}
                />
                Voice Control
              </label>
              <label className="flex items-center gap-2">
                <input
                  type="checkbox"
                  checked={dayNightCycle}
                  onChange={(e) => setDayNightCycle(e.target.checked)}
                />
                Day/Night Cycle
              </label>
              <label className="flex items-center gap-2">
                <input
                  type="checkbox"
                  checked={questActive}
                  onChange={(e) => setQuestActive(e.target.checked)}
                />
                Quests Active
              </label>
            </div>
          </div>
        </div>

        {/* Cart Counter */}
        {cart.length > 0 && (
          <div className="absolute top-4 left-1/2 transform -translate-x-1/2 pointer-events-auto">
            <button
              onClick={() => setShowShop(true)}
              className="bg-green-600 hover:bg-green-700 px-4 py-2 rounded-full text-white font-semibold flex items-center gap-2 transition-all"
            >
              🛒 Cart ({cart.reduce((sum, item) => sum + item.quantity, 0)})
            </button>
          </div>
        )}

        {/* Achievements Notifications */}
        {achievements.length > 0 && (
          <div className="absolute top-1/2 left-4 transform -translate-y-1/2 pointer-events-none">
            {achievements.slice(-3).map((achievement, index) => (
              <div
                key={achievement}
                className="bg-gradient-to-r from-yellow-400 to-orange-500 text-black px-4 py-2 rounded-lg mb-2 font-semibold animate-pulse"
              >
                🏆 Achievement: {achievement}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Shopping Modal */}
      {showShop && (
        <div className="absolute inset-0 bg-black/80 flex items-center justify-center pointer-events-auto z-50">
          <div className="bg-gray-900 rounded-xl p-6 max-w-2xl w-full mx-4 max-h-[80vh] overflow-y-auto">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-2xl font-bold text-white">Shopping Cart</h2>
              <button
                onClick={() => setShowShop(false)}
                className="text-white hover:text-red-400 text-xl"
              >
                ✕
              </button>
            </div>
            
            {cart.length === 0 ? (
              <p className="text-gray-400 text-center py-8">Your cart is empty</p>
            ) : (
              <div className="space-y-4">
                {cart.map((item, index) => (
                  <div key={index} className="flex items-center justify-between bg-gray-800 p-4 rounded-lg">
                    <div>
                      <h3 className="text-white font-semibold">{item.title}</h3>
                      <p className="text-gray-400 text-sm">{item.description}</p>
                    </div>
                    <div className="text-right">
                      <p className="text-green-400 font-semibold">{item.price}</p>
                      <p className="text-gray-400 text-sm">Qty: {item.quantity}</p>
                    </div>
                  </div>
                ))}
                
                <div className="border-t border-gray-700 pt-4">
                  <div className="flex justify-between items-center">
                    <span className="text-white font-semibold">Total Items:</span>
                    <span className="text-white">{cart.reduce((sum, item) => sum + item.quantity, 0)}</span>
                  </div>
                  <button className="w-full mt-4 bg-gradient-to-r from-green-500 to-blue-500 hover:from-green-600 hover:to-blue-600 px-6 py-3 rounded-lg text-white font-semibold transition-all">
                    Proceed to Checkout
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default VRPerfumeGallery;
