import React from "react";
import styled from "styled-components";

const AboutSection = styled.section`
  background-color: #000;
  color: #fff;
  padding: 160px 10vw 120px;
  font-family: 'Helvetica Neue', sans-serif;
`;

const Header = styled.h1`
  font-size: 6vw;
  font-weight: 600;
  line-height: 1.1;
  margin-bottom: 80px;
  letter-spacing: -0.05em;
`;

const SectionBlock = styled.div`
  margin-bottom: 100px;
  max-width: 1000px;
`;

const BlockTitle = styled.h2`
  font-size: 2.5rem;
  font-weight: 500;
  margin-bottom: 24px;
  color: #fff;
`;

const BlockText = styled.p`
  font-size: 1.3rem;
  line-height: 1.9;
  color: #bbb;
  max-width: 850px;
`;

const About = () => {
  return (
    <AboutSection>
      <Header>Agency</Header>

      <SectionBlock>
        <BlockTitle>What we do</BlockTitle>
        <BlockText>
          We create immersive digital experiences at the intersection of design, code, and emotion.
          Our work spans interactive storytelling, web platforms, and spatial web environments for forward-thinking brands and institutions.
        </BlockText>
      </SectionBlock>

      <SectionBlock>
        <BlockTitle>How we think</BlockTitle>
        <BlockText>
          We believe in the power of aesthetics, usability, and narrative. Our process is driven by curiosity,
          research, and experimentation â€” always challenging conventions to shape unique outcomes.
        </BlockText>
      </SectionBlock>

      <SectionBlock>
        <BlockTitle>Why we create</BlockTitle>
        <BlockText>
          Because we care. About the people we work with, the stories we tell, and the impact we leave behind.
          Every pixel, transition, and interaction is a reflection of that care.
        </BlockText>
      </SectionBlock>
    </AboutSection>
  );
};

export default About;
