import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';

const videos = [
  { url: '/videos/perfume1.mp4' },
  { url: '/videos/perfume2.mp4' },
  { url: '/videos/perfume3.mp4' },
  { url: '/videos/perfume4.mp4' },
  { url: '/videos/perfume5.mp4' },
  { url: '/videos/perfume6.mp4' },
];

const VRPerfumeGallery = () => {
  const mountRef = useRef();
  const videoElements = useRef([]);

  useEffect(() => {
    const width = mountRef.current.clientWidth;
    const height = mountRef.current.clientHeight;

    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x000000);

    const camera = new THREE.PerspectiveCamera(60, width / height, 0.1, 1000);
    camera.position.set(0, 0, 0);

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(width, height);
    renderer.setPixelRatio(window.devicePixelRatio);
    mountRef.current.appendChild(renderer.domElement);

    const light = new THREE.AmbientLight(0xffffff, 1);
    scene.add(light);

    const radius = 25;
    const panelWidth = 9;
    const panelHeight = 16;

    videos.forEach((video, i) => {
      const videoEl = document.createElement('video');
      videoEl.src = video.url;
      videoEl.muted = true;
      videoEl.loop = true;
      videoEl.playsInline = true;
      videoEl.autoplay = true;
      videoEl.crossOrigin = 'anonymous';
      videoEl.play();

      videoElements.current.push(videoEl);

      const texture = new THREE.VideoTexture(videoEl);
      texture.minFilter = THREE.LinearFilter;
      texture.magFilter = THREE.LinearFilter;
      texture.format = THREE.RGBFormat;

      const geometry = new THREE.PlaneGeometry(panelWidth, panelHeight);
      const material = new THREE.MeshBasicMaterial({ map: texture, side: THREE.FrontSide });
      const mesh = new THREE.Mesh(geometry, material);

      const angle = (i / videos.length) * Math.PI * 2;
      const x = Math.cos(angle) * radius;
      const z = Math.sin(angle) * radius;
      mesh.position.set(x, 0, z);
      mesh.lookAt(0, 0, 0);

      scene.add(mesh);
    });

    let isMouseDown = false;
    let lastX = 0;
    let rotationY = 0;
    let targetRotationY = 0;

    const onMouseDown = (e) => {
      isMouseDown = true;
      lastX = e.clientX;
    };

    const onMouseMove = (e) => {
      if (isMouseDown) {
        const deltaX = e.clientX - lastX;
        targetRotationY += deltaX * 0.005;
        lastX = e.clientX;
      }
    };

    const onMouseUp = () => {
      isMouseDown = false;
    };

    const animate = () => {
      requestAnimationFrame(animate);
      rotationY += (targetRotationY - rotationY) * 0.05;
      camera.rotation.y = rotationY;
      renderer.render(scene, camera);
    };

    animate();

    renderer.domElement.addEventListener('mousedown', onMouseDown);
    renderer.domElement.addEventListener('mousemove', onMouseMove);
    renderer.domElement.addEventListener('mouseup', onMouseUp);
    renderer.domElement.addEventListener('mouseleave', onMouseUp);

    const onResize = () => {
      const width = mountRef.current.clientWidth;
      const height = mountRef.current.clientHeight;
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
      renderer.setSize(width, height);
    };

    window.addEventListener('resize', onResize);

    return () => {
      window.removeEventListener('resize', onResize);
      renderer.domElement.removeEventListener('mousedown', onMouseDown);
      renderer.domElement.removeEventListener('mousemove', onMouseMove);
      renderer.domElement.removeEventListener('mouseup', onMouseUp);
      renderer.domElement.removeEventListener('mouseleave', onMouseUp);
      videoElements.current.forEach((v) => v.pause());
      renderer.dispose();
    };
  }, []);

  return (
    <div className="w-full h-screen bg-black overflow-hidden">
      <div ref={mountRef} className="w-full h-full" />
    </div>
  );
};

export default VRPerfumeGallery;
