import React, { useState, useEffect, useCallback, useMemo } from "react";

const Navbar = () => {
  const [isSoundOn, setIsSoundOn] = useState(true);
  const [isScrolled, setIsScrolled] = useState(false);
  const [currentTime, setCurrentTime] = useState({ time: "", isDay: true });

  // Мемоизированные стили
  const navStyles = useMemo(() => ({
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    padding: isScrolled ? '12px 40px' : '20px 40px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 1000,
    background: isScrolled ? 'rgba(0, 0, 0, 0.95)' : 'rgba(0, 0, 0, 0.8)',
    backdropFilter: 'blur(20px)',
    borderBottom: `1px solid ${isScrolled ? 'rgba(255,255,255,0.1)' : 'rgba(255,255,255,0.05)'}`,
    transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
  }), [isScrolled]);

  // Оптимизированный обработчик скролла с throttling
  const handleScroll = useCallback(() => {
    const scrollY = window.scrollY;
    setIsScrolled(scrollY > 20);
  }, []);

  // Throttled scroll handler
  const throttledScrollHandler = useMemo(() => {
    let ticking = false;
    return () => {
      if (!ticking) {
        requestAnimationFrame(() => {
          handleScroll();
          ticking = false;
        });
        ticking = true;
      }
    };
  }, [handleScroll]);

  // Обновление времени
  const updateTime = useCallback(() => {
    const now = new Date();
    const astanaTime = new Date(now.getTime() + (6 * 60 * 60 * 1000)); // GMT+6
    const hours = astanaTime.getUTCHours();
    const minutes = astanaTime.getUTCMinutes();
    
    setCurrentTime({
      time: `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`,
      isDay: hours >= 6 && hours < 18,
    });
  }, []);

  useEffect(() => {
    updateTime();
    const timeInterval = setInterval(updateTime, 60000);
    
    window.addEventListener('scroll', throttledScrollHandler, { passive: true });
    
    return () => {
      clearInterval(timeInterval);
      window.removeEventListener('scroll', throttledScrollHandler);
    };
  }, [updateTime, throttledScrollHandler]);

  const handleLogoClick = useCallback((e) => {
    e.preventDefault();
    window.history.pushState({}, "", "/");
  }, []);

  const toggleSound = useCallback(() => {
    setIsSoundOn(prev => !prev);
  }, []);

  // Мемоизированные звуковые точки
  const soundDots = useMemo(() => 
    Array.from({ length: 24 }, (_, i) => ({
      id: i,
      delay: Math.random() * 0.8,
    }))
  , []);

  return (
    <nav style={navStyles} role="navigation" aria-label="Главная навигация">
      {/* Logo */}
      <a 
        href="/" 
        onClick={handleLogoClick}
        style={{
          position: 'absolute',
          left: '40px',
          display: 'flex',
          alignItems: 'center',
          textDecoration: 'none',
        }}
        aria-label="Перейти на главную страницу"
      >
        <div
          style={{
            width: '80px',
            height: '40px',
            background: 'linear-gradient(135deg, #4ecdc4, #44a08d)',
            borderRadius: '8px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            color: 'white',
            fontWeight: 'bold',
            fontSize: '16px',
            transition: 'all 0.3s ease',
            cursor: 'pointer',
          }}
          onMouseEnter={(e) => {
            e.target.style.transform = 'translateY(-2px)';
            e.target.style.boxShadow = '0 8px 25px rgba(78, 205, 196, 0.3)';
          }}
          onMouseLeave={(e) => {
            e.target.style.transform = 'translateY(0)';
            e.target.style.boxShadow = 'none';
          }}
        >
          LOGO
        </div>
      </a>

      {/* Sound Control */}
      <button
        onClick={toggleSound}
        style={{
          display: 'flex',
          alignItems: 'center',
          background: 'transparent',
          border: 'none',
          cursor: 'pointer',
          color: '#ffffff',
          fontFamily: "'Inter', sans-serif",
          fontSize: '11px',
          fontWeight: 500,
          letterSpacing: '0.12em',
          textTransform: 'uppercase',
          transition: 'all 0.3s ease',
          padding: '8px 12px',
          borderRadius: '6px',
        }}
        onMouseEnter={(e) => {
          e.target.style.color = '#4ecdc4';
          e.target.style.background = 'rgba(78, 205, 196, 0.1)';
        }}
        onMouseLeave={(e) => {
          e.target.style.color = '#ffffff';
          e.target.style.background = 'transparent';
        }}
        aria-label={`Звук ${isSoundOn ? 'включен' : 'выключен'}`}
        aria-pressed={isSoundOn}
      >
        {/* Sound Bars */}
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(8, 2px)',
            gridTemplateRows: 'repeat(3, 2px)',
            gap: '2px',
            marginRight: '12px',
          }}
        >
          {soundDots.map((dot) => (
            <div
              key={dot.id}
              style={{
                width: '2px',
                height: '2px',
                background: '#ffffff',
                borderRadius: '1px',
                opacity: isSoundOn ? 0.9 : 0.15,
                animation: isSoundOn 
                  ? `soundWave 1.4s ease-in-out infinite ${dot.delay}s`
                  : 'none',
                transition: 'opacity 0.4s ease',
              }}
            />
          ))}
        </div>
        <span>SOUND [{isSoundOn ? "ON" : "OFF"}]</span>
      </button>

      {/* Right Section */}
      <div
        style={{
          position: 'absolute',
          right: '40px',
          display: 'flex',
          alignItems: 'center',
          gap: '32px',
        }}
      >
        {/* Clock Widget */}
        <div
          style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'flex-end',
            color: 'white',
            fontFamily: "'Courier New', monospace",
            fontSize: '11px',
            fontWeight: 500,
          }}
        >
          <div
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '8px',
              color: currentTime.isDay ? '#fff' : '#aaa',
              opacity: currentTime.isDay ? 1 : 0.6,
              fontWeight: currentTime.isDay ? 600 : 400,
            }}
          >
            {/* Day/Night Indicator */}
            <div
              style={{
                width: '6px',
                height: '6px',
                borderRadius: '50%',
                background: currentTime.isDay ? '#fff' : '#777',
                opacity: currentTime.isDay ? 1 : 0.4,
                position: 'relative',
              }}
            >
              {!currentTime.isDay && (
                <div
                  style={{
                    position: 'absolute',
                    left: '2px',
                    top: '0',
                    width: '6px',
                    height: '6px',
                    borderRadius: '50%',
                    background: 'black',
                  }}
                />
              )}
            </div>
            
            <div style={{ display: 'flex', justifyContent: 'space-between', width: '160px' }}>
              <span>ASTANA, KZ</span>
              <span>{currentTime.time} GMT+6</span>
            </div>
          </div>
        </div>

        {/* CTA Button */}
        <button
          style={{
            background: 'linear-gradient(135deg, #ffffff, #f0f0f0)',
            color: '#000000',
            border: 'none',
            borderRadius: '50px',
            padding: '12px 24px',
            fontFamily: "'Inter', sans-serif",
            fontSize: '14px',
            fontWeight: 500,
            cursor: 'pointer',
            transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
            position: 'relative',
            overflow: 'hidden',
            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
          }}
          onMouseEnter={(e) => {
            e.target.style.transform = 'translateY(-2px)';
            e.target.style.boxShadow = '0 8px 25px rgba(255, 255, 255, 0.2)';
          }}
          onMouseLeave={(e) => {
            e.target.style.transform = 'translateY(0)';
            e.target.style.boxShadow = '0 2px 8px rgba(0, 0, 0, 0.1)';
          }}
          onMouseDown={(e) => {
            e.target.style.transform = 'translateY(-1px)';
          }}
          onMouseUp={(e) => {
            e.target.style.transform = 'translateY(-2px)';
          }}
        >
          Let's Talk
        </button>
      </div>

      {/* CSS Animation for sound waves */}
      <style jsx>{`
        @keyframes soundWave {
          0%, 100% {
            transform: scale(1);
            opacity: 0.4;
          }
          50% {
            transform: scale(1.6);
            opacity: 1;
          }
        }
        
        @media (max-width: 768px) {
          nav {
            padding: ${isScrolled ? '12px 20px' : '16px 20px'} !important;
          }
          
          nav > a {
            left: 20px !important;
          }
          
          nav > a > div {
            width: 60px !important;
            font-size: 14px !important;
          }
          
          nav > div:last-child {
            right: 20px !important;
            gap: 20px !important;
          }
          
          nav button:last-child {
            padding: 10px 20px !important;
            font-size: 13px !important;
          }
        }
      `}</style>
    </nav>
  );
};

export default Navbar;
