import React, { useState } from "react";

const FooterNav = () => {
  const [active, setActive] = useState("Work");
  
  const navItems = ["Work", "About", "Careers"];

  return (
    <div className="fixed bottom-8 left-0 right-0 flex justify-center z-50 px-4">
      <nav className="flex bg-black/20 backdrop-blur-xl border border-white/10 rounded-full p-1.5 gap-1 shadow-2xl">
        {navItems.map((item) => (
          <button
            key={item}
            onClick={() => setActive(item)}
            className={`
              relative px-6 py-3 rounded-full font-medium text-sm transition-all duration-300 ease-out
              ${active === item 
                ? 'bg-white text-gray-900 shadow-lg transform scale-105' 
                : 'bg-transparent text-white/90 hover:bg-white/10 hover:text-white hover:scale-102'
              }
            `}
          >
            <span className="relative z-10">{item}</span>
            {active === item && (
              <div className="absolute inset-0 bg-gradient-to-r from-blue-400 to-purple-400 rounded-full opacity-20 animate-pulse" />
            )}
          </button>
        ))}
      </nav>
    </div>
  );
};

export default FooterNav;
