// src/pages/Home.jsx
import React, { useState } from "react";
import Carousel3D from "../components/Carousel3D"; // или EnhancedVideoGallery
import EnhancedVideoGallery from "../components/EnhancedVideoGallery";
import styled from "styled-components";
import Navbar from "../components/Navbar";
import FooterNav from "../components/FooterNav";

const Wrapper = styled.div`
  background: black;
  color: white;
  min-height: 100vh;
  overflow-x: hidden;
  padding-bottom: 100px;
`;

const Home = () => {
  const [activeTab, setActiveTab] = useState("Work");

  return (
    <Wrapper>
      <Navbar />

      {activeTab === "Work" && <EnhancedVideoGallery />} {/* или <Carousel3D /> */}

      {activeTab === "About" && (
        <div className="text-center text-white mt-20">
          <h2 className="text-3xl font-semibold mb-4">About</h2>
          <p className="text-gray-400">Brand info coming soon.</p>
        </div>
      )}

      {activeTab === "Careers" && (
        <div className="text-center text-white mt-20">
          <h2 className="text-3xl font-semibold mb-4">Careers</h2>
          <p className="text-gray-400">We're hiring soon!</p>
        </div>
      )}

      <FooterNav active={activeTab} setActive={setActiveTab} />
    </Wrapper>
  );
};

export default Home;
