import React, { useState, useEffect } from "react";
import styled, { keyframes } from "styled-components";

const Nav = styled.div`
  position: fixed;
  top: 0;
  width: 100%;
  padding: 1rem 2rem;
  display: flex;
  justify-content: space-between;
  z-index: 100;
`;

const Logo = styled.img`
  width: 80px;
  height: auto;
  cursor: pointer;
`;

const Button = styled.button`
  background: white;
  color: black;
  border-radius: 999px;
  padding: 0.5rem 1rem;
  font-size: 0.9rem;
  font-weight: bold;
  border: none;
  cursor: pointer;
`;

const SoundControl = styled.div`
  display: flex;
  align-items: center;
  cursor: pointer;
  font-size: 1rem;
  color: white;
`;

const waveAnimation = keyframes`
  0% {
    transform: translateY(0);
    opacity: 0.6;
  }
  50% {
    transform: translateY(-10px);
    opacity: 1;
  }
  100% {
    transform: translateY(0);
    opacity: 0.6;
  }
`;

const Wave = styled.div`
  width: 5px;
  height: 20px;
  background-color: white;
  margin: 0 2px;
  animation: ${waveAnimation} 1s ease-in-out infinite;
  opacity: ${({ isSoundOn }) => (isSoundOn ? 1 : 0)};
  transition: opacity 0.3s ease;
`;

const WaveContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ToggleSwitch = styled.div`
  width: 40px;
  height: 20px;
  background-color: ${({ isActive }) => (isActive ? "green" : "grey")};
  border-radius: 50px;
  position: relative;
  margin-right: 10px;
  transition: background-color 0.3s ease;
  cursor: pointer;

  &::before {
    content: "";
    position: absolute;
    top: 50%;
    left: ${({ isActive }) => (isActive ? "20px" : "0px")};
    transform: translateY(-50%);
    width: 18px;
    height: 18px;
    background-color: white;
    border-radius: 50%;
    transition: left 0.3s ease;
  }
`;

const Navbar = () => {
  const [isSoundOn, setIsSoundOn] = useState(true); // Состояние для переключателя звука

  const handleSoundToggle = () => {
    setIsSoundOn(!isSoundOn); // Переключаем состояние
  };

  const handleLogoClick = (event) => {
    event.preventDefault(); // Предотвращает перезагрузку страницы
    window.history.pushState({}, "", "/"); // Изменяет URL без перезагрузки
  };

  return (
    <Nav>
      <a href="/" onClick={handleLogoClick}>
        <Logo src="/assets/logo.png" alt="logo" />
      </a>
      <div style={{ display: "flex", alignItems: "center" }}>
        <SoundControl onClick={handleSoundToggle}>
          <ToggleSwitch isActive={isSoundOn} /> 
          <span>{isSoundOn ? "Sound [ON]" : "Sound [OFF]"}</span>
        </SoundControl>
        <WaveContainer>
          {isSoundOn && (
            <>
              <Wave isSoundOn={isSoundOn} />
              <Wave isSoundOn={isSoundOn} />
              <Wave isSoundOn={isSoundOn} />
            </>
          )}
        </WaveContainer>
        <Button>Let’s Talk</Button>
      </div>
    </Nav>
  );
};

export default Navbar;
