import React, { useState, useRef } from 'react';
import styled from 'styled-components';
import { useSwipeable } from 'react-swipeable';

// Твои видео
const videos = [
  { url: '/assets/video1.mp4', title: 'Luxe Noir Collection', description: 'Deep, mysterious fragrance with notes of vanilla and sandalwood' },
  { url: '/assets/video2.mp4', title: 'Summer Breeze', description: 'Fresh citrus blend perfect for warm days' },
  { url: '/assets/video3.mp4', title: 'Midnight Essence', description: 'Sophisticated evening scent with floral undertones' },
  { url: '/assets/video4.mp4', title: 'Ocean Mist', description: 'Aquatic fragrance inspired by coastal winds' },
  { url: '/assets/video5.mp4', title: 'Rose Garden', description: 'Classic rose perfume with modern twist' },
];

// Стили
const Container = styled.div`
  min-height: 100vh;
  background: linear-gradient(to bottom right, #1f2937, #000, #1f2937);
  padding: 2rem;
  @media (min-width: 768px) {
    padding: 3rem;
  }
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 3rem;
  h1 {
    font-size: 2.5rem;
    font-weight: 300;
    color: white;
    margin-bottom: 1rem;
    letter-spacing: 0.05em;
  }
  p {
    color: #9ca3af;
    margin-top: 1.5rem;
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
    font-size: 1.125rem;
  }
`;

const Grid = styled.div`
  display: grid;
  gap: 2rem;
  grid-template-columns: 1fr;
  max-width: 1200px;
  margin: auto;
  @media (min-width: 768px) {
    grid-template-columns: repeat(2, 1fr);
  }
  @media (min-width: 1024px) {
    grid-template-columns: repeat(3, 1fr);
  }
`;

const Card = styled.div`
  position: relative;
  border-radius: 1rem;
  overflow: hidden;
  background: linear-gradient(to bottom, #1f2937, #111827);
  transition: transform 0.5s, box-shadow 0.5s;
  box-shadow: ${({ hovered }) => hovered ? '0 20px 30px rgba(251, 191, 36, 0.2)' : '0 10px 15px rgba(0, 0, 0, 0.5)'};
  &:hover {
    transform: scale(1.05);
  }
`;

const VideoWrapper = styled.div`
  position: relative;
  aspect-ratio: 16 / 9;
  overflow: hidden;
`;

const StyledVideo = styled.video`
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.7s;
  ${Card}:hover & {
    transform: scale(1.1);
  }
`;

const Overlay = styled.div`
  position: absolute;
  inset: 0;
  background: linear-gradient(to top, rgba(0,0,0,0.6), transparent, transparent);
  opacity: 0;
  transition: opacity 0.3s;
  ${Card}:hover & {
    opacity: 1;
  }
`;

const PlayButton = styled.button`
  position: absolute;
  inset: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  background: transparent;
  opacity: 0;
  transition: all 0.3s;
  ${Card}:hover & {
    opacity: 1;
    background-color: rgba(0, 0, 0, 0.2);
  }
`;

const Circle = styled.div`
  background-color: rgba(255, 255, 255, 0.9);
  padding: 1rem;
  border-radius: 9999px;
  transition: all 0.2s;
  backdrop-filter: blur(5px);
  &:hover {
    background-color: white;
    transform: scale(1.1);
  }
`;

const Content = styled.div`
  padding: 1.5rem;
  h3 {
    font-size: 1.25rem;
    color: white;
    font-weight: 600;
    margin-bottom: 0.5rem;
    transition: color 0.3s;
    ${Card}:hover & {
      color: #fbbf24;
    }
  }
  p {
    color: #9ca3af;
    font-size: 0.875rem;
    line-height: 1.5;
    margin-bottom: 1rem;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 0.75rem;
  button {
    border-radius: 0.5rem;
    padding: 0.5rem 1rem;
    font-weight: 500;
    transition: all 0.2s;
  }
  button:first-child {
    flex: 1;
    background: linear-gradient(to right, #f59e0b, #d97706);
    color: white;
    &:hover {
      background: linear-gradient(to right, #d97706, #b45309);
      transform: scale(1.05);
    }
  }
  button:last-child {
    border: 1px solid #4b5563;
    color: #d1d5db;
    &:hover {
      border-color: #fbbf24;
      color: #fbbf24;
    }
  }
`;

const Footer = styled.div`
  text-align: center;
  margin-top: 4rem;
  padding-top: 2rem;
  border-top: 1px solid #1f2937;
  p {
    color: #6b7280;
    font-size: 0.875rem;
  }
`;

const MobileSwipe = styled.div`
  margin-bottom: 2rem;
  @media (min-width: 768px) {
    display: none;
  }
`;

const EnhancedVideoGallery = () => {
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const [playingIndex, setPlayingIndex] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const videoRefs = useRef([]);

  const handleMouseEnter = (index) => {
    setHoveredIndex(index);
    const video = videoRefs.current[index];
    if (video) video.play().catch(() => {});
  };

  const handleMouseLeave = (index) => {
    setHoveredIndex(null);
    const video = videoRefs.current[index];
    if (video && playingIndex !== index) {
      video.pause();
      video.currentTime = 0;
    }
  };

  const handlePlayPause = (index) => {
    const video = videoRefs.current[index];
    if (video) {
      if (playingIndex === index) {
        video.pause();
        setPlayingIndex(null);
      } else {
        videoRefs.current.forEach((v, i) => {
          if (v && i !== index) v.pause();
        });
        video.play();
        setPlayingIndex(index);
      }
    }
  };

  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => setCurrentIndex((prev) => (prev + 1) % videos.length),
    onSwipedRight: () => setCurrentIndex((prev) => (prev - 1 + videos.length) % videos.length),
    trackMouse: true,
  });

  const renderCard = (video, index) => (
    <Card
      key={index}
      hovered={hoveredIndex === index}
      onMouseEnter={() => handleMouseEnter(index)}
      onMouseLeave={() => handleMouseLeave(index)}
    >
      <VideoWrapper>
        <StyledVideo
          ref={(el) => (videoRefs.current[index] = el)}
          src={video.url}
          muted
          playsInline
          loop
          onPlay={() => setPlayingIndex(index)}
          onPause={() => setPlayingIndex(null)}
        />
        <Overlay />
        <PlayButton onClick={() => handlePlayPause(index)}>
          <Circle>
            {playingIndex === index ? (
              <svg className="w-6 h-6 text-black" fill="currentColor" viewBox="0 0 24 24">
                <path d="M6 4h4v16H6V4zm8 0h4v16h-4V4z" />
              </svg>
            ) : (
              <svg className="w-6 h-6 text-black ml-1" fill="currentColor" viewBox="0 0 24 24">
                <path d="M8 5v14l11-7z" />
              </svg>
            )}
          </Circle>
        </PlayButton>
      </VideoWrapper>

      <Content>
        <h3>{video.title}</h3>
        <p>{video.description}</p>
        <ButtonGroup>
          <button>View Details</button>
          <button>
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
            </svg>
          </button>
        </ButtonGroup>
      </Content>
    </Card>
  );

  return (
    <Container>
      <Header>
        <h1>Perfume Collection</h1>
        <p>Discover our exquisite collection of luxury fragrances</p>
      </Header>

      <MobileSwipe {...swipeHandlers}>
        {renderCard(videos[currentIndex], currentIndex)}
      </MobileSwipe>

      <Grid>
        {videos.map((video, index) => renderCard(video, index))}
      </Grid>

      <Footer>
        <p>Hover over cards to preview • Click play button to watch</p>
      </Footer>
    </Container>
  );
};

export default EnhancedVideoGallery;
