import React, { useEffect, useRef, useState } from 'react';
import * as THREE from 'three';

const videos = [
  { url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4', title: 'STRONGER WITH YOU', year: '2024' },
  { url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ElephantsDream.mp4', title: 'INTENSO', year: '2023' },
  { url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerBlazes.mp4', title: 'THE SCENT', year: '2024' },
  { url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerEscapes.mp4', title: 'SAUVAGE', year: '2022' },
  { url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerFun.mp4', title: 'LE MALE', year: '2023' },
  { url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerJoyrides.mp4', title: 'BLEU DE CHANEL', year: '2024' },
];

const VRPerfumeGallery = () => {
  const mountRef = useRef();
