      videoElement.src = video.url;
      videoElement.muted = true;
      videoElement.loop = true;
      videoElement.playsInline = true;
      videoElement.crossOrigin = 'anonymous';
      videoElement.addEventListener('loadeddata', () => {
        videoElement.play().catch(console.error);
      });
      videoElements.current.push(videoElement);

      // Create video texture
      const videoTexture = new THREE.VideoTexture(videoElement);
      videoTexture.minFilter = THREE.LinearFilter;
      videoTexture.magFilter = THREE.LinearFilter;

      // Panel group
      const panelGroup = new THREE.Group();

      // Holographic frame
      const frameGeometry = new THREE.RingGeometry(4, 4.5, 32);
      const frameMaterial = new THREE.MeshBasicMaterial({
        color: 0x00ffff,
        transparent: true,
        opacity: 0.7,
        side: THREE.DoubleSide
      });
      const frame = new THREE.Mesh(frameGeometry, frameMaterial);
      panelGroup.add(frame);

      // Video screen
      const screenGeometry = new THREE.PlaneGeometry(8, 6);
      const screenMaterial = new THREE.MeshBasicMaterial({
        map: videoTexture,
        transparent: true,
        opacity: 0.9
      });
      const screen = new THREE.Mesh(screenGeometry, screenMaterial);
      screen.castShadow = true;
      panelGroup.add(screen);

      // Glowing border effect
      const glowGeometry = new THREE.PlaneGeometry(8.5, 6.5);
      const glowMaterial = new THREE.MeshBasicMaterial({
        color: 0x00ffff,
        transparent: true,
        opacity: 0.2,
        blending: THREE.AdditiveBlending
      });
      const glow = new THREE.Mesh(glowGeometry, glowMaterial);
      glow.position.z = -0.01;
      panelGroup.add(glow);

      // Title text (simplified as plane with text texture)
      const canvas = document.createElement('canvas');
      canvas.width = 512;
      canvas.height = 128;
      const context = canvas.getContext('2d');
      context.fillStyle = '#ffffff';
      context.font = 'bold 32px Arial';
      context.textAlign = 'center';
      context.fillText(video.title, 256, 50);
      context.font = '24px Arial';
      context.fillStyle = '#888888';
      context.fillText(video.year, 256, 90);

      const textTexture = new THREE.CanvasTexture(canvas);
      const textGeometry = new THREE.PlaneGeometry(6, 1.5);
      const textMaterial = new THREE.MeshBasicMaterial({
        map: textTexture,
        transparent: true
      });
      const textMesh = new THREE.Mesh(textGeometry, textMaterial);
      textMesh.position.y = -4;
      panelGroup.add(textMesh);

      panelGroup.position.set(x, y, z);
      panelGroup.lookAt(0, y, 0);
      
      // Initial scale animation
      panelGroup.scale.setScalar(0);
      panels.push(panelGroup);
      panelGroups.current.push(panelGroup);
      scene.add(panelGroup);

      // Animate in
      const animateIn = () => {
        const scale = panelGroup.scale.x;
        if (scale < 1) {
          panelGroup.scale.setScalar(Math.min(1, scale + 0.02));
          requestAnimationFrame(animateIn);
        }
      };
      setTimeout(animateIn, index * 200);
    });

    // Mouse/Touch controls for VR-like movement
    const handlePointerDown = (event) => {
      vrState.current.isMoving = true;
      vrState.current.lastMouseX = event.clientX || event.touches[0].clientX;
      vrState.current.lastMouseY = event.clientY || event.touches[0].clientY;
    };

    const handlePointerMove = (event) => {
      if (!vrState.current.isMoving) return;

      const clientX = event.clientX || (event.touches && event.touches[0].clientX);
      const clientY = event.clientY || (event.touches && event.touches[0].clientY);

      const deltaX = clientX - vrState.current.lastMouseX;
      const deltaY = clientY - vrState.current.lastMouseY;

      vrState.current.targetRotation.y += deltaX * 0.005;
      vrState.current.targetRotation.x += deltaY * 0.005;
      vrState.current.targetRotation.x = Math.max(-Math.PI/3, Math.min(Math.PI/3, vrState.current.targetRotation.x));

      vrState.current.lastMouseX = clientX;
      vrState.current.lastMouseY = clientY;
    };

    const handlePointerUp = () => {
      vrState.current.isMoving = false;
    };

    // Add event listeners
    const canvas = renderer.domElement;
    canvas.addEventListener('mousedown', handlePointerDown);
    canvas.addEventListener('mousemove', handlePointerMove);
    canvas.addEventListener('mouseup', handlePointerUp);
    canvas.addEventListener('mouseleave', handlePointerUp);
    canvas.addEventListener('touchstart', handlePointerDown, { passive: true });
    canvas.addEventListener('touchmove', handlePointerMove, { passive: true });
    canvas.addEventListener('touchend', handlePointerUp);

    // Keyboard controls
    const keys = {};
    const handleKeyDown = (event) => {
      keys[event.code] = true;
    };
    const handleKeyUp = (event) => {
      keys[event.code] = false;
    };
    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    // Animation loop
    const animate = () => {
      frameId.current = requestAnimationFrame(animate);

      const time = Date.now() * 0.001;

      // Smooth camera rotation
      vrState.current.rotation.y += (vrState.current.targetRotation.y - vrState.current.rotation.y) * 0.1;
      vrState.current.rotation.x += (vrState.current.targetRotation.x - vrState.current.rotation.x) * 0.1;

      // Keyboard movement
      const moveSpeed = 0.5;
      if (keys['KeyW'] || keys['ArrowUp']) {
        vrState.current.position.z -= moveSpeed;
      }
      if (keys['KeyS'] || keys['ArrowDown']) {
        vrState.current.position.z += moveSpeed;
      }
      if (keys['KeyA'] || keys['ArrowLeft']) {
        vrState.current.position.x -= moveSpeed;
      }
      if (keys['KeyD'] || keys['ArrowRight']) {
        vrState.current.position.x += moveSpeed;
      }

      // Apply camera transformations
      camera.position.set(
        vrState.current.position.x,
        vrState.current.position.y,
        vrState.current.position.z
      );
      camera.rotation.set(
        vrState.current.rotation.x,
        vrState.current.rotation.y,
        0
      );

      // Animate particles
      const positions = particles.geometry.attributes.position.array;
      for (let i = 0; i < positions.length; i += 3) {
        positions[i + 1] += Math.sin(time + positions[i]) * 0.01;
      }
      particles.geometry.attributes.position.needsUpdate = true;
      particles.rotation.y = time * 0.1;

      // Animate panels
      panelGroups.current.forEach((group, index) => {
        const offset = time + index * 0.5;
        group.position.y += Math.sin(offset) * 0.01;
        group.children[0].rotation.z = Math.sin(offset * 2) * 0.1; // Frame rotation
        
        // Pulsing glow effect
        if (group.children[2]) {
          group.children[2].material.opacity = 0.2 + Math.sin(offset * 3) * 0.1;
        }
      });

      // Rotate stars
      stars.rotation.y = time * 0.02;

      renderer.render(scene, camera);
    };

    animate();

    // Handle resize
    const handleResize = () => {
      if (!mountRef.current) return;
      const width = mountRef.current.clientWidth;
      const height = mountRef.current.clientHeight;

      camera.aspect = width / height;
      camera.updateProjectionMatrix();
      renderer.setSize(width, height);
    };

    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      if (frameId.current) {
        cancelAnimationFrame(frameId.current);
      }
      
      canvas.removeEventListener('mousedown', handlePointerDown);
      canvas.removeEventListener('mousemove', handlePointerMove);
      canvas.removeEventListener('mouseup', handlePointerUp);
      canvas.removeEventListener('mouseleave', handlePointerUp);
      canvas.removeEventListener('touchstart', handlePointerDown);
      canvas.removeEventListener('touchmove', handlePointerMove);
      canvas.removeEventListener('touchend', handlePointerUp);
      
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
      window.removeEventListener('resize', handleResize);

      videoElements.current.forEach(video => {
        video.pause();
        video.src = '';
      });

      if (mountRef.current && renderer.domElement) {
        mountRef.current.removeChild(renderer.domElement);
      }

      renderer.dispose();
    };
  }, []);

  const enterVR = async () => {
    if (!isVRSupported || !rendererRef.current) return;

    try {
      const session = await navigator.xr.requestSession('immersive-vr');
      rendererRef.current.xr.setSession(session);
      setIsVRActive(true);
      
      session.addEventListener('end', () => {
        setIsVRActive(false);
      });
    } catch (error) {
      console.error('Failed to enter VR:', error);
    }
  };

  return (
    <div className="relative w-full h-screen bg-black overflow-hidden">
      <div ref={mountRef} className="w-full h-full" />
      
      {/* VR Controls */}
      <div className="absolute top-4 left-4 z-10 text-white">
        <div className="bg-black bg-opacity-50 p-4 rounded-lg backdrop-blur-sm">
          <h3 className="text-lg font-bold mb-2">VR Perfume Gallery</h3>
          <div className="text-sm space-y-1">
            <p>üñ±Ô∏è Mouse: Look around</p>
            <p>‚å®Ô∏è WASD/Arrows: Move</p>
            <p>üì± Touch: Drag to look around</p>
          </div>
          {isVRSupported && (
            <button
              onClick={enterVR}
              disabled={isVRActive}
              className="mt-3 px-4 py-2 bg-blue-600 hover:bg-blue-700 disabled:bg-gray-600 rounded-lg text-white font-medium transition-colors"
            >
              {isVRActive ? 'VR Active' : 'Enter VR'}
            </button>
          )}
        </div>
      </div>

      {/* Instructions */}
      <div className="absolute bottom-4 right-4 z-10 text-white">
        <div className="bg-black bg-opacity-50 p-3 rounded-lg backdrop-blur-sm text-sm max-w-xs">
          <p>Immerse yourself in the perfume gallery. Move through space and explore each fragrance in this VR-like environment.</p>
        </div>
      </div>
    </div>
  );
};

export default VRPerfumeGallery;
