// src/components/VRExperience.jsx
import React, { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { useTexture, Environment } from '@react-three/drei';
import { EffectComposer, Bloom, Noise, Vignette } from '@react-three/postprocessing';
import styled from 'styled-components';
import * as THREE from 'three';

const images = [
  '/assets/img1.jpg',
  '/assets/img2.jpg',
  '/assets/img3.jpg',
  '/assets/img4.jpg',
  '/assets/img5.jpg',
];

const defaultZStep = 5;

const VRExperience = () => {
  return (
    <Wrapper>
      <Canvas camera={{ position: [0, 0, 2], fov: 65 }}>
        <ambientLight intensity={0.6} />
        <directionalLight position={[5, 5, 5]} intensity={1.5} />
        <Environment preset="sunset" />
        <CameraScroll />

        {images.map((url, index) => {
          const position = getPosition(index);
          return <FloatingImage key={index} url={url} position={position} />;
        })}

        <EffectComposer>
          <Bloom luminanceThreshold={0.3} luminanceSmoothing={0.8} intensity={1.2} />
          <Noise opacity={0.02} />
          <Vignette eskil={false} offset={0.2} darkness={1.1} />
        </EffectComposer>
      </Canvas>
    </Wrapper>
  );
};

// Обеспечиваем безопасную генерацию позиций
const getPosition = (index) => {
  const fallback = [0, 0, -defaultZStep * (index + 1)];
  const predefined = [
    [-3, 0, -5],
    [2, 1, -10],
    [-2, -1, -15],
    [3, 0.5, -20],
    [0, -0.5, -25],
  ];
  return predefined[index] || fallback;
};

function FloatingImage({ url, position }) {
  const ref = useRef();
  const texture = useTexture(url);

  useFrame(({ clock }) => {
    const t = clock.getElapsedTime();
    if (ref.current) {
      ref.current.rotation.y = Math.sin(t * 0.3 + position[0]) * 0.1;
      ref.current.rotation.x = Math.cos(t * 0.2 + position[1]) * 0.1;
    }
  });

  return (
    <mesh ref={ref} position={position}>
      <planeGeometry args={[2.5, 3.5]} />
      <meshStandardMaterial
        map={texture}
        roughness={0.3}
        metalness={0.5}
        emissive={new THREE.Color(0x111111)}
        emissiveIntensity={0.4}
        toneMapped={false}
      />
    </mesh>
  );
}

function CameraScroll() {
  useFrame((state) => {
    state.camera.position.z -= 0.015;
  });
  return null;
}

export default VRExperience;

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  overflow: hidden;
`;
