import React, { useState, useRef } from 'react';
import { useSwipeable } from 'react-swipeable';
import styled from 'styled-components';

const videos = [
  { url: '/assets/video1.mp4', title: 'Luxe Noir Collection', description: 'Deep, mysterious fragrance with notes of vanilla and sandalwood' },
  { url: '/assets/video2.mp4', title: 'Summer Breeze', description: 'Fresh citrus blend perfect for warm days' },
  { url: '/assets/video3.mp4', title: 'Midnight Essence', description: 'Sophisticated evening scent with floral undertones' },
  { url: '/assets/video4.mp4', title: 'Ocean Mist', description: 'Aquatic fragrance inspired by coastal winds' },
  { url: '/assets/video5.mp4', title: 'Rose Garden', description: 'Classic rose perfume with modern twist' },
];

const GalleryContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(to bottom right, #1f2937, #000);
  padding: 2rem;
  @media (min-width: 768px) {
    padding: 3rem;
  }
`;

const Grid = styled.div`
  display: grid;
  gap: 2rem;
  grid-template-columns: repeat(1, 1fr);
  @media (min-width: 768px) {
    grid-template-columns: repeat(2, 1fr);
  }
  @media (min-width: 1024px) {
    grid-template-columns: repeat(3, 1fr);
  }
`;

const Card = styled.div`
  position: relative;
  background: linear-gradient(to bottom, #1f2937, #111827);
  border-radius: 1rem;
  overflow: hidden;
  transition: 0.5s;
  box-shadow: ${({ active }) =>
    active
      ? '0 0 30px rgba(251, 191, 36, 0.2)'
      : '0 4px 15px rgba(0,0,0,0.5)'};
  transform: ${({ active }) => (active ? 'scale(1.05)' : 'scale(1)')};
`;

const VideoContainer = styled.div`
  position: relative;
  aspect-ratio: 16 / 9;
  overflow: hidden;
`;

const VideoStyled = styled.video`
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: 0.7s;
`;

const Overlay = styled.div`
  position: absolute;
  inset: 0;
  background: linear-gradient(to top, rgba(0, 0, 0, 0.6), transparent);
  opacity: 0;
  transition: opacity 0.3s;
  ${Card}:hover & {
    opacity: 1;
  }
`;

const PlayButton = styled.button`
  position: absolute;
  inset: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  opacity: 0;
  transition: all 0.3s;
  ${Card}:hover & {
    opacity: 1;
  }
`;

const ButtonIcon = styled.div`
  background: rgba(255, 255, 255, 0.9);
  padding: 1rem;
  border-radius: 9999px;
  transition: 0.2s;
`;

const Content = styled.div`
  padding: 1.5rem;
  color: white;
`;

const Title = styled.h3`
  font-size: 1.25rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const Description = styled.p`
  color: #9ca3af;
  font-size: 0.875rem;
`;

const ActionRow = styled.div`
  display: flex;
  gap: 0.75rem;
  margin-top: 1rem;
`;

const ViewButton = styled.button`
  flex: 1;
  padding: 0.5rem 1rem;
  background: linear-gradient(to right, #f59e0b, #d97706);
  border: none;
  color: white;
  border-radius: 0.5rem;
  font-weight: 500;
`;

const LikeButton = styled.button`
  border: 1px solid #4b5563;
  background: transparent;
  padding: 0.5rem;
  border-radius: 0.5rem;
  color: #d1d5db;
`;

const Indicator = styled.div`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: #ef4444;
  width: 0.75rem;
  height: 0.75rem;
  border-radius: 9999px;
  animation: pulse 1s infinite;
`;

const MobileSwipe = styled.div`
  display: block;
  @media (min-width: 1024px) {
    display: none;
  }
`;

const EnhancedVideoGallery = () => {
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const [playingIndex, setPlayingIndex] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const videoRefs = useRef([]);

  const handleMouseEnter = (index) => {
    setHoveredIndex(index);
    const video = videoRefs.current[index];
    if (video) video.play().catch(() => {});
  };

  const handleMouseLeave = (index) => {
    setHoveredIndex(null);
    const video = videoRefs.current[index];
    if (video && playingIndex !== index) {
      video.pause();
      video.currentTime = 0;
    }
  };

  const handlePlayPause = (index) => {
    const video = videoRefs.current[index];
    if (video) {
      if (playingIndex === index) {
        video.pause();
        setPlayingIndex(null);
      } else {
        videoRefs.current.forEach((v, i) => {
          if (v && i !== index) v.pause();
        });
        video.play();
        setPlayingIndex(index);
      }
    }
  };

  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => setCurrentIndex((prev) => (prev + 1) % videos.length),
    onSwipedRight: () =>
      setCurrentIndex((prev) => (prev - 1 + videos.length) % videos.length),
    preventScrollOnSwipe: true,
    trackMouse: true,
  });

  const renderCard = (video, index) => (
    <Card
      key={index}
      onMouseEnter={() => handleMouseEnter(index)}
      onMouseLeave={() => handleMouseLeave(index)}
      active={hoveredIndex === index}
    >
      <VideoContainer>
        <VideoStyled
          ref={(el) => (videoRefs.current[index] = el)}
          src={video.url}
          muted
          playsInline
          loop
          onPlay={() => setPlayingIndex(index)}
          onPause={() => setPlayingIndex(null)}
        />
        <Overlay />
        <PlayButton onClick={() => handlePlayPause(index)}>
          <ButtonIcon>
            {playingIndex === index ? (
              <svg className="w-6 h-6 text-black" fill="currentColor" viewBox="0 0 24 24">
                <path d="M6 4h4v16H6V4zm8 0h4v16h-4V4z" />
              </svg>
            ) : (
              <svg className="w-6 h-6 text-black ml-1" fill="currentColor" viewBox="0 0 24 24">
                <path d="M8 5v14l11-7z" />
              </svg>
            )}
          </ButtonIcon>
        </PlayButton>
        {playingIndex === index && <Indicator />}
      </VideoContainer>
      <Content>
        <Title>{video.title}</Title>
        <Description>{video.description}</Description>
        <ActionRow>
          <ViewButton>View Details</ViewButton>
          <LikeButton>
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
            </svg>
          </LikeButton>
        </ActionRow>
      </Content>
    </Card>
  );

  return (
    <GalleryContainer>
      <MobileSwipe {...swipeHandlers}>
        {renderCard(videos[currentIndex], currentIndex)}
      </MobileSwipe>

      {/* Desktop grid */}
      <div style={{ display: 'none' }} className="lg:block">
        <Grid>
          {videos.map((video, index) => renderCard(video, index))}
        </Grid>
      </div>
    </GalleryContainer>
  );
};

export default EnhancedVideoGallery;
