import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { Text } from 'troika-three-text';
import { EffectComposer } from 'three/examples/jsm/postprocessing/EffectComposer';
import { RenderPass } from 'three/examples/jsm/postprocessing/RenderPass';
import { UnrealBloomPass } from 'three/examples/jsm/postprocessing/UnrealBloomPass';

const videos = [
  { url: '/assets/perfume1.mp4', title: 'STRONGER WITH YOU', year: '2024' },
  { url: '/assets/perfume2.mp4', title: 'INTENSO', year: '2023' },
  { url: '/assets/perfume3.mp4', title: 'THE SCENT', year: '2024' },
  { url: '/assets/perfume4.mp4', title: 'SAUVAGE', year: '2022' },
  { url: '/assets/perfume5.mp4', title: 'LE MALE', year: '2023' },
  { url: '/assets/perfume6.mp4', title: 'BLEU DE CHANEL', year: '2024' },
];

const VRPerfumeGallery = () => {
  const mountRef = useRef();
  const videoElements = useRef([]);
  const groupRefs = useRef([]);
  const raycaster = new THREE.Raycaster();
  const mouse = new THREE.Vector2();

  useEffect(() => {
    const width = mountRef.current.clientWidth;
    const height = mountRef.current.clientHeight;

    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x000000); // можно заменить на текстуру

    const camera = new THREE.PerspectiveCamera(60, width / height, 0.1, 1000);
    camera.position.set(0, 0, 0);

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(width, height);
    renderer.setPixelRatio(window.devicePixelRatio);
    mountRef.current.appendChild(renderer.domElement);

    // postprocessing
    const composer = new EffectComposer(renderer);
    composer.addPass(new RenderPass(scene, camera));
    composer.addPass(new UnrealBloomPass(new THREE.Vector2(width, height), 1.5, 0.4, 0.85));

    const light = new THREE.AmbientLight(0xffffff, 1);
    scene.add(light);

    const radius = 28;
    const panelWidth = 8;
    const panelHeight = 14;
    const rows = 2;

    videos.forEach((video, i) => {
      const row = Math.floor(i / (videos.length / rows));
      const col = i % (videos.length / rows);
      const angle = (col / (videos.length / rows)) * Math.PI * 2;
      const y = (rows / 2 - row) * (panelHeight + 3) - panelHeight / 2;
      const x = Math.cos(angle) * radius;
      const z = Math.sin(angle) * radius;

      const videoEl = document.createElement('video');
      videoEl.src = video.url;
      videoEl.muted = true;
      videoEl.loop = true;
      videoEl.playsInline = true;
      videoEl.autoplay = true;
      videoEl.crossOrigin = 'anonymous';
      videoEl.addEventListener('canplay', () => videoEl.play());
      videoElements.current.push(videoEl);

      const texture = new THREE.VideoTexture(videoEl);

      const panelGroup = new THREE.Group();
      groupRefs.current.push(panelGroup);

      const background = new THREE.Mesh(
        new THREE.PlaneGeometry(panelWidth + 2, panelHeight + 4),
        new THREE.MeshBasicMaterial({ color: 0x111111 })
      );
      panelGroup.add(background);

      const videoMesh = new THREE.Mesh(
        new THREE.PlaneGeometry(panelWidth, panelHeight),
        new THREE.MeshBasicMaterial({ map: texture })
      );
      videoMesh.position.y = 0.5;
      panelGroup.add(videoMesh);

      const titleText = new Text();
      titleText.text = video.title;
      titleText.fontSize = 1;
      titleText.color = 0xffffff;
      titleText.anchorX = 'center';
      titleText.anchorY = 'middle';
      titleText.position.y = -panelHeight / 2 - 0.5;
      panelGroup.add(titleText);

      const yearText = new Text();
      yearText.text = video.year;
      yearText.fontSize = 0.6;
      yearText.color = 0x888888;
      yearText.anchorX = 'center';
      yearText.anchorY = 'middle';
      yearText.position.y = -panelHeight / 2 - 1.5;
      panelGroup.add(yearText);

      panelGroup.position.set(x, y, z);
      panelGroup.lookAt(0, y, 0);
      panelGroup.scale.setScalar(0);

      // Плавное появление
      const clock = new THREE.Clock();
      const fadeIn = () => {
        const s = panelGroup.scale.x;
        if (s < 1) {
          panelGroup.scale.setScalar(s + 0.02);
          requestAnimationFrame(fadeIn);
        } else {
          panelGroup.scale.setScalar(1);
        }
      };
      fadeIn();

      scene.add(panelGroup);
    });

    let isMouseDown = false;
    let lastX = 0, lastY = 0;
    let rotationY = 0, rotationX = 0;
    let targetRotationY = 0, targetRotationX = 0;

    const onMouseDown = (e) => {
      isMouseDown = true;
      lastX = e.clientX;
      lastY = e.clientY;
    };

    const onMouseMove = (e) => {
      if (isMouseDown) {
        const deltaX = e.clientX - lastX;
        const deltaY = e.clientY - lastY;
        targetRotationY += deltaX * 0.005;
        targetRotationX += deltaY * 0.005;
        targetRotationX = Math.max(-Math.PI / 2.5, Math.min(Math.PI / 2.5, targetRotationX));
        lastX = e.clientX;
        lastY = e.clientY;
      }
      mouse.x = (e.clientX / width) * 2 - 1;
      mouse.y = -(e.clientY / height) * 2 + 1;
    };

    const onMouseUp = () => (isMouseDown = false);

    const animate = () => {
      requestAnimationFrame(animate);
      rotationY += (targetRotationY - rotationY) * 0.05;
      rotationX += (targetRotationX - rotationX) * 0.05;
      camera.rotation.set(rotationX, rotationY, 0);

      raycaster.setFromCamera(mouse, camera);
      const intersects = raycaster.intersectObjects(groupRefs.current.map(g => g.children[1]), true);
      groupRefs.current.forEach(group => group.children[1].scale.set(1, 1, 1));
      if (intersects.length > 0) {
        intersects[0].object.scale.set(1.1, 1.1, 1);
      }

      composer.render();
    };
    animate();

    renderer.domElement.addEventListener('mousedown', onMouseDown);
    renderer.domElement.addEventListener('mousemove', onMouseMove);
    renderer.domElement.addEventListener('mouseup', onMouseUp);
    renderer.domElement.addEventListener('mouseleave', onMouseUp);

    const onResize = () => {
      const width = mountRef.current.clientWidth;
      const height = mountRef.current.clientHeight;
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
      renderer.setSize(width, height);
      composer.setSize(width, height);
    };
    window.addEventListener('resize', onResize);

    return () => {
      window.removeEventListener('resize', onResize);
      renderer.domElement.removeEventListener('mousedown', onMouseDown);
      renderer.domElement.removeEventListener('mousemove', onMouseMove);
      renderer.domElement.removeEventListener('mouseup', onMouseUp);
      renderer.domElement.removeEventListener('mouseleave', onMouseUp);
      videoElements.current.forEach((v) => v.pause());
      composer.dispose();
      renderer.dispose();
    };
  }, []);

  return (
    <div className="w-full h-screen bg-black overflow-hidden">
      <div ref={mountRef} className="w-full h-full" />
    </div>
  );
};

export default VRPerfumeGallery;
