import React, { useState, useRef } from 'react';
import { useSwipeable } from 'react-swipeable';

const videos = [
  { url: '/assets/video1.mp4', title: 'Luxe Noir Collection', description: 'Deep, mysterious fragrance with notes of vanilla and sandalwood' },
  { url: '/assets/video2.mp4', title: 'Summer Breeze', description: 'Fresh citrus blend perfect for warm days' },
  { url: '/assets/video3.mp4', title: 'Midnight Essence', description: 'Sophisticated evening scent with floral undertones' },
  { url: '/assets/video4.mp4', title: 'Ocean Mist', description: 'Aquatic fragrance inspired by coastal winds' },
  { url: '/assets/video5.mp4', title: 'Rose Garden', description: 'Classic rose perfume with modern twist' },
];

const EnhancedVideoGallery = () => {
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const [playingIndex, setPlayingIndex] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const videoRefs = useRef([]);

  const handleMouseEnter = (index) => {
    setHoveredIndex(index);
    const video = videoRefs.current[index];
    if (video) {
      video.play().catch(() => {});
    }
  };

  const handleMouseLeave = (index) => {
    setHoveredIndex(null);
    const video = videoRefs.current[index];
    if (video && playingIndex !== index) {
      video.pause();
      video.currentTime = 0;
    }
  };

  const handlePlayPause = (index) => {
    const video = videoRefs.current[index];
    if (video) {
      if (playingIndex === index) {
        video.pause();
        setPlayingIndex(null);
      } else {
        videoRefs.current.forEach((v, i) => {
          if (v && i !== index) v.pause();
        });
        video.play();
        setPlayingIndex(index);
      }
    }
  };

  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => setCurrentIndex((prev) => (prev + 1) % videos.length),
    onSwipedRight: () => setCurrentIndex((prev) => (prev - 1 + videos.length) % videos.length),
    trackMouse: true,
  });

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-black to-gray-900 p-6 md:p-12">
      <div className="text-center mb-12">
        <h1 className="text-4xl md:text-6xl font-light text-white mb-4 tracking-wide">Perfume Collection</h1>
        <div className="w-24 h-0.5 bg-gradient-to-r from-transparent via-amber-400 to-transparent mx-auto"></div>
        <p className="text-gray-400 mt-6 max-w-2xl mx-auto text-lg">
          Discover our exquisite collection of luxury fragrances
        </p>
      </div>

      {/* Desktop Grid */}
      <div className="hidden lg:grid max-w-7xl mx-auto grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {videos.map((video, index) => (
          <VideoCard
            key={index}
            video={video}
            index={index}
            hoveredIndex={hoveredIndex}
            playingIndex={playingIndex}
            videoRefs={videoRefs}
            handleMouseEnter={handleMouseEnter}
            handleMouseLeave={handleMouseLeave}
            handlePlayPause={handlePlayPause}
          />
        ))}
      </div>

      {/* Mobile Swipe View */}
      <div className="lg:hidden max-w-xl mx-auto" {...swipeHandlers}>
        <VideoCard
          video={videos[currentIndex]}
          index={currentIndex}
          hoveredIndex={hoveredIndex}
          playingIndex={playingIndex}
          videoRefs={videoRefs}
          handleMouseEnter={handleMouseEnter}
          handleMouseLeave={handleMouseLeave}
          handlePlayPause={handlePlayPause}
        />
        <div className="flex justify-between text-white mt-4">
          <button onClick={() => setCurrentIndex((currentIndex - 1 + videos.length) % videos.length)}>← Prev</button>
          <button onClick={() => setCurrentIndex((currentIndex + 1) % videos.length)}>Next →</button>
        </div>
      </div>

      <div className="text-center mt-16 pt-8 border-t border-gray-800">
        <p className="text-gray-500 text-sm">
          Hover over cards to preview • Click play button to watch
        </p>
      </div>
    </div>
  );
};

const VideoCard = ({ video, index, hoveredIndex, playingIndex, videoRefs, handleMouseEnter, handleMouseLeave, handlePlayPause }) => (
  <div
    className={`group relative bg-gradient-to-b from-gray-800 to-gray-900 rounded-2xl overflow-hidden transition-all duration-500 hover:scale-105 ${
      hoveredIndex === index ? 'shadow-2xl shadow-amber-500/20' : 'shadow-xl shadow-black/50'
    }`}
    onMouseEnter={() => handleMouseEnter(index)}
    onMouseLeave={() => handleMouseLeave(index)}
  >
    <div className="relative aspect-video overflow-hidden">
      <video
        ref={(el) => (videoRefs.current[index] = el)}
        src={video.url}
        className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
        muted
        playsInline
        loop
        onPlay={() => setPlayingIndex(index)}
        onPause={() => setPlayingIndex(null)}
      />
      <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
      <button
        onClick={() => handlePlayPause(index)}
        className="absolute inset-0 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-all duration-300 hover:bg-black/20"
      >
        <div className="bg-white/90 hover:bg-white rounded-full p-4 transform hover:scale-110 transition-all duration-200 backdrop-blur-sm">
          {playingIndex === index ? (
            <svg className="w-6 h-6 text-black" fill="currentColor" viewBox="0 0 24 24">
              <path d="M6 4h4v16H6V4zm8 0h4v16h-4V4z" />
            </svg>
          ) : (
            <svg className="w-6 h-6 text-black ml-1" fill="currentColor" viewBox="0 0 24 24">
              <path d="M8 5v14l11-7z" />
            </svg>
          )}
        </div>
      </button>
      {playingIndex === index && (
        <div className="absolute top-4 right-4">
          <div className="bg-red-500 w-3 h-3 rounded-full animate-pulse"></div>
        </div>
      )}
    </div>
    <div className="p-6">
      <h3 className="text-xl font-semibold text-white mb-2 group-hover:text-amber-400 transition-colors duration-300">{video.title}</h3>
      <p className="text-gray-400 text-sm leading-relaxed mb-4">{video.description}</p>
      <div className="flex gap-3">
        <button className="flex-1 bg-gradient-to-r from-amber-500 to-amber-600 hover:from-amber-600 hover:to-amber-700 text-white py-2 px-4 rounded-lg font-medium transition-all duration-200 transform hover:scale-105">View Details</button>
        <button className="px-4 py-2 border border-gray-600 hover:border-amber-400 text-gray-300 hover:text-amber-400 rounded-lg transition-all duration-200">
          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
          </svg>
        </button>
      </div>
    </div>
    <div className="absolute inset-0 -translate-x-full group-hover:translate-x-full transition-transform duration-1000 bg-gradient-to-r from-transparent via-white/5 to-transparent"></div>
  </div>
);

export default EnhancedVideoGallery;
