import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';

const videos = [
  { url: '/assets/video1.mp4', title: 'VIDEO 1' },
  { url: '/assets/video2.mp4', title: 'VIDEO 2' },
  { url: '/assets/video3.mp4', title: 'VIDEO 3' },
];

const VRPerfumeGallery = () => {
  const mountRef = useRef();

  useEffect(() => {
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.z = 25;

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    mountRef.current.appendChild(renderer.domElement);

    const radius = 20;
    const panels = [];

    videos.forEach((video, index) => {
      const angle = (index / videos.length) * Math.PI * 2;
      const x = Math.cos(angle) * radius;
      const z = Math.sin(angle) * radius;

      const videoEl = document.createElement('video');
      videoEl.src = video.url;
      videoEl.crossOrigin = 'anonymous';
      videoEl.loop = true;
      videoEl.muted = true;
      videoEl.playsInline = true;
      videoEl.autoplay = true;
      videoEl.play();

      const texture = new THREE.VideoTexture(videoEl);
      const geometry = new THREE.PlaneGeometry(10, 6);
      const material = new THREE.MeshBasicMaterial({ map: texture, side: THREE.DoubleSide });
      const mesh = new THREE.Mesh(geometry, material);
      mesh.position.set(x, 0, z);
      mesh.lookAt(0, 0, 0);
      scene.add(mesh);
      panels.push(mesh);
    });

    const animate = () => {
      requestAnimationFrame(animate);
      scene.rotation.y += 0.001;
      renderer.render(scene, camera);
    };

    animate();

    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      mountRef.current.removeChild(renderer.domElement);
    };
  }, []);

  return <div ref={mountRef} style={{ width: '100vw', height: '100vh', background: 'black' }} />;
};

export default VRPerfumeGallery;
