import React from "react";
import styled from "styled-components";

const AboutSection = styled.section`
  background-color: #000;
  color: #fff;
  padding: 120px 5vw;
  font-family: 'Inter', sans-serif;
`;

const SectionTitle = styled.h2`
  font-size: 3.5rem;
  font-weight: 600;
  margin-bottom: 2rem;
`;

const SectionBlock = styled.div`
  margin-bottom: 80px;
  max-width: 900px;
`;

const BlockTitle = styled.h3`
  font-size: 2rem;
  margin-bottom: 1rem;
  font-weight: 500;
`;

const BlockText = styled.p`
  font-size: 1.2rem;
  line-height: 1.8;
  color: #aaa;
`;

const About = () => {
  return (
    <AboutSection>
      <SectionTitle>Agency</SectionTitle>

      <SectionBlock>
        <BlockTitle>What we do</BlockTitle>
        <BlockText>
          We design and develop immersive experiences at the intersection of art, technology, and storytelling. From concept to execution, we help brands shape meaningful digital narratives.
        </BlockText>
      </SectionBlock>

      <SectionBlock>
        <BlockTitle>How we think</BlockTitle>
        <BlockText>
          Our creative process is research-driven, experimental, and collaborative. We embrace curiosity and treat each project as a chance to push boundaries and rethink conventions.
        </BlockText>
      </SectionBlock>

      <SectionBlock>
        <BlockTitle>Why we create</BlockTitle>
        <BlockText>
          Because we believe digital experiences can evoke emotions, shift perspectives, and leave a lasting impact. We're here to craft those moments â€” meaningful and memorable.
        </BlockText>
      </SectionBlock>
    </AboutSection>
  );
};

export default About;
