import React, { useEffect } from "react";
import { switchMusic } from "../components/MusicPlayer";

const Home = () => {
  useEffect(() => {
    const handleScroll = () => {
      const y = window.scrollY;
      const h = window.innerHeight;
      if (y < h) switchMusic("intro");
      else if (y < 2 * h) switchMusic("section1");
      else switchMusic("section2");
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      <section className="h-screen flex items-center justify-center bg-black">
        <h1 className="text-6xl font-bold tracking-widest">ARQ</h1>
      </section>
      <section className="h-screen flex items-center justify-center bg-gray-900">
        <p className="text-2xl">Architecture is frozen music.</p>
      </section>
      <section className="h-screen flex items-center justify-center bg-gray-800">
        <p className="text-2xl">Immersive experiences by design.</p>
      </section>
    </>
  );
};

export default Home;
