import React, { useEffect, useRef, useState } from 'react';
import * as THREE from 'three';

const media = [
  { url: 'https://picsum.photos/800/600?random=1', title: 'Experience Design', category: 'UI/UX', year: '2024' },
  { url: 'https://picsum.photos/800/600?random=2', title: 'Brand Identity', category: 'Branding', year: '2024' },
  { url: 'https://picsum.photos/800/600?random=3', title: 'Web Platform', category: 'Development', year: '2023' },
  { url: 'https://picsum.photos/800/600?random=4', title: 'Mobile App', category: 'Product', year: '2023' },
  { url: 'https://picsum.photos/800/600?random=5', title: 'Digital Campaign', category: 'Marketing', year: '2024' },
  { url: 'https://picsum.photos/800/600?random=6', title: 'Art Direction', category: 'Creative', year: '2023' },
  { url: 'https://picsum.photos/800/600?random=7', title: 'Visual Identity', category: 'Branding', year: '2024' },
  { url: 'https://picsum.photos/800/600?random=8', title: 'User Research', category: 'Strategy', year: '2023' },
  { url: 'https://picsum.photos/800/600?random=9', title: 'Innovation Lab', category: 'R&D', year: '2024' },
  { url: 'https://picsum.photos/800/600?random=10', title: 'Future Vision', category: 'Concept', year: '2024' }
];

const Carousel3D = () => {
  const mountRef = useRef();
  const sceneRef = useRef();
  const rendererRef = useRef();
  const cameraRef = useRef();
  const meshesRef = useRef([]);
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const [selectedIndex, setSelectedIndex] = useState(null);

  useEffect(() => {
    const width = mountRef.current.clientWidth;
    const height = mountRef.current.clientHeight;

    // Scene setup
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x0a0a0a);
    scene.fog = new THREE.FogExp2(0x0a0a0a, 0.015);
    sceneRef.current = scene;

    // Camera
    const camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
    camera.position.set(0, 0, 25);
    cameraRef.current = camera;

    // Renderer
    const renderer = new THREE.WebGLRenderer({ 
      antialias: true, 
      alpha: true,
      powerPreference: "high-performance"
    });
    renderer.setSize(width, height);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFSoftShadowMap;
    renderer.toneMapping = THREE.ACESFilmicToneMapping;
    renderer.toneMappingExposure = 1.2;
    mountRef.current.appendChild(renderer.domElement);
    rendererRef.current = renderer;

    // Lighting
    const ambientLight = new THREE.AmbientLight(0x404040, 0.6);
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
    directionalLight.position.set(10, 10, 5);
    directionalLight.castShadow = true;
    directionalLight.shadow.mapSize.width = 2048;
    directionalLight.shadow.mapSize.height = 2048;
    scene.add(directionalLight);

    const pointLight = new THREE.PointLight(0x4080ff, 0.5, 100);
    pointLight.position.set(-10, 5, 10);
    scene.add(pointLight);

    const spotLight = new THREE.SpotLight(0xff4080, 0.3, 50, Math.PI / 4, 0.5);
    spotLight.position.set(15, 15, 15);
    scene.add(spotLight);

    // Create meshes
    const meshes = [];
    const loader = new THREE.TextureLoader();
    
    media.forEach((item, i) => {
      // Create geometry with rounded corners
      const geometry = new THREE.PlaneGeometry(4, 3, 32, 32);
      
      // Load texture
      loader.load(item.url, (texture) => {
        texture.colorSpace = THREE.SRGBColorSpace;
        texture.generateMipmaps = false;
        texture.minFilter = THREE.LinearFilter;
        texture.magFilter = THREE.LinearFilter;
        
        const material = new THREE.MeshPhysicalMaterial({
          map: texture,
          roughness: 0.1,
          metalness: 0.1,
          transparent: true,
          opacity: 0.9,
          side: THREE.DoubleSide,
          emissive: 0x111111,
          clearcoat: 0.3,
          clearcoatRoughness: 0.1
        });

        const mesh = new THREE.Mesh(geometry, material);
        
        // Dynamic positioning in 3D space
        const radius = 18;
        const cols = 4;
        const rows = Math.ceil(media.length / cols);
        const row = Math.floor(i / cols);
        const col = i % cols;
        
        // Create more dynamic, scattered positioning
        const angle = (col / cols) * Math.PI * 2 + (row * 0.3);
        const radiusVariation = radius + Math.sin(i * 0.7) * 3;
        const heightVariation = (row - rows/2) * 4 + Math.cos(i * 0.5) * 2;
        
        const x = Math.sin(angle) * radiusVariation + (Math.random() - 0.5) * 2;
        const y = heightVariation + (Math.random() - 0.5) * 3;
        const z = Math.cos(angle) * radiusVariation + (Math.random() - 0.5) * 2;
        
        mesh.position.set(x, y, z);
        
        // Random rotation for more dynamic look
        mesh.rotation.x = (Math.random() - 0.5) * 0.3;
        mesh.rotation.y = angle + (Math.random() - 0.5) * 0.5;
        mesh.rotation.z = (Math.random() - 0.5) * 0.1;
        
        mesh.userData = {
          originalPosition: mesh.position.clone(),
          originalRotation: mesh.rotation.clone(),
          index: i,
          item: item
        };

        mesh.castShadow = true;
        mesh.receiveShadow = true;
        
        scene.add(mesh);
        meshes.push(mesh);
      });
    });

    meshesRef.current = meshes;

    // Mouse interaction
    let isMouseDown = false;
    let mouseX = 0;
    let mouseY = 0;
    let targetRotationX = 0;
    let targetRotationY = 0;
    let currentRotationX = 0;
    let currentRotationY = 0;
    let autoRotate = true;
    let autoRotateSpeed = 0.002;

    const raycaster = new THREE.Raycaster();
    const mouse = new THREE.Vector2();

    const handleMouseDown = (event) => {
      isMouseDown = true;
      autoRotate = false;
      mouseX = event.clientX;
      mouseY = event.clientY;
    };

    const handleMouseMove = (event) => {
      mouse.x = (event.clientX / width) * 2 - 1;
      mouse.y = -(event.clientY / height) * 2 + 1;

      if (isMouseDown) {
        const deltaX = event.clientX - mouseX;
        const deltaY = event.clientY - mouseY;
        
        targetRotationY += deltaX * 0.005;
        targetRotationX += deltaY * 0.005;
        targetRotationX = Math.max(-Math.PI / 3, Math.min(Math.PI / 3, targetRotationX));
        
        mouseX = event.clientX;
        mouseY = event.clientY;
      }

      // Raycast for hover effects
      raycaster.setFromCamera(mouse, camera);
      const intersects = raycaster.intersectObjects(meshes);
      
      if (intersects.length > 0) {
        const hoveredMesh = intersects[0].object;
        setHoveredIndex(hoveredMesh.userData.index);
        document.body.style.cursor = 'pointer';
      } else {
        setHoveredIndex(null);
        document.body.style.cursor = 'default';
      }
    };

    const handleMouseUp = () => {
      isMouseDown = false;
      setTimeout(() => {
        autoRotate = true;
      }, 3000);
    };

    const handleClick = (event) => {
      mouse.x = (event.clientX / width) * 2 - 1;
      mouse.y = -(event.clientY / height) * 2 + 1;
      
      raycaster.setFromCamera(mouse, camera);
      const intersects = raycaster.intersectObjects(meshes);
      
      if (intersects.length > 0) {
        const clickedMesh = intersects[0].object;
        setSelectedIndex(clickedMesh.userData.index);
      }
    };

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);

      if (autoRotate) {
        targetRotationY += autoRotateSpeed;
      }

      // Smooth rotation
      currentRotationX += (targetRotationX - currentRotationX) * 0.05;
      currentRotationY += (targetRotationY - currentRotationY) * 0.05;

      // Update mesh positions and effects
      meshes.forEach((mesh, index) => {
        if (mesh.userData) {
          // Apply global rotation
          const pos = mesh.userData.originalPosition.clone();
          pos.applyAxisAngle(new THREE.Vector3(0, 1, 0), currentRotationY);
          pos.applyAxisAngle(new THREE.Vector3(1, 0, 0), currentRotationX);
          mesh.position.copy(pos);

          // Hover effects
          if (hoveredIndex === index) {
            mesh.scale.lerp(new THREE.Vector3(1.2, 1.2, 1.2), 0.1);
            mesh.material.emissive.setHex(0x222222);
          } else {
            mesh.scale.lerp(new THREE.Vector3(1, 1, 1), 0.1);
            mesh.material.emissive.setHex(0x111111);
          }

          // Selected effect
          if (selectedIndex === index) {
            mesh.material.emissive.setHex(0x333333);
          }

          // Floating animation
          const time = Date.now() * 0.001;
          mesh.position.y += Math.sin(time + index * 0.5) * 0.01;
          mesh.rotation.z = mesh.userData.originalRotation.z + Math.sin(time + index) * 0.02;
        }
      });

      // Camera gentle movement
      const time = Date.now() * 0.0005;
      camera.position.x += Math.sin(time) * 0.02;
      camera.position.y += Math.cos(time * 0.7) * 0.01;

      renderer.render(scene, camera);
    };

    animate();

    // Event listeners
    renderer.domElement.addEventListener('mousedown', handleMouseDown);
    renderer.domElement.addEventListener('mousemove', handleMouseMove);
    renderer.domElement.addEventListener('mouseup', handleMouseUp);
    renderer.domElement.addEventListener('click', handleClick);

    // Resize handler
    const handleResize = () => {
      const width = mountRef.current.clientWidth;
      const height = mountRef.current.clientHeight;
      
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
      renderer.setSize(width, height);
    };

    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      renderer.domElement.removeEventListener('mousedown', handleMouseDown);
      renderer.domElement.removeEventListener('mousemove', handleMouseMove);
      renderer.domElement.removeEventListener('mouseup', handleMouseUp);
      renderer.domElement.removeEventListener('click', handleClick);
      window.removeEventListener('resize', handleResize);
      
      meshes.forEach(mesh => {
        scene.remove(mesh);
        if (mesh.geometry) mesh.geometry.dispose();
        if (mesh.material) {
          if (mesh.material.map) mesh.material.map.dispose();
          mesh.material.dispose();
        }
      });
      
      renderer.dispose();
    };
  }, [hoveredIndex, selectedIndex]);

  return (
    <div className="relative w-full h-screen bg-black overflow-hidden">
      <div
        ref={mountRef}
        className="w-full h-full cursor-grab active:cursor-grabbing"
      />
      
      {/* UI Overlay */}
      <div className="absolute top-0 left-0 w-full h-full pointer-events-none">
        {/* Header */}
        <div className="absolute top-8 left-8 text-white pointer-events-auto">
          <h1 className="text-4xl font-bold mb-2 bg-gradient-to-r from-white to-gray-400 bg-clip-text text-transparent">
            Portfolio
          </h1>
          <p className="text-gray-400 text-sm">Drag to explore • Click to select</p>
        </div>

        {/* Navigation */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 pointer-events-auto">
          <div className="flex space-x-6 bg-black/50 backdrop-blur-md rounded-full px-6 py-3 border border-gray-800">
            <button className="text-white hover:text-blue-400 transition-colors">Work</button>
            <button className="text-gray-400 hover:text-white transition-colors">About</button>
            <button className="text-gray-400 hover:text-white transition-colors">Contact</button>
          </div>
        </div>

        {/* Project Info */}
        {(hoveredIndex !== null || selectedIndex !== null) && (
          <div className="absolute top-1/2 right-8 transform -translate-y-1/2 pointer-events-auto">
            <div className="bg-black/80 backdrop-blur-md rounded-lg p-6 border border-gray-800 max-w-xs">
              {(() => {
                const item = media[hoveredIndex !== null ? hoveredIndex : selectedIndex];
                return (
                  <div>
                    <h3 className="text-white text-xl font-semibold mb-2">{item.title}</h3>
                    <p className="text-blue-400 text-sm mb-1">{item.category}</p>
                    <p className="text-gray-400 text-sm mb-4">{item.year}</p>
                    <button className="bg-white text-black px-4 py-2 rounded-full text-sm font-medium hover:bg-gray-200 transition-colors">
                      View Project
                    </button>
                  </div>
                );
              })()}
            </div>
          </div>
        )}

        {/* Particle effect overlay */}
        <div className="absolute inset-0 pointer-events-none">
          <div className="absolute inset-0 bg-gradient-to-b from-transparent via-transparent to-black/20"></div>
        </div>
      </div>
    </div>
  );
};

export default Carousel3D;
