// src/components/VRCarousel.jsx
import React, { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { Html, useTexture, Environment } from '@react-three/drei';
import { EffectComposer, Bloom, Noise, Vignette } from '@react-three/postprocessing';
import styled from 'styled-components';
import * as THREE from 'three';

const images = [
  '/assets/img1.jpg',
  '/assets/img2.jpg',
  '/assets/img3.jpg',
  '/assets/img4.jpg',
  '/assets/img5.jpg',
];

function ImagePlane({ url, index, total }) {
  const ref = useRef();
  const texture = useTexture(url);

  useFrame(({ clock }) => {
    const t = clock.getElapsedTime();
    const angle = (index / total) * Math.PI * 2 + t * 0.1;
    const radius = 6;
    ref.current.position.x = Math.cos(angle) * radius;
    ref.current.position.z = Math.sin(angle) * radius;
    ref.current.lookAt(0, 0, 0);
  });

  return (
    <mesh ref={ref}>
      <planeGeometry args={[2, 3]} />
      <meshStandardMaterial
        map={texture}
        roughness={0.3}
        metalness={0.8}
        emissive={new THREE.Color(0x222222)}
        emissiveIntensity={0.5}
        toneMapped={false}
      />
    </mesh>
  );
}

function CameraRig({ children }) {
  const ref = useRef();

  useFrame(({ mouse }) => {
    if (ref.current) {
      ref.current.rotation.y = mouse.x * 0.5;
      ref.current.rotation.x = mouse.y * 0.2;
    }
  });

  return <group ref={ref}>{children}</group>;
}

const VRCarousel = () => {
  return (
    <Wrapper>
      <Canvas camera={{ position: [0, 1.5, 10], fov: 55 }}>
        <ambientLight intensity={0.6} />
        <directionalLight position={[10, 10, 5]} intensity={2} />
        <Environment preset="sunset" />

        <CameraRig>
          {images.map((url, i) => (
            <ImagePlane key={i} url={url} index={i} total={images.length} />
          ))}
        </CameraRig>

        <EffectComposer>
          <Bloom luminanceThreshold={0.2} luminanceSmoothing={0.9} intensity={1.2} />
          <Noise opacity={0.03} />
          <Vignette eskil={false} offset={0.15} darkness={1.1} />
        </EffectComposer>
      </Canvas>
    </Wrapper>
  );
};

export default VRCarousel;

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  overflow: hidden;
`;
