import React, { useState, useEffect } from "react";
import styled, { keyframes, css } from "styled-components";
import { DateTime } from "luxon";

// Sound animation
const soundWave = keyframes`
  0%, 100% { transform: scale(1); opacity: 0.4; }
  50% { transform: scale(1.6); opacity: 1; }
`;

const Nav = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  padding: ${({ scrolled }) => (scrolled ? '12px 40px' : '20px 40px')};
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  background: ${({ scrolled }) => (scrolled ? 'rgba(0, 0, 0, 0.95)' : 'rgba(0, 0, 0, 0.8)')};
  backdrop-filter: blur(20px);
  border-bottom: 1px solid ${({ scrolled }) => (scrolled ? 'rgba(255,255,255,0.1)' : 'rgba(255,255,255,0.05)')};
  transition: all 0.3s ease;
`;

const Logo = styled.img`
  position: absolute;
  left: 40px;
  width: 80px;
  cursor: pointer;
  transition: all 0.3s ease;
  &:hover { transform: translateY(-1px); }
`;

const CenterGroup = styled.div`
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  align-items: center;
  gap: 40px;
`;

const SoundWrapper = styled.div`
  display: flex;
  align-items: center;
  cursor: pointer;
  user-select: none;
  color: #fff;
  font-family: 'Courier New', monospace;
  font-size: 11px;
  font-weight: 500;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  transition: all 0.3s ease;
`;

const Bars = styled.div`
  display: grid;
  grid-template-columns: repeat(8, 2px);
  grid-template-rows: repeat(3, 2px);
  gap: 2px;
  margin-right: 12px;
`;

const Dot = styled.div`
  width: 2px;
  height: 2px;
  background: #fff;
  border-radius: 1px;
  opacity: ${({ isSoundOn }) => (isSoundOn ? 0.9 : 0.15)};
  animation: ${({ isSoundOn }) => isSoundOn ? css`${soundWave} 1.4s ease-in-out infinite` : "none"};
  animation-delay: ${({ delay }) => delay}s;
  transition: opacity 0.4s ease;
`;

const NavRight = styled.div`
  position: absolute;
  right: 40px;
  display: flex;
  align-items: center;
`;

const Button = styled.button`
  background: #fff;
  color: #000;
  border: none;
  border-radius: 50px;
  padding: 12px 24px;
  font-family: 'Inter', sans-serif;
  font-size: 15px;
  font-weight: 600;
  cursor: pointer;
`;

const ClockWrapper = styled.div`
  display: flex;
  flex-direction: column;
  color: white;
  font-family: 'Courier New', monospace;
  font-size: 11px;
  font-weight: 500;
`;

const CityTime = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 16px;
  width: 220px;
  color: ${({ dim }) => (dim ? '#aaa' : '#fff')};
  opacity: ${({ dim }) => (dim ? 0.6 : 1)};
  font-weight: ${({ dim }) => (dim ? 400 : 600)};
`;

const Circle = styled.div`
  width: 6px;
  height: 6px;
  border-radius: 50%;
  background: ${({ dim }) => (dim ? '#777' : '#fff')};
  opacity: ${({ dim }) => (dim ? 0.4 : 1)};
  margin-right: 8px;
`;

const Moon = styled.div`
  width: 6px;
  height: 6px;
  border-radius: 50%;
  background: ${({ dim }) => (dim ? '#777' : '#fff')};
  opacity: ${({ dim }) => (dim ? 0.4 : 1)};
  margin-right: 8px;
  position: relative;
  &::after {
    content: '';
    position: absolute;
    left: 2px;
    top: 0;
    width: 6px;
    height: 6px;
    border-radius: 50%;
    background: black;
  }
`;

const ClockWidget = () => {
  const [times, setTimes] = useState({
    london: { time: "", isDay: true },
    auckland: { time: "", isDay: true }
  });

  useEffect(() => {
    const updateTimes = () => {
      const london = DateTime.now().setZone("Europe/London");
      const auckland = DateTime.now().setZone("Pacific/Auckland");

      setTimes({
        london: {
          time: london.toFormat("HH:mm"),
          isDay: london.hour >= 6 && london.hour < 18
        },
        auckland: {
          time: auckland.toFormat("HH:mm"),
          isDay: auckland.hour >= 6 && auckland.hour < 18
        }
      });
    };
    updateTimes();
    const interval = setInterval(updateTimes, 60000);
    return () => clearInterval(interval);
  }, []);

  return (
    <ClockWrapper>
      <CityTime dim={!times.london.isDay}>
        {times.london.isDay ? <Circle /> : <Moon />}
        <span>LONDON, UK</span>
        <span>{times.london.time} GMT+1</span>
      </CityTime>
      <CityTime dim={!times.auckland.isDay}>
        {times.auckland.isDay ? <Circle /> : <Moon />}
        <span>AUCKLAND, NZ</span>
        <span>{times.auckland.time} GMT+12</span>
      </CityTime>
    </ClockWrapper>
  );
};

const Navbar = () => {
  const [isSoundOn, setIsSoundOn] = useState(true);
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => setIsScrolled(window.scrollY > 20);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <Nav scrolled={isScrolled}>
      <a href="/" onClick={e => { e.preventDefault(); window.history.pushState({}, "", "/"); }}>
        <Logo src="/assets/logo.png" alt="logo" />
      </a>
      <CenterGroup>
        <SoundWrapper onClick={() => setIsSoundOn(!isSoundOn)}>
          <Bars>
            {Array.from({ length: 24 }).map((_, i) => (
              <Dot key={i} delay={Math.random()} isSoundOn={isSoundOn} />
            ))}
          </Bars>
          <span>SOUND [{isSoundOn ? "ON" : "OFF"}]</span>
        </SoundWrapper>
        <ClockWidget />
      </CenterGroup>
      <NavRight>
        <Button>Let's Talk</Button>
      </NavRight>
    </Nav>
  );
};

export default Navbar;
