import React, { useEffect, useRef, useState, useCallback } from "react";

const Contacts = () => {
  const headerRef = useRef(null);
  const sectionsRef = useRef([]); // ✅ Убедись, что это массив

  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [scrollProgress, setScrollProgress] = useState(0);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState(null);

  // Обновление позиции мыши (для параллакса)
  const updateMousePosition = useCallback((e) => {
    setMousePosition({
      x: (e.clientX / window.innerWidth) * 100,
      y: (e.clientY / window.innerHeight) * 100,
    });
  }, []);

  // Обновление прогресса скролла
  const updateScrollProgress = useCallback(() => {
    const scrollTop = window.scrollY;
    const docHeight = document.documentElement.scrollHeight - window.innerHeight;
    const progress = Math.min(scrollTop / docHeight, 1);
    setScrollProgress(progress);
  }, []);

  useEffect(() => {
    window.addEventListener('mousemove', updateMousePosition);
    window.addEventListener('scroll', updateScrollProgress);

    return () => {
      window.removeEventListener('mousemove', updateMousePosition);
      window.removeEventListener('scroll', updateScrollProgress);
    };
  }, [updateMousePosition, updateScrollProgress]);

  // Анимации появления при скролле
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.style.opacity = '1';
            entry.target.style.transform = 'translateY(0)';
          }
        });
      },
      { threshold: 0.1, rootMargin: '0px 0px -50px 0px' }
    );

    if (headerRef.current) {
      observer.observe(headerRef.current);
    }

    sectionsRef.current.forEach((section) => {
      if (section) observer.observe(section);
    });

    return () => observer.disconnect();
  }, []);

  // Обработка ввода
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Обработка отправки формы
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    setTimeout(() => {
      setIsSubmitting(false);
      setSubmitStatus('success');
      setFormData({ name: '', email: '', phone: '', message: '' });

      setTimeout(() => {
        setSubmitStatus(null);
      }, 3000);
    }, 2000);
  };

  // Копирование контактов в буфер
  const copyToClipboard = (text, type) => {
    if (navigator.clipboard) {
      navigator.clipboard.writeText(text).then(() => {
        console.log(`${type} скопирован: ${text}`);
      }).catch(err => {
        console.error('Ошибка копирования:', err);
      });
    } else {
      alert("Clipboard API не поддерживается в вашем браузере.");
    }
  };

  return (
    <div className="min-h-screen bg-black text-white overflow-hidden relative">
      {/* Прогресс-бар скролла */}
      <div className="fixed top-0 left-0 right-0 z-50 h-1 bg-gray-800">
        <div
          className="h-full bg-gradient-to-r from-green-500 to-teal-500 transition-all duration-150 ease-out"
          style={{ width: `${scrollProgress * 100}%` }}
        ></div>
      </div>

      {/* Анимационный фон */}
      <div
        className="fixed inset-0 pointer-events-none transition-all duration-700 ease-out"
        style={{
          transform: `translate(${(mousePosition.x - 50) * 0.02}px, ${(mousePosition.y - 50) * 0.02}px)`,
        }}
      >
        <div
          className="absolute top-1/4 -right-1/4 w-96 h-96 bg-gradient-to-r from-green-500/20 to-teal-500/20 rounded-full blur-3xl animate-pulse"
          style={{
            transform: `scale(${1 + scrollProgress * 0.5}) rotate(${scrollProgress * 360}deg)`,
          }}
        ></div>
        <div
          className="absolute bottom-1/4 -left-1/4 w-80 h-80 bg-gradient-to-r from-blue-500/20 to-cyan-500/20 rounded-full blur-3xl animate-pulse"
          style={{
            transform: `scale(${1 + scrollProgress * 0.3}) rotate(${-scrollProgress * 180}deg)`,
            animationDelay: '1s',
          }}
        ></div>
        <div
          className="absolute top-1/2 left-1/2 w-64 h-64 bg-gradient-to-r from-purple-500/10 to-pink-500/10 rounded-full blur-2xl animate-pulse"
          style={{
            transform: `translate(-50%, -50%) scale(${1 + Math.sin(scrollProgress * Math.PI) * 0.5})`,
            animationDelay: '2s',
          }}
        ></div>
      </div>

      {/* Контент */}
      <div className="relative z-10 px-6 md:px-16 lg:px-24 xl:px-32">
        <header
          ref={headerRef}
          className="pt-32 pb-16 md:pt-48 md:pb-24 opacity-0 transform translate-y-8 transition-all duration-1000 ease-out"
        >
          <h1 className="text-5xl md:text-7xl font-light leading-[0.9] tracking-tight mb-8">
            <span className="block hover:text-green-200 transition-colors duration-300">
              Свяжитесь с
            </span>
            <span className="block text-transparent bg-gradient-to-r from-white via-green-200 to-teal-300 bg-clip-text animate-gradient-x">
              командой Olfy
            </span>
            <span className="block font-extralight italic text-gray-400 hover:text-gray-300 transition-colors duration-300">
              мы всегда на связи.
            </span>
          </h1>
        </header>

        <main className="pb-32">
          {/* Контент и форма */}
          <div className="max-w-4xl mx-auto">
            <section
              ref={el => sectionsRef.current[0] = el}
              className="opacity-0 transform translate-y-8 transition-all duration-1000 ease-out delay-200"
            >
              <form onSubmit={handleSubmit} className="space-y-6">
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                  placeholder="Имя"
                  className="w-full p-3 bg-gray-800 text-white rounded"
                />
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  required
                  placeholder="Email"
                  className="w-full p-3 bg-gray-800 text-white rounded"
                />
                <textarea
                  name="message"
                  value={formData.message}
                  onChange={handleInputChange}
                  required
                  placeholder="Сообщение"
                  className="w-full p-3 bg-gray-800 text-white rounded h-32"
                />
                <button
                  type="submit"
                  disabled={isSubmitting}
                  className="px-6 py-3 bg-gradient-to-r from-green-500 to-teal-500 text-white rounded hover:from-green-600 hover:to-teal-600 transition-all"
                >
                  {isSubmitting ? 'Отправляем...' : 'Отправить сообщение'}
                </button>
              </form>

              {submitStatus === 'success' && (
                <div className="mt-4 p-4 bg-green-500/10 border border-green-500/50 rounded animate-fade-in">
                  <p className="text-green-400">Сообщение успешно отправлено!</p>
                </div>
              )}
            </section>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Contacts;
