import React, { useState } from "react";
import Preloader from "./components/Preloader";
import LogoSplash from "./components/LogoSplash";
import Home from "./pages/Home";
import useAudioEffect from "./hooks/useAudioEffect";

const App = () => {
  const [phase, setPhase] = useState("loading");
  const [loaded, setLoaded] = useState(false);

  const handlePreloadFinish = () => setPhase("splash");
  const handleSplashFinish = () => setPhase("main");

  useAudioEffect(); // включаем звуки при взаимодействиях

  return (
    <>
      {phase === "loading" && <Preloader onFinish={handlePreloadFinish} />}
      {phase === "splash" && <LogoSplash onFinish={handleSplashFinish} />}
      {!loaded && <Preloader onFinish={() => setLoaded(true)} />}
      {loaded && <Home />}
    </>
  );
};

export default App;
