import React, { useEffect, useRef, useState, useCallback } from 'react';
import * as THREE from 'three';

const videos = [
  { url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4', title: 'STRONGER WITH YOU', year: '2024', color: '#ff6b6b' },
  { url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ElephantsDream.mp4', title: 'INTENSO', year: '2023', color: '#4ecdc4' },
  { url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerBlazes.mp4', title: 'THE SCENT', year: '2024', color: '#45b7d1' },
  { url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerEscapes.mp4', title: 'SAUVAGE', year: '2022', color: '#f9ca24' },
  { url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerFun.mp4', title: 'LE MALE', year: '2023', color: '#ff9ff3' },
  { url: 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerJoyrides.mp4', title: 'BLEU DE CHANEL', year: '2024', color: '#70a1ff' },
];

const VRPerfumeGallery = () => {
  const mountRef = useRef();
  const sceneRef = useRef();
  const rendererRef = useRef();
  const cameraRef = useRef();
  const videoElements = useRef([]);
  const panelGroups = useRef([]);
  const frameId = useRef();
  const raycaster = useRef(new THREE.Raycaster());
  const mouse = useRef(new THREE.Vector2());
  const [isVRSupported, setIsVRSupported] = useState(false);
  const [isVRActive, setIsVRActive] = useState(false);
  const [selectedPanel, setSelectedPanel] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // Enhanced VR movement state
  const vrState = useRef({
    rotation: { x: 0, y: 0 },
    targetRotation: { x: 0, y: 0 },
    position: { x: 0, y: 0, z: 0 },
    targetPosition: { x: 0, y: 0, z: 0 },
    isMoving: false,
    lastMouseX: 0,
    lastMouseY: 0,
    velocity: { x: 0, y: 0, z: 0 },
    teleportTarget: null
  });

  // Audio context for spatial audio
  const audioContext = useRef();
  const audioSources = useRef([]);

  const initAudio = useCallback(() => {
    try {
      audioContext.current = new (window.AudioContext || window.webkitAudioContext)();
    } catch (error) {
      console.warn('Audio context not supported:', error);
    }
  }, []);

  const createSpatialAudio = useCallback((video, position) => {
    if (!audioContext.current) return;
    
    try {
      const source = audioContext.current.createMediaElementSource(video);
      const panner = audioContext.current.createPanner();
      
      panner.panningModel = 'HRTF';
      panner.distanceModel = 'inverse';
      panner.refDistance = 1;
      panner.maxDistance = 50;
      panner.rolloffFactor = 1;
      panner.coneInnerAngle = 360;
      panner.coneOuterAngle = 0;
      panner.coneOuterGain = 0;
      
      panner.setPosition(position.x, position.y, position.z);
      
      source.connect(panner);
      panner.connect(audioContext.current.destination);
      
      audioSources.current.push({ panner, position });
    } catch (error) {
      console.warn('Spatial audio setup failed:', error);
    }
  }, []);

  const handlePanelClick = useCallback((panelIndex) => {
    setSelectedPanel(panelIndex);
    const targetPanel = panelGroups.current[panelIndex];
    if (targetPanel) {
      // Smooth movement to selected panel
      const panelPosition = targetPanel.position;
      const direction = new THREE.Vector3().copy(panelPosition).normalize();
      const targetPos = direction.multiplyScalar(-15).add(panelPosition);
      
      vrState.current.targetPosition = {
        x: targetPos.x,
        y: targetPos.y,
        z: targetPos.z
      };
      
      // Look at the panel
      const lookDirection = new THREE.Vector3().subVectors(panelPosition, targetPos).normalize();
      vrState.current.targetRotation.y = Math.atan2(lookDirection.x, lookDirection.z);
      vrState.current.targetRotation.x = Math.asin(-lookDirection.y);
    }
  }, []);

  const createTeleportationSystem = useCallback((scene) => {
    // Create teleport indicator
    const teleportGeometry = new THREE.RingGeometry(0.5, 1, 16);
    const teleportMaterial = new THREE.MeshBasicMaterial({
      color: 0x00ff00,
      transparent: true,
      opacity: 0.7,
      side: THREE.DoubleSide
    });
    const teleportIndicator = new THREE.Mesh(teleportGeometry, teleportMaterial);
    teleportIndicator.rotation.x = -Math.PI / 2;
    teleportIndicator.visible = false;
    scene.add(teleportIndicator);
    
    return teleportIndicator;
  }, []);

  useEffect(() => {
    if (!mountRef.current) return;

    const width = mountRef.current.clientWidth;
    const height = mountRef.current.clientHeight;

    // Scene setup with enhanced environment
    const scene = new THREE.Scene();
    scene.fog = new THREE.Fog(0x0a0a0a, 30, 100);
    sceneRef.current = scene;

    // Create immersive space environment
    const starGeometry = new THREE.BufferGeometry();
    const starMaterial = new THREE.PointsMaterial({ 
      color: 0xffffff, 
      size: 0.8,
      transparent: true,
      opacity: 0.8,
      sizeAttenuation: true
    });
    const starVertices = [];
    const starColors = [];
    
    for (let i = 0; i < 15000; i++) {
      const x = (Math.random() - 0.5) * 3000;
      const y = (Math.random() - 0.5) * 3000;
      const z = (Math.random() - 0.5) * 3000;
      starVertices.push(x, y, z);
      
      // Add some colored stars
      const color = Math.random() > 0.95 ? new THREE.Color().setHSL(Math.random(), 0.7, 0.8) : new THREE.Color(1, 1, 1);
      starColors.push(color.r, color.g, color.b);
    }
    
    starGeometry.setAttribute('position', new THREE.Float32BufferAttribute(starVertices, 3));
    starGeometry.setAttribute('color', new THREE.Float32BufferAttribute(starColors, 3));
    const stars = new THREE.Points(starGeometry, starMaterial);
    scene.add(stars);

    // Add nebula-like background
    const nebulaGeometry = new THREE.SphereGeometry(200, 32, 16);
    const nebulaMaterial = new THREE.MeshBasicMaterial({
      color: 0x220066,
      transparent: true,
      opacity: 0.1,
      side: THREE.BackSide
    });
    const nebula = new THREE.Mesh(nebulaGeometry, nebulaMaterial);
    scene.add(nebula);

    // Camera setup with improved parameters
    const camera = new THREE.PerspectiveCamera(85, width / height, 0.1, 2000);
    camera.position.set(0, 2, 30);
    cameraRef.current = camera;

    // Enhanced renderer setup
    const renderer = new THREE.WebGLRenderer({ 
      antialias: true, 
      alpha: false,
      powerPreference: "high-performance",
      stencil: false,
      depth: true
    });
    renderer.setSize(width, height);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFSoftShadowMap;
    renderer.toneMapping = THREE.ACESFilmicToneMapping;
    renderer.toneMappingExposure = 1.4;
    renderer.outputColorSpace = THREE.SRGBColorSpace;
    rendererRef.current = renderer;

    // VR support check
    if ('xr' in navigator) {
      navigator.xr.isSessionSupported('immersive-vr').then((supported) => {
        setIsVRSupported(supported);
        if (supported) {
          renderer.xr.enabled = true;
        }
      });
    }

    mountRef.current.appendChild(renderer.domElement);

    // Enhanced lighting system
    const ambientLight = new THREE.AmbientLight(0x404060, 0.4);
    scene.add(ambientLight);

    const keyLight = new THREE.DirectionalLight(0xffffff, 1.2);
    keyLight.position.set(20, 20, 10);
    keyLight.castShadow = true;
    keyLight.shadow.mapSize.width = 4096;
    keyLight.shadow.mapSize.height = 4096;
    keyLight.shadow.camera.near = 0.1;
    keyLight.shadow.camera.far = 200;
    keyLight.shadow.camera.left = -50;
    keyLight.shadow.camera.right = 50;
    keyLight.shadow.camera.top = 50;
    keyLight.shadow.camera.bottom = -50;
    scene.add(keyLight);

    // Add rim lighting
    const rimLight = new THREE.DirectionalLight(0x4488ff, 0.6);
    rimLight.position.set(-20, 10, -10);
    scene.add(rimLight);

    // Point lights for atmosphere
    const pointLight1 = new THREE.PointLight(0xff4488, 2, 50);
    pointLight1.position.set(30, 10, 30);
    scene.add(pointLight1);

    const pointLight2 = new THREE.PointLight(0x44ff88, 2, 50);
    pointLight2.position.set(-30, 10, -30);
    scene.add(pointLight2);

    // Enhanced floating particles system
    const particleGeometry = new THREE.BufferGeometry();
    const particleCount = 2000;
    const particlePositions = new Float32Array(particleCount * 3);
    const particleColors = new Float32Array(particleCount * 3);
    const particleSizes = new Float32Array(particleCount);
    const particleVelocities = new Float32Array(particleCount * 3);

    for (let i = 0; i < particleCount; i++) {
      particlePositions[i * 3] = (Math.random() - 0.5) * 150;
      particlePositions[i * 3 + 1] = (Math.random() - 0.5) * 150;
      particlePositions[i * 3 + 2] = (Math.random() - 0.5) * 150;

      const color = new THREE.Color();
      color.setHSL(Math.random(), 0.8, 0.6);
      particleColors[i * 3] = color.r;
      particleColors[i * 3 + 1] = color.g;
      particleColors[i * 3 + 2] = color.b;

      particleSizes[i] = Math.random() * 0.3 + 0.1;
      
      particleVelocities[i * 3] = (Math.random() - 0.5) * 0.02;
      particleVelocities[i * 3 + 1] = (Math.random() - 0.5) * 0.02;
      particleVelocities[i * 3 + 2] = (Math.random() - 0.5) * 0.02;
    }

    particleGeometry.setAttribute('position', new THREE.BufferAttribute(particlePositions, 3));
    particleGeometry.setAttribute('color', new THREE.BufferAttribute(particleColors, 3));
    particleGeometry.setAttribute('size', new THREE.BufferAttribute(particleSizes, 1));

    const particleMaterial = new THREE.PointsMaterial({
      size: 0.2,
      vertexColors: true,
      transparent: true,
      opacity: 0.8,
      blending: THREE.AdditiveBlending,
      sizeAttenuation: true
    });

    const particles = new THREE.Points(particleGeometry, particleMaterial);
    scene.add(particles);

    // Create teleportation system
    const teleportIndicator = createTeleportationSystem(scene);

    // Initialize audio
    initAudio();

    // Enhanced video panels with better interactivity
    const radius = 35;
    const panels = [];
    let loadedVideos = 0;

    videos.forEach((video, index) => {
      const angle = (index / videos.length) * Math.PI * 2;
      const height = Math.sin(index * 0.8) * 8; // More varied heights
      
      const x = Math.cos(angle) * radius;
      const z = Math.sin(angle) * radius;
      const y = height + 5;

      // Create video element with better loading
      const videoElement = document.createElement('video');
      videoElement.src = video.url;
      videoElement.muted = true;
      videoElement.loop = true;
      videoElement.playsInline = true;
      videoElement.crossOrigin = 'anonymous';
      videoElement.preload = 'metadata';
      
      videoElement.addEventListener('loadeddata', () => {
        videoElement.play().catch(console.error);
        loadedVideos++;
        if (loadedVideos === videos.length) {
          setIsLoading(false);
        }
      });
      
      videoElements.current.push(videoElement);

      // Create spatial audio for this video
      createSpatialAudio(videoElement, { x, y, z });

      // Create video texture with better filtering
      const videoTexture = new THREE.VideoTexture(videoElement);
      videoTexture.minFilter = THREE.LinearFilter;
      videoTexture.magFilter = THREE.LinearFilter;
      videoTexture.format = THREE.RGBFormat;

      // Enhanced panel group
      const panelGroup = new THREE.Group();
      panelGroup.userData = { index, video, interactive: true };

      // Create holographic frame with animated colors
      const frameGeometry = new THREE.RingGeometry(5, 5.8, 64);
      const frameMaterial = new THREE.MeshBasicMaterial({
        color: new THREE.Color(video.color),
        transparent: true,
        opacity: 0.8,
        side: THREE.DoubleSide
      });
      const frame = new THREE.Mesh(frameGeometry, frameMaterial);
      panelGroup.add(frame);

      // Enhanced video screen with rounded corners effect
      const screenGeometry = new THREE.PlaneGeometry(10, 7.5, 32, 32);
      const screenMaterial = new THREE.MeshLambertMaterial({
        map: videoTexture,
        transparent: true,
        opacity: 0.95,
        side: THREE.DoubleSide
      });
      const screen = new THREE.Mesh(screenGeometry, screenMaterial);
      screen.castShadow = true;
      screen.receiveShadow = true;
      panelGroup.add(screen);

      // Multi-layered glow effect
      for (let i = 0; i < 3; i++) {
        const glowSize = 11 + i * 1.5;
        const glowGeometry = new THREE.PlaneGeometry(glowSize, glowSize * 0.75);
        const glowMaterial = new THREE.MeshBasicMaterial({
          color: new THREE.Color(video.color),
          transparent: true,
          opacity: 0.1 - i * 0.03,
          blending: THREE.AdditiveBlending,
          side: THREE.DoubleSide
        });
        const glow = new THREE.Mesh(glowGeometry, glowMaterial);
        glow.position.z = -0.1 - i * 0.05;
        panelGroup.add(glow);
      }

      // Enhanced title with better typography
      const canvas = document.createElement('canvas');
      canvas.width = 1024;
      canvas.height = 256;
      const context = canvas.getContext('2d');
      
      // Gradient background
      const gradient = context.createLinearGradient(0, 0, canvas.width, 0);
      gradient.addColorStop(0, 'rgba(0,0,0,0.8)');
      gradient.addColorStop(0.5, 'rgba(0,0,0,0.9)');
      gradient.addColorStop(1, 'rgba(0,0,0,0.8)');
      context.fillStyle = gradient;
      context.fillRect(0, 0, canvas.width, canvas.height);
      
      // Title text with shadow
      context.shadowColor = video.color;
      context.shadowBlur = 20;
      context.fillStyle = '#ffffff';
      context.font = 'bold 48px Arial, sans-serif';
      context.textAlign = 'center';
      context.fillText(video.title, 512, 100);
      
      // Year text
      context.shadowBlur = 10;
      context.font = '32px Arial, sans-serif';
      context.fillStyle = '#cccccc';
      context.fillText(video.year, 512, 160);
      
      // Brand accent
      context.shadowBlur = 15;
      context.fillStyle = video.color;
      context.font = 'italic 24px Arial, sans-serif';
      context.fillText('PARFUM COLLECTION', 512, 200);

      const textTexture = new THREE.CanvasTexture(canvas);
      textTexture.needsUpdate = true;
      const textGeometry = new THREE.PlaneGeometry(8, 2);
      const textMaterial = new THREE.MeshBasicMaterial({
        map: textTexture,
        transparent: true,
        opacity: 0.9
      });
      const textMesh = new THREE.Mesh(textGeometry, textMaterial);
      textMesh.position.y = -5.5;
      panelGroup.add(textMesh);

      // Add interaction zone (invisible mesh for raycasting)
      const interactionGeometry = new THREE.PlaneGeometry(12, 10);
      const interactionMaterial = new THREE.MeshBasicMaterial({
        transparent: true,
        opacity: 0,
        side: THREE.DoubleSide
      });
      const interactionMesh = new THREE.Mesh(interactionGeometry, interactionMaterial);
      interactionMesh.userData = { interactive: true, panelIndex: index };
      panelGroup.add(interactionMesh);

      panelGroup.position.set(x, y, z);
      panelGroup.lookAt(0, y, 0);
      
      // Staggered entrance animation
      panelGroup.scale.setScalar(0);
      panelGroup.rotation.y += Math.PI;
      panels.push(panelGroup);
      panelGroups.current.push(panelGroup);
      scene.add(panelGroup);

      // Enhanced entrance animation
      const animateIn = () => {
        const targetScale = 1;
        const targetRotationY = panelGroup.rotation.y - Math.PI;
        const currentScale = panelGroup.scale.x;
        const currentRotationY = panelGroup.rotation.y;
        
        if (currentScale < targetScale || Math.abs(currentRotationY - targetRotationY) > 0.01) {
          panelGroup.scale.setScalar(Math.min(targetScale, currentScale + 0.03));
          panelGroup.rotation.y += (targetRotationY - currentRotationY) * 0.1;
          requestAnimationFrame(animateIn);
        }
      };
      
      setTimeout(() => {
        animateIn();
      }, index * 300);
    });

    // Enhanced mouse/touch controls
    const handlePointerDown = (event) => {
      vrState.current.isMoving = true;
      vrState.current.lastMouseX = event.clientX || event.touches[0].clientX;
      vrState.current.lastMouseY = event.clientY || event.touches[0].clientY;
      
      // Check for panel clicks
      const rect = renderer.domElement.getBoundingClientRect();
      mouse.current.x = ((event.clientX - rect.left) / rect.width) * 2 - 1;
      mouse.current.y = -((event.clientY - rect.top) / rect.height) * 2 + 1;
      
      raycaster.current.setFromCamera(mouse.current, camera);
      const intersects = raycaster.current.intersectObjects(scene.children, true);
      
      for (let intersect of intersects) {
        if (intersect.object.userData.interactive && intersect.object.userData.panelIndex !== undefined) {
          handlePanelClick(intersect.object.userData.panelIndex);
          break;
        }
      }
    };

    const handlePointerMove = (event) => {
      if (!vrState.current.isMoving) {
        // Hover effects
        const rect = renderer.domElement.getBoundingClientRect();
        mouse.current.x = ((event.clientX - rect.left) / rect.width) * 2 - 1;
        mouse.current.y = -((event.clientY - rect.top) / rect.height) * 2 + 1;
        
        raycaster.current.setFromCamera(mouse.current, camera);
        const intersects = raycaster.current.intersectObjects(scene.children, true);
        
        // Reset all panels
        panelGroups.current.forEach(group => {
          if (group.userData.hovered) {
            group.userData.hovered = false;
          }
        });
        
        // Set hovered panel
        for (let intersect of intersects) {
          if (intersect.object.userData.interactive && intersect.object.userData.panelIndex !== undefined) {
            const panelIndex = intersect.object.userData.panelIndex;
            panelGroups.current[panelIndex].userData.hovered = true;
            document.body.style.cursor = 'pointer';
            break;
          } else {
            document.body.style.cursor = 'default';
          }
        }
        return;
      }

      const clientX = event.clientX || (event.touches && event.touches[0].clientX);
      const clientY = event.clientY || (event.touches && event.touches[0].clientY);

      const deltaX = clientX - vrState.current.lastMouseX;
      const deltaY = clientY - vrState.current.lastMouseY;

      vrState.current.targetRotation.y += deltaX * 0.008;
      vrState.current.targetRotation.x += deltaY * 0.008;
      vrState.current.targetRotation.x = Math.max(-Math.PI/2.5, Math.min(Math.PI/2.5, vrState.current.targetRotation.x));

      vrState.current.lastMouseX = clientX;
      vrState.current.lastMouseY = clientY;
    };

    const handlePointerUp = () => {
      vrState.current.isMoving = false;
    };

    // Enhanced keyboard controls
    const keys = {};
    const handleKeyDown = (event) => {
      keys[event.code] = true;
      
      // Quick panel selection
      const num = parseInt(event.key);
      if (num >= 1 && num <= videos.length) {
        handlePanelClick(num - 1);
      }
      
      if (event.code === 'Space') {
        event.preventDefault();
        // Reset to center
        vrState.current.targetPosition = { x: 0, y: 2, z: 30 };
        vrState.current.targetRotation = { x: 0, y: 0 };
        setSelectedPanel(null);
      }
    };
    
    const handleKeyUp = (event) => {
      keys[event.code] = false;
    };

    // Add event listeners
    const canvas = renderer.domElement;
    canvas.addEventListener('mousedown', handlePointerDown);
    canvas.addEventListener('mousemove', handlePointerMove);
    canvas.addEventListener('mouseup', handlePointerUp);
    canvas.addEventListener('mouseleave', handlePointerUp);
    canvas.addEventListener('touchstart', handlePointerDown, { passive: true });
    canvas.addEventListener('touchmove', handlePointerMove, { passive: true });
    canvas.addEventListener('touchend', handlePointerUp);

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    // Enhanced animation loop
    const animate = () => {
      frameId.current = requestAnimationFrame(animate);

      const time = Date.now() * 0.001;
      const deltaTime = 0.016; // Assume 60fps

      // Smooth camera movement with easing
      const lerpFactor = 0.08;
      vrState.current.rotation.y += (vrState.current.targetRotation.y - vrState.current.rotation.y) * lerpFactor;
      vrState.current.rotation.x += (vrState.current.targetRotation.x - vrState.current.rotation.x) * lerpFactor;
      
      vrState.current.position.x += (vrState.current.targetPosition.x - vrState.current.position.x) * lerpFactor;
      vrState.current.position.y += (vrState.current.targetPosition.y - vrState.current.position.y) * lerpFactor;
      vrState.current.position.z += (vrState.current.targetPosition.z - vrState.current.position.z) * lerpFactor;

      // Enhanced keyboard movement with momentum
      const moveSpeed = 1.2;
      const acceleration = 0.2;
      const friction = 0.85;

      if (keys['KeyW'] || keys['ArrowUp']) {
        vrState.current.velocity.z -= acceleration;
      }
      if (keys['KeyS'] || keys['ArrowDown']) {
        vrState.current.velocity.z += acceleration;
      }
      if (keys['KeyA'] || keys['ArrowLeft']) {
        vrState.current.velocity.x -= acceleration;
      }
      if (keys['KeyD'] || keys['ArrowRight']) {
        vrState.current.velocity.x += acceleration;
      }
      if (keys['KeyQ']) {
        vrState.current.velocity.y += acceleration;
      }
      if (keys['KeyE']) {
        vrState.current.velocity.y -= acceleration;
      }

      // Apply velocity with friction
      vrState.current.velocity.x *= friction;
      vrState.current.velocity.y *= friction;
      vrState.current.velocity.z *= friction;

      vrState.current.targetPosition.x += vrState.current.velocity.x * moveSpeed;
      vrState.current.targetPosition.y += vrState.current.velocity.y * moveSpeed;
      vrState.current.targetPosition.z += vrState.current.velocity.z * moveSpeed;

      // Apply camera transformations
      camera.position.set(
        vrState.current.position.x,
        vrState.current.position.y,
        vrState.current.position.z
      );
      camera.rotation.set(
        vrState.current.rotation.x,
        vrState.current.rotation.y,
        0
      );

      // Update spatial audio listener position
      if (audioContext.current && audioContext.current.listener) {
        audioContext.current.listener.setPosition(
          camera.position.x,
          camera.position.y,
          camera.position.z
        );
        
        const forward = new THREE.Vector3(0, 0, -1);
        const up = new THREE.Vector3(0, 1, 0);
        forward.applyQuaternion(camera.quaternion);
        up.applyQuaternion(camera.quaternion);
        
        audioContext.current.listener.setOrientation(
          forward.x, forward.y, forward.z,
          up.x, up.y, up.z
        );
      }

      // Animate enhanced particles with physics
      const positions = particles.geometry.attributes.position.array;
      const velocities = particleGeometry.userData?.velocities || particleVelocities;
      
      for (let i = 0; i < positions.length; i += 3) {
        positions[i] += velocities[i];
        positions[i + 1] += velocities[i + 1] + Math.sin(time + positions[i]) * 0.02;
        positions[i + 2] += velocities[i + 2];
        
        // Boundary wrapping
        if (Math.abs(positions[i]) > 75) velocities[i] *= -1;
        if (Math.abs(positions[i + 1]) > 75) velocities[i + 1] *= -1;
        if (Math.abs(positions[i + 2]) > 75) velocities[i + 2] *= -1;
      }
      particles.geometry.attributes.position.needsUpdate = true;
      particles.rotation.y = time * 0.05;

      // Enhanced panel animations
      panelGroups.current.forEach((group, index) => {
        const offset = time + index * 0.8;
        const hovered = group.userData.hovered;
        const selected = selectedPanel === index;
        
        // Floating animation
        group.position.y += Math.sin(offset) * 0.015;
        group.children[0].rotation.z = Math.sin(offset * 1.5) * 0.08; // Frame rotation
        
        // Hover and selection effects
        const targetScale = selected ? 1.2 : (hovered ? 1.1 : 1.0);
        const currentScale = group.scale.x;
        group.scale.setScalar(currentScale + (targetScale - currentScale) * 0.1);

        // Pulsing glow effects
       for (let i = 2; i < group.children.length - 2; i++) {
         if (group.children[i].material && group.children[i].material.opacity !== undefined) {
           const baseOpacity = 0.1 - (i - 2) * 0.03;
           const pulse = Math.sin(offset * 2 + i) * 0.05;
           const hoverBoost = hovered ? 0.1 : 0;
           const selectBoost = selected ? 0.2 : 0;
           group.children[i].material.opacity = Math.max(0, baseOpacity + pulse + hoverBoost + selectBoost);
         }
       }
       
       // Color shifting for selected panel
       if (selected && group.children[0].material) {
         const hue = (time * 0.5 + index) % 1;
         group.children[0].material.color.setHSL(hue, 0.8, 0.6);
       }
     });

     // Animate environment
     stars.rotation.y = time * 0.01;
     stars.rotation.x = Math.sin(time * 0.5) * 0.02;
     
     nebula.rotation.y = time * 0.003;
     nebula.material.opacity = 0.1 + Math.sin(time * 0.2) * 0.05;

     // Animate point lights
     pointLight1.position.x = Math.cos(time * 0.3) * 40;
     pointLight1.position.z = Math.sin(time * 0.3) * 40;
     pointLight1.intensity = 2 + Math.sin(time * 2) * 0.5;

     pointLight2.position.x = Math.cos(time * 0.4 + Math.PI) * 35;
     pointLight2.position.z = Math.sin(time * 0.4 + Math.PI) * 35;
     pointLight2.intensity = 2 + Math.cos(time * 1.8) * 0.5;

     renderer.render(scene, camera);
   };

   animate();

   // Handle resize
   const handleResize = () => {
     if (!mountRef.current) return;
     const width = mountRef.current.clientWidth;
     const height = mountRef.current.clientHeight;

     camera.aspect = width / height;
     camera.updateProjectionMatrix();
     renderer.setSize(width, height);
   };

   window.addEventListener('resize', handleResize);

   // Cleanup
   return () => {
     if (frameId.current) {
       cancelAnimationFrame(frameId.current);
     }
     
     canvas.removeEventListener('mousedown', handlePointerDown);
     canvas.removeEventListener('mousemove', handlePointerMove);
     canvas.removeEventListener('mouseup', handlePointerUp);
     canvas.removeEventListener('mouseleave', handlePointerUp);
     canvas.removeEventListener('touchstart', handlePointerDown);
     canvas.removeEventListener('touchmove', handlePointerMove);
     canvas.removeEventListener('touchend', handlePointerUp);
     
     window.removeEventListener('keydown', handleKeyDown);
     window.removeEventListener('keyup', handleKeyUp);
     window.removeEventListener('resize', handleResize);

     videoElements.current.forEach(video => {
       video.pause();
       video.src = '';
     });

     if (audioContext.current) {
       audioContext.current.close();
     }

     if (mountRef.current && renderer.domElement) {
       mountRef.current.removeChild(renderer.domElement);
     }

     renderer.dispose();
     
     // Dispose geometries and materials
     scene.traverse((object) => {
       if (object.geometry) object.geometry.dispose();
       if (object.material) {
         if (Array.isArray(object.material)) {
           object.material.forEach(material => material.dispose());
         } else {
           object.material.dispose();
         }
       }
     });
   };
 }, [handlePanelClick, createSpatialAudio, createTeleportationSystem, initAudio]);

 const enterVR = async () => {
   if (!isVRSupported || !rendererRef.current) return;

   try {
     const session = await navigator.xr.requestSession('immersive-vr', {
       optionalFeatures: ['hand-tracking', 'eye-tracking']
     });
     rendererRef.current.xr.setSession(session);
     setIsVRActive(true);
     
     // Resume audio context for VR
     if (audioContext.current && audioContext.current.state === 'suspended') {
       audioContext.current.resume();
     }
     
     session.addEventListener('end', () => {
       setIsVRActive(false);
     });
   } catch (error) {
     console.error('Failed to enter VR:', error);
   }
 };

 const resetView = () => {
   vrState.current.targetPosition = { x: 0, y: 2, z: 30 };
   vrState.current.targetRotation = { x: 0, y: 0 };
   vrState.current.velocity = { x: 0, y: 0, z: 0 };
   setSelectedPanel(null);
 };

 return (
   <div className="relative w-full h-screen bg-black overflow-hidden">
     {isLoading && (
       <div className="absolute inset-0 z-50 flex items-center justify-center bg-black">
         <div className="text-center text-white">
           <div className="w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
           <p className="text-xl font-semibold">Loading VR Gallery...</p>
           <p className="text-sm text-gray-400 mt-2">Preparing immersive experience</p>
         </div>
       </div>
     )}
     
     <div ref={mountRef} className="w-full h-full" />
     
     {/* Enhanced VR Controls */}
     <div className="absolute top-4 left-4 z-10 text-white">
       <div className="bg-black bg-opacity-70 p-5 rounded-xl backdrop-blur-md border border-gray-700">
         <h3 className="text-xl font-bold mb-3 text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-400">
           VR Perfume Gallery
         </h3>
         <div className="text-sm space-y-2">
           <p className="flex items-center gap-2">
             <span className="text-blue-400">ðŸ–±ï¸</span> Mouse: Look around
           </p>
           <p className="flex items-center gap-2">
             <span className="text-green-400">âŒ¨ï¸</span> WASD/Arrows: Move
           </p>
           <p className="flex items-center gap-2">
             <span className="text-purple-400">QE</span> Up/Down
           </p>
           <p className="flex items-center gap-2">
             <span className="text-yellow-400">1-6</span> Select panel
           </p>
           <p className="flex items-center gap-2">
             <span className="text-red-400">Space</span> Reset view
           </p>
           <p className="flex items-center gap-2">
             <span className="text-pink-400">ðŸ“±</span> Touch: Drag to look
           </p>
         </div>
         
         <div className="flex gap-2 mt-4">
           {isVRSupported && (
             <button
               onClick={enterVR}
               disabled={isVRActive}
               className="flex-1 px-4 py-2 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 disabled:from-gray-600 disabled:to-gray-600 rounded-lg text-white font-medium transition-all duration-300 transform hover:scale-105"
             >
               {isVRActive ? 'ðŸ¥½ VR Active' : 'ðŸ¥½ Enter VR'}
             </button>
           )}
           
           <button
             onClick={resetView}
             className="px-4 py-2 bg-gradient-to-r from-gray-600 to-gray-700 hover:from-gray-700 hover:to-gray-800 rounded-lg text-white font-medium transition-all duration-300 transform hover:scale-105"
           >
             ðŸŽ¯ Reset
           </button>
         </div>
       </div>
     </div>

     {/* Panel Selection Info */}
     {selectedPanel !== null && (
       <div className="absolute top-4 right-4 z-10 text-white">
         <div className="bg-black bg-opacity-70 p-4 rounded-xl backdrop-blur-md border border-gray-700 max-w-xs">
           <h4 className="text-lg font-bold text-transparent bg-clip-text bg-gradient-to-r from-pink-400 to-yellow-400">
             {videos[selectedPanel].title}
           </h4>
           <p className="text-gray-300">{videos[selectedPanel].year}</p>
           <p className="text-sm text-gray-400 mt-2">
             Selected fragrance in focus
           </p>
         </div>
       </div>
     )}

     {/* Enhanced Instructions */}
     <div className="absolute bottom-4 right-4 z-10 text-white">
       <div className="bg-black bg-opacity-70 p-4 rounded-xl backdrop-blur-md border border-gray-700 max-w-sm">
         <h4 className="font-semibold mb-2 text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 to-blue-400">
           ðŸŒŸ Immersive Experience
         </h4>
         <p className="text-sm text-gray-300 leading-relaxed">
           Navigate through this luxurious perfume gallery. Click on panels to focus, 
           use keyboard or mouse to explore. Experience spatial audio and dynamic lighting 
           in this premium fragrance showcase.
         </p>
       </div>
     </div>

     {/* Performance Stats (Debug) */}
     {process.env.NODE_ENV === 'development' && (
       <div className="absolute bottom-4 left-4 z-10 text-white">
         <div className="bg-black bg-opacity-50 p-2 rounded text-xs font-mono">
           <p>VR: {isVRSupported ? 'âœ…' : 'âŒ'}</p>
           <p>Audio: {audioContext.current ? 'ðŸ”Š' : 'ðŸ”‡'}</p>
           <p>Selected: {selectedPanel !== null ? selectedPanel + 1 : 'None'}</p>
         </div>
       </div>
     )}
   </div>
 );
};

export default VRPerfumeGallery;
