  { id: 5, title: 'ANIMATION STUDIO', year: '2024', type: 'CREATIVE', image: 'https://picsum.photos/400/300?random=5', category: 'ANIMATION' },
  { id: 6, title: 'FASHION WEEK', year: '2023', type: 'EVENT', image: 'https://picsum.photos/400/300?random=6', category: 'FASHION' },
  { id: 7, title: 'YELLOW SUBMARINE', year: '2024', type: 'INTERACTIVE', image: 'https://picsum.photos/400/300?random=7', category: 'MUSIC' },
  { id: 8, title: 'PORTRAIT SERIES', year: '2023', type: 'PHOTOGRAPHY', image: 'https://picsum.photos/400/300?random=8', category: 'ART' },
