  { id: 10, title: 'JUDAS PRIEST', year: '2023', type: 'MUSIC', image: 'https://picsum.photos/400/300?random=10', category: 'MUSIC' },
  { id: 11, title: 'ALTER EGO', year: '2024', type: 'ALBUM', image: 'https://picsum.photos/400/300?random=11', category: 'MUSIC' },
  { id: 12, title: 'DIGITAL ART', year: '2024', type: 'ARTWORK', image: 'https://picsum.photos/400/300?random=12', category: 'DIGITAL' },
];

const PhantomGallery = () => {
  const mountRef = useRef(null);
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const cameraRef = useRef(null);
  const meshesRef = useRef([]);
  const mouseRef = useRef({ x: 0, y: 0 });
  const targetRef = useRef({ x: 0, y: 0, z: 0 });
  const currentRef = useRef({ x: 0, y: 0, z: 0 });
  const [isLoading, setIsLoading] = useState(true);
  const [hoveredProject, setHoveredProject] = useState(null);

  useEffect(() => {
    if (!mountRef.current) return;

    // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ ÑÑ†ÐµÐ½Ñƒ
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x0a0a0a);
    scene.fog = new THREE.Fog(0x0a0a0a, 50, 200);
    sceneRef.current = scene;

    // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ ÐºÐ°Ð¼ÐµÑ€Ñƒ (Ð¸Ð·Ð¾Ð¼ÐµÑ‚Ñ€Ð¸Ñ‡ÐµÑÐºÐ¸Ð¹ Ð²Ð¸Ð´)
    const camera = new THREE.PerspectiveCamera(
      35,
      mountRef.current.clientWidth / mountRef.current.clientHeight,
      0.1,
      1000
    );
    camera.position.set(30, 25, 30);
    camera.lookAt(0, 0, 0);
    cameraRef.current = camera;

    // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ñ€ÐµÐ½Ð´ÐµÑ€ÐµÑ€
    const renderer = new THREE.WebGLRenderer({ 
      antialias: true, 
      alpha: true,
      powerPreference: "high-performance"
    });
    renderer.setSize(mountRef.current.clientWidth, mountRef.current.clientHeight);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFSoftShadowMap;
    mountRef.current.appendChild(renderer.domElement);
    rendererRef.current = renderer;

    // ÐžÑÐ²ÐµÑ‰ÐµÐ½Ð¸Ðµ
    const ambientLight = new THREE.AmbientLight(0x404040, 0.3);
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
    directionalLight.position.set(50, 50, 25);
    directionalLight.castShadow = true;
    directionalLight.shadow.mapSize.width = 2048;
    directionalLight.shadow.mapSize.height = 2048;
    directionalLight.shadow.camera.near = 0.1;
    directionalLight.shadow.camera.far = 200;
    directionalLight.shadow.camera.left = -50;
    directionalLight.shadow.camera.right = 50;
    directionalLight.shadow.camera.top = 50;
    directionalLight.shadow.camera.bottom = -50;
    scene.add(directionalLight);

    // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ ÑÐµÑ‚ÐºÑƒ Ð¿Ñ€Ð¾ÐµÐºÑ‚Ð¾Ð²
    const geometry = new THREE.PlaneGeometry(8, 6);
    const loader = new THREE.TextureLoader();
    const meshes = [];

    // ÐŸÐ°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ñ‹ ÑÐµÑ‚ÐºÐ¸
    const cols = 4;
    const rows = 3;
    const spacing = 12;

    // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐºÐ¸ Ð¿Ñ€Ð¾ÐµÐºÑ‚Ð¾Ð²
    projects.forEach((project, i) => {
      const row = Math.floor(i / cols);
      const col = i % cols;
      
      // ÐŸÐ¾Ð·Ð¸Ñ†Ð¸Ð¾Ð½Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ Ð² ÑÐµÑ‚ÐºÐµ
      const x = (col - (cols - 1) / 2) * spacing;
      const z = (row - (rows - 1) / 2) * spacing;
      const y = 0;

      // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ð³Ñ€ÑƒÐ¿Ð¿Ñƒ Ð´Ð»Ñ ÐºÐ°Ð¶Ð´Ð¾Ð³Ð¾ Ð¿Ñ€Ð¾ÐµÐºÑ‚Ð°
      const group = new THREE.Group();
      group.position.set(x, y, z);
      group.userData = { project, originalY: y };

      // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð¸Ð·Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ðµ
      loader.load(
        project.image,
        (texture) => {
          texture.flipY = false;
          
          // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ð¼Ð°Ñ‚ÐµÑ€Ð¸Ð°Ð»
          const material = new THREE.MeshLambertMaterial({
            map: texture,
            transparent: true,
          });

          // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ð¼ÐµÑˆ
          const mesh = new THREE.Mesh(geometry, material);
          mesh.castShadow = true;
          mesh.receiveShadow = true;
          group.add(mesh);

          // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ñ€Ð°Ð¼ÐºÑƒ
          const borderGeometry = new THREE.PlaneGeometry(8.2, 6.2);
          const borderMaterial = new THREE.MeshBasicMaterial({
            color: 0x222222,
            transparent: true,
            opacity: 0.8
          });
          const border = new THREE.Mesh(borderGeometry, borderMaterial);
          border.position.z = -0.01;
          group.add(border);

          // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ð¿Ð¾Ð´Ð»Ð¾Ð¶ÐºÑƒ Ñ Ñ‚ÐµÐ½ÑŒÑŽ
          const shadowGeometry = new THREE.PlaneGeometry(8.5, 6.5);
          const shadowMaterial = new THREE.MeshBasicMaterial({
            color: 0x000000,
            transparent: true,
            opacity: 0.3
          });
          const shadow = new THREE.Mesh(shadowGeometry, shadowMaterial);
          shadow.position.set(0.5, -0.5, -0.02);
          shadow.rotation.x = -Math.PI / 2;
          group.add(shadow);

          meshes.push(group);
          scene.add(group);

          if (meshes.length === projects.length) {
            setIsLoading(false);
          }
        },
        undefined,
        (error) => {
          console.warn('ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸:', error);
        }
      );
    });

    meshesRef.current = meshes;

    // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° Ð´Ð²Ð¸Ð¶ÐµÐ½Ð¸Ñ Ð¼Ñ‹ÑˆÐ¸
    const handleMouseMove = (event) => {
      const rect = mountRef.current.getBoundingClientRect();
      mouseRef.current.x = ((event.clientX - rect.left) / rect.width) * 2 - 1;
      mouseRef.current.y = -((event.clientY - rect.top) / rect.height) * 2 + 1;

      // ÐŸÐ°Ñ€Ð°Ð»Ð»Ð°ÐºÑ ÑÑ„Ñ„ÐµÐºÑ‚
      targetRef.current.x = mouseRef.current.x * 5;
      targetRef.current.y = mouseRef.current.y * 3;
      targetRef.current.z = mouseRef.current.x * 2;
    };

    // Raycasting Ð´Ð»Ñ hover ÑÑ„Ñ„ÐµÐºÑ‚Ð¾Ð²
    const raycaster = new THREE.Raycaster();
    const handleMouseHover = (event) => {
      const rect = mountRef.current.getBoundingClientRect();
      const mouse = new THREE.Vector2();
      mouse.x = ((event.clientX - rect.left) / rect.width) * 2 - 1;
      mouse.y = -((event.clientY - rect.top) / rect.height) * 2 + 1;

      raycaster.setFromCamera(mouse, camera);
      const intersects = raycaster.intersectObjects(meshes, true);

      if (intersects.length > 0) {
        const hoveredMesh = intersects[0].object.parent;
        setHoveredProject(hoveredMesh.userData.project);
        
        // ÐÐ½Ð¸Ð¼Ð°Ñ†Ð¸Ñ hover
        meshes.forEach(mesh => {
          if (mesh === hoveredMesh) {
            mesh.userData.targetY = mesh.userData.originalY + 2;
            mesh.userData.targetScale = 1.05;
          } else {
            mesh.userData.targetY = mesh.userData.originalY;
            mesh.userData.targetScale = 1;
          }
        });
      } else {
        setHoveredProject(null);
        meshes.forEach(mesh => {
          mesh.userData.targetY = mesh.userData.originalY;
          mesh.userData.targetScale = 1;
        });
      }
    };

    mountRef.current.addEventListener('mousemove', handleMouseMove);
    mountRef.current.addEventListener('mousemove', handleMouseHover);

    // ÐÐ½Ð¸Ð¼Ð°Ñ†Ð¸Ð¾Ð½Ð½Ñ‹Ð¹ Ñ†Ð¸ÐºÐ»
    const animate = () => {
      requestAnimationFrame(animate);

      // ÐŸÐ»Ð°Ð²Ð½Ð¾Ðµ Ð´Ð²Ð¸Ð¶ÐµÐ½Ð¸Ðµ ÐºÐ°Ð¼ÐµÑ€Ñ‹
      currentRef.current.x += (targetRef.current.x - currentRef.current.x) * 0.05;
      currentRef.current.y += (targetRef.current.y - currentRef.current.y) * 0.05;
      currentRef.current.z += (targetRef.current.z - currentRef.current.z) * 0.05;

      camera.position.set(
        30 + currentRef.current.x,
        25 + currentRef.current.y,
        30 + currentRef.current.z
      );
      camera.lookAt(currentRef.current.x * 0.1, currentRef.current.y * 0.1, 0);

      // ÐÐ½Ð¸Ð¼Ð°Ñ†Ð¸Ñ Ð¼ÐµÑˆÐµÐ¹
      meshes.forEach(mesh => {
        if (mesh.userData.targetY !== undefined) {
          mesh.position.y += (mesh.userData.targetY - mesh.position.y) * 0.1;
        }
        if (mesh.userData.targetScale !== undefined) {
          const currentScale = mesh.scale.x;
          const newScale = currentScale + (mesh.userData.targetScale - currentScale) * 0.1;
          mesh.scale.set(newScale, newScale, newScale);
        }
      });

      renderer.render(scene, camera);
    };

    animate();

    // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ñ Ñ€Ð°Ð·Ð¼ÐµÑ€Ð°
    const handleResize = () => {
      if (!mountRef.current) return;
      camera.aspect = mountRef.current.clientWidth / mountRef.current.clientHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(mountRef.current.clientWidth, mountRef.current.clientHeight);
    };

    window.addEventListener('resize', handleResize);

    // ÐžÑ‡Ð¸ÑÑ‚ÐºÐ°
    return () => {
      if (mountRef.current) {
        mountRef.current.removeEventListener('mousemove', handleMouseMove);
        mountRef.current.removeEventListener('mousemove', handleMouseHover);
      }
      window.removeEventListener('resize', handleResize);
      
      if (mountRef.current && renderer.domElement) {
        mountRef.current.removeChild(renderer.domElement);
      }
      renderer.dispose();
    };
  }, []);

  return (
    <div style={{
      width: '100%',
      height: '100vh',
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 100%)',
      overflow: 'hidden',
      position: 'relative',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
    }}>
      {/* Header */}
      <div style={{
        position: 'absolute',
        top: '20px',
        left: '20px',
        right: '20px',
        zIndex: 10,
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <div style={{ color: 'white', fontSize: '24px', fontWeight: 'bold' }}>
          PHANTOM
        </div>
        <div style={{ color: 'rgba(255,255,255,0.6)', fontSize: '14px' }}>
          LONDON, UK â€¢ 15:15 GMT+1
        </div>
      </div>

      {/* Navigation */}
      <div style={{
        position: 'absolute',
        bottom: '40px',
        left: '50%',
        transform: 'translateX(-50%)',
        zIndex: 10,
        display: 'flex',
        background: 'rgba(255,255,255,0.1)',
        backdropFilter: 'blur(10px)',
        borderRadius: '25px',
        padding: '8px'
      }}>
        {['Work', 'About', 'Careers'].map(item => (
          <button
            key={item}
            style={{
              background: item === 'Work' ? 'white' : 'transparent',
              color: item === 'Work' ? 'black' : 'white',
              border: 'none',
              padding: '12px 24px',
              borderRadius: '20px',
              fontSize: '14px',
              fontWeight: '500',
              cursor: 'pointer',
              transition: 'all 0.3s ease'
            }}
          >
            {item}
          </button>
        ))}
      </div>

      {/* Filter Button */}
      <div style={{
        position: 'absolute',
        bottom: '40px',
        right: '40px',
        zIndex: 10
      }}>
        <button style={{
          background: 'rgba(255,255,255,0.1)',
          backdropFilter: 'blur(10px)',
          color: 'white',
          border: 'none',
          padding: '12px 20px',
          borderRadius: '20px',
          fontSize: '14px',
          cursor: 'pointer'
        }}>
          Filter
        </button>
      </div>

      {/* Project Info Tooltip */}
      {hoveredProject && (
        <div style={{
          position: 'absolute',
          top: '100px',
          right: '40px',
          zIndex: 10,
          background: 'rgba(0,0,0,0.8)',
          backdropFilter: 'blur(10px)',
          color: 'white',
          padding: '20px',
          borderRadius: '10px',
          maxWidth: '300px',
          border: '1px solid rgba(255,255,255,0.1)'
        }}>
          <h3 style={{ margin: '0 0 10px 0', fontSize: '18px' }}>
            {hoveredProject.title}
          </h3>
          <p style={{ margin: '0 0 5px 0', color: 'rgba(255,255,255,0.6)', fontSize: '14px' }}>
            {hoveredProject.year} â€¢ {hoveredProject.type}
          </p>
          <p style={{ margin: '0', color: 'rgba(255,255,255,0.8)', fontSize: '12px' }}>
            Category: {hoveredProject.category}
          </p>
        </div>
      )}

      {/* Loading */}
      {isLoading && (
        <div style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          color: 'white',
          fontSize: '18px',
          zIndex: 10
        }}>
          Loading...
        </div>
      )}

      {/* 3D Canvas */}
      <div ref={mountRef} style={{ width: '100%', height: '100%' }} />
      
      {/* Sound indicator */}
      <div style={{
        position: 'absolute',
        top: '20px',
        left: '50%',
        transform: 'translateX(-50%)',
        color: 'rgba(255,255,255,0.4)',
        fontSize: '12px',
        zIndex: 10
      }}>
        â™ª SOUND [ON]
      </div>

      {/* Agency description */}
      <div style={{
        position: 'absolute',
        top: '60px',
        right: '20px',
        color: 'rgba(255,255,255,0.6)',
        fontSize: '12px',
        maxWidth: '300px',
        textAlign: 'right',
        zIndex: 10,
        lineHeight: '1.4'
      }}>
        PHANTOM IS A TECHNOLOGY-LED<br/>
        CREATIVE AGENCY CRAFTING<br/>
        EXPERIENCES FOR GLOBAL BRANDS.
      </div>
    </div>
  );
};

export default Carousel3D;
