// src/components/Carousel3D.jsx
import React, { useRef } from 'react';
import { Canvas, useFrame, extend, useThree } from '@react-three/fiber';
import { Html, useTexture, OrbitControls } from '@react-three/drei';
import * as THREE from 'three';
import styled from 'styled-components';

const images = [
  '/assets/img1.jpg',
  '/assets/img2.jpg',
  '/assets/img3.jpg',
  '/assets/img4.jpg',
  '/assets/img5.jpg',
];

function ImagePlane({ url, index, total }) {
  const ref = useRef();
  const texture = useTexture(url);

  useFrame(({ clock }) => {
    const t = clock.getElapsedTime();
    const angle = (index / total) * Math.PI * 2 + t * 0.1;
    const radius = 4;
    ref.current.position.x = Math.cos(angle) * radius;
    ref.current.position.z = Math.sin(angle) * radius;
    ref.current.lookAt(0, 0, 0);
  });

  return (
    <mesh ref={ref}>
      <planeGeometry args={[2, 3]} />
      <meshBasicMaterial map={texture} toneMapped={false} />
    </mesh>
  );
}

function CameraRig() {
  const { camera, mouse } = useThree();
  const vec = new THREE.Vector3();

  useFrame(() => {
    vec.set(mouse.x * 2, mouse.y * 1.5, 8); // Depth
    camera.position.lerp(vec, 0.05); // Smooth transition
    camera.lookAt(0, 0, 0);
  });

  return null;
}

const Carousel3D = () => {
  return (
    <Wrapper>
      <Canvas camera={{ position: [0, 1.5, 8], fov: 50 }}>
        <ambientLight intensity={1.5} />
        <CameraRig />
        {images.map((url, i) => (
          <ImagePlane key={i} url={url} index={i} total={images.length} />
        ))}
      </Canvas>
    </Wrapper>
  );
};

export default Carousel3D;

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: radial-gradient(ellipse at center, #000 0%, #111 100%);
  overflow: hidden;
  cursor: grab;
`;
