import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

const text = "PHANTOM STUDIOS TECHNOLOGY CREATIVE";

const Preloader = ({ onFinish }) => {
  const [progress, setProgress] = useState(0);
  const [exitAnim, setExitAnim] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setProgress((prev) => {
        if (prev >= 100) {
          clearInterval(interval);
          setExitAnim(true);
          return 100;
        }
        return prev + 1;
      });
    }, 30);
    return () => clearInterval(interval);
  }, []);

  // Когда анимация ухода текста закончится — вызываем onFinish
  const handleAnimationComplete = () => {
    if (exitAnim) onFinish();
  };

  return (
    <div className="fixed inset-0 bg-black flex flex-col justify-center items-center overflow-hidden">
      <AnimatePresence>
        {!exitAnim && (
          <motion.div
            initial={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="w-full px-4"
          >
            {/* Текст по центру */}
            <motion.div
              className="flex justify-center space-x-4 text-white uppercase font-semibold tracking-widest text-center text-xl md:text-3xl"
            >
              {text.split(" ").map((word, i) => (
                <motion.span
                  key={i}
                  className="inline-block"
                  initial={{ opacity: 1, x: 0 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={
                    i % 2 === 0
                      ? { x: "-100vw", opacity: 0 }
                      : { x: "100vw", opacity: 0 }
                  }
                  transition={{ duration: 1 }}
                  onAnimationComplete={
                    i === text.split(" ").length - 1
                      ? handleAnimationComplete
                      : undefined
                  }
                >
                  {word}
                </motion.span>
              ))}
            </motion.div>

            {/* Зеленая полоска загрузки внизу экрана */}
            <div className="fixed bottom-0 left-0 w-full h-1 bg-gray-800">
              <motion.div
                className="h-full bg-green-500"
                style={{ width: `${progress}%` }}
                transition={{ ease: "linear" }}
              />
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default Preloader;
