// src/components/Carousel3D.jsx
import React, { Suspense, useEffect, useRef, useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Html, useTexture } from '@react-three/drei';
import * as THREE from 'three';
import styled from 'styled-components';

// üß© –ú–∞—Å—Å–∏–≤ –º–µ–¥–∏–∞
const media = [
  { type: 'image', url: '/assets/img1.jpg' },
  { type: 'image', url: '/assets/img2.jpg' },
  { type: 'image', url: '/assets/img3.jpg' },
  { type: 'video', url: '/assets/video1.mp4' },
  { type: 'image', url: '/assets/img4.jpg' },
  { type: 'video', url: '/assets/video2.mp4' },
];

// üñº –û—Ç–¥–µ–ª—å–Ω—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è
function ImagePlane({ url, position, rotation }) {
  const texture = useTexture(url);
  return (
    <mesh position={position} rotation={rotation}>
      <planeGeometry args={[3, 2]} />
      <meshBasicMaterial map={texture} toneMapped={false} transparent />
    </mesh>
  );
}

// üé• –û—Ç–¥–µ–ª—å–Ω—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è –≤–∏–¥–µ–æ
function VideoPlane({ url, position, rotation }) {
  const [video] = useState(() => Object.assign(document.createElement('video'), {
    src: url,
    crossOrigin: 'anonymous',
    loop: true,
    muted: true,
    playsInline: true,
    autoplay: true,
  }));

  const [texture, setTexture] = useState(null);

  useEffect(() => {
    video.play().catch(err => console.warn('Autoplay blocked:', err));
    const tex = new THREE.VideoTexture(video);
    tex.minFilter = THREE.LinearFilter;
    tex.magFilter = THREE.LinearFilter;
    tex.format = THREE.RGBFormat;
    setTexture(tex);
  }, [video]);

  if (!texture) return null;

  return (
    <mesh position={position} rotation={rotation}>
      <planeGeometry args={[3, 2]} />
      <meshBasicMaterial map={texture} toneMapped={false} transparent />
    </mesh>
  );
}

// üîÅ VR-—Å–µ—Ç–∫–∞ –º–µ–¥–∏–∞
function CurvedMediaGrid({ itemsPerRow = 5, radius = 9 }) {
  return (
    <>
      {media.map((item, i) => {
        const row = Math.floor(i / itemsPerRow);
        const col = i % itemsPerRow;
        const angle = ((col - (itemsPerRow - 1) / 2) / itemsPerRow) * Math.PI;

        const x = Math.sin(angle) * radius;
        const z = Math.cos(angle) * radius;
        const y = 1.5 - row * 3;
        const rotation = [0, angle, 0];

        return item.type === 'image' ? (
          <ImagePlane key={i} url={item.url} position={[x, y, z]} rotation={rotation} />
        ) : (
          <VideoPlane key={i} url={item.url} position={[x, y, z]} rotation={rotation} />
        );
      })}
    </>
  );
}

// üß† –ì–ª–∞–≤–Ω—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç
const Carousel3D = () => {
  return (
    <Wrapper>
      <Canvas camera={{ position: [0, 0, 14], fov: 60 }}>
        <color attach="background" args={['#000']} />
        <ambientLight intensity={1.2} />
        <Suspense fallback={<Html center>Loading...</Html>}>
          <CurvedMediaGrid />
        </Suspense>
        <OrbitControls enableZoom={false} enablePan={false} />
      </Canvas>
    </Wrapper>
  );
};

export default Carousel3D;

// üé® –°—Ç–∏–ª–∏
const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: #000;
  overflow: hidden;
  cursor: grab;
`;
