import React, { useState } from "react";
import styled from "styled-components";
import { FaVolumeUp, FaVolumeMute } from "react-icons/fa"; // Импортируем иконки из react-icons

const Nav = styled.div`
  position: fixed;
  top: 0;
  width: 100%;
  padding: 1rem 2rem;
  display: flex;
  justify-content: space-between;
  z-index: 100;
`;

const Logo = styled.img`
  width: 80px;
  height: auto;
  cursor: pointer;
`;

const Button = styled.button`
  background: white;
  color: black;
  border-radius: 999px;
  padding: 0.5rem 1rem;
  font-size: 0.9rem;
  font-weight: bold;
  border: none;
  cursor: pointer;
`;

const SoundButton = styled.div`
  display: flex;
  align-items: center;
  cursor: pointer;
  font-size: 1rem;
`;

const Navbar = () => {
  const [isSoundOn, setIsSoundOn] = useState(true); // Состояние для переключателя звука

  const handleSoundToggle = () => {
    setIsSoundOn(!isSoundOn); // Переключаем состояние
  };

  const handleLogoClick = (event) => {
    event.preventDefault(); // Предотвращает перезагрузку страницы
    window.history.pushState({}, "", "/"); // Изменяет URL без перезагрузки
  };

  return (
    <Nav>
      <a href="/" onClick={handleLogoClick}>
        <Logo src="/assets/logo.png" alt="logo" />
      </a>
      <div>
        <SoundButton onClick={handleSoundToggle}>
          {isSoundOn ? (
            <>
              <FaVolumeUp style={{ marginRight: "0.5rem" }} /> Sound [ON]
            </>
          ) : (
            <>
              <FaVolumeMute style={{ marginRight: "0.5rem" }} /> Sound [OFF]
            </>
          )}
        </SoundButton>
        <Button>Let’s Talk</Button>
      </div>
    </Nav>
  );
};

export default Navbar;
