import React, { useEffect, useRef, useState } from "react";

const About = () => {
  const headerRef = useRef(null);
  const sectionsRef = useRef([]);
  const musicRef = useRef(null);        // music.mp3 ‚Äî –¥–ª—è –≤–∏–¥–µ–æ
  const aboutMusicRef = useRef(null);   // about.mp3 ‚Äî —Ñ–æ–Ω–æ–≤–∞—è –º—É–∑—ã–∫–∞
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.style.opacity = '1';
            entry.target.style.transform = 'translateY(0)';
          }
        });
      },
      { threshold: 0.1, rootMargin: '0px 0px -50px 0px' }
    );

    if (headerRef.current) {
      observer.observe(headerRef.current);
    }

    sectionsRef.current.forEach(section => {
      if (section) observer.observe(section);
    });

    return () => observer.disconnect();
  }, []);

  const handleVideoClick = () => {
    const music = musicRef.current;
    const aboutMusic = aboutMusicRef.current;
    if (!music || !aboutMusic) return;

    if (music.paused) {
      aboutMusic.pause();
      music.play()
        .then(() => setIsPlaying(true))
        .catch(err => console.error("music.mp3 play failed:", err));
    } else {
      music.pause();
      setIsPlaying(false);
      aboutMusic.play().catch(err => console.error("about.mp3 play failed:", err));
    }
  };

  return (
    <div className="min-h-screen bg-black text-white overflow-hidden">
      {/* Background Elements */}
      <div className="fixed inset-0 pointer-events-none">
        <div className="absolute top-1/4 -right-1/4 w-96 h-96 bg-gradient-to-r from-purple-500/10 to-blue-500/10 rounded-full blur-3xl"></div>
        <div className="absolute bottom-1/4 -left-1/4 w-80 h-80 bg-gradient-to-r from-pink-500/10 to-orange-500/10 rounded-full blur-3xl"></div>
      </div>

      <div className="relative z-10 px-6 md:px-16 lg:px-24 xl:px-32">
        {/* Header */}
        <header 
          ref={headerRef}
          className="pt-32 pb-16 md:pt-48 md:pb-24 opacity-0 transform translate-y-8 transition-all duration-1000 ease-out"
        >
          <div className="max-w-6xl">
            <h1 className="text-4xl md:text-6xl lg:text-7xl xl:text-8xl font-light leading-[0.9] tracking-tight mb-8">
              <span className="block">Olfy online store &</span>
              <span className="block text-transparent bg-gradient-to-r from-white via-gray-300 to-gray-500 bg-clip-text">
                –ø–∞—Ä—Ñ—é–º–µ—Ä–∏–∏,
              </span>
              <span className="block">—Å–æ–∑–¥–∞—é—â–∏–π —É–Ω–∏–∫–∞–ª—å–Ω—ã–µ</span>
              <span className="block font-extralight italic text-gray-400">–≤–µ–±-–æ–ø—ã—Ç—ã.</span>
            </h1>
            <div className="w-24 h-px bg-gradient-to-r from-white to-transparent mt-12"></div>
          </div>
        </header>

        {/* Main Content */}
        <main className="pb-32">
          {/* What we do */}
          <section 
            ref={el => sectionsRef.current[0] = el}
            className="mb-24 md:mb-32 opacity-0 transform translate-y-8 transition-all duration-1000 ease-out delay-200"
          >
            <div className="grid grid-cols-1 lg:grid-cols-12 gap-8 lg:gap-16 max-w-6xl">
              <div className="lg:col-span-3">
                <h2 className="text-sm uppercase tracking-widest text-gray-500 font-medium">
                  01 ‚Äî –ø–æ—á–µ–º—É –∏–º–µ–Ω–Ω–æ –º—ã?
                </h2>
              </div>
              <div className="lg:col-span-9">
                <p className="text-xl md:text-2xl lg:text-3xl leading-relaxed text-gray-300 font-light">
                  –í—ã–±–∏—Ä–∞—è –ø–∞—Ä—Ñ—é–º, –≤—ã –≤—ã–±–∏—Ä–∞–µ—Ç–µ –Ω–µ –ø—Ä–æ—Å—Ç–æ –∞—Ä–æ–º–∞—Ç{' '}
                  <span className="text-white font-normal">–≤—ã —Å–æ–∑–¥–∞–µ—Ç–µ –∞—Ç–º–æ—Å—Ñ–µ—Ä—É</span>,
                  –∫–æ—Ç–æ—Ä–∞—è –±—É–¥–µ—Ç —Å–æ–ø—Ä–æ–≤–æ–∂–¥–∞—Ç—å –≤–∞—Å –∫–∞–∂–¥—ã–π –¥–µ–Ω—å. –ù–∞—à –æ–Ω–ª–∞–π–Ω-–º–∞–≥–∞–∑–∏–Ω - —ç—Ç–æ –º–µ—Å—Ç–æ, –≥–¥–µ –∫–∞–∂–¥—ã–π —Å–º–æ–∂–µ—Ç –Ω–∞–π—Ç–∏ —Å–≤–æ–π –∏–¥–µ–∞–ª—å–Ω—ã–π –∑–∞–ø–∞—Ö, –∞ –º—ã –ø–æ–º–æ–∂–µ–º –≤–∞–º –≤ —ç—Ç–æ–º!
                </p>
              </div>
            </div>
          </section>

          {/* How we think */}
          <section 
            ref={el => sectionsRef.current[1] = el}
            className="mb-24 md:mb-32 opacity-0 transform translate-y-8 transition-all duration-1000 ease-out delay-400"
          >
            <div className="grid grid-cols-1 lg:grid-cols-12 gap-8 lg:gap-16 max-w-6xl">
              <div className="lg:col-span-3">
                <h2 className="text-sm uppercase tracking-widest text-gray-500 font-medium">
                  02 ‚Äî –ú–æ—è –ö–∞—Ä—å–µ—Ä–∞
                </h2>
              </div>
              <div className="lg:col-span-9">
                <p className="text-xl md:text-2xl lg:text-3xl leading-relaxed text-gray-300 font-light">
                  –ö–æ–≥–¥–∞ –º–Ω–µ –±—ã–ª–æ 14-15 –ª–µ—Ç, —è –∑–∞—Ö–æ—Ç–µ–ª –∫—É–ø–∏—Ç—å —Å–µ–±–µ AirPods Pro. –ß—Ç–æ–±—ã –Ω–∞–∫–æ–ø–∏—Ç—å –Ω–∞ –Ω–∏—Ö, —è –Ω–∞—á–∞–ª –∏—Å–∫–∞—Ç—å –ø–æ—Å—Ç–∞–≤—â–∏–∫–∞ —Å —Ö–æ—Ä–æ—à–µ–π —Ü–µ–Ω–æ–π –∏, –≤ –∏—Ç–æ–≥–µ, –Ω–∞—à–µ–ª. –í–º–µ—Å—Ç–µ —Å –∑–Ω–∞–∫–æ–º—ã–º —è –ø–æ–µ—Ö–∞–ª, —á—Ç–æ–±—ã –∫—É–ø–∏—Ç—å –∏—Ö, –∏ –∫–∞—á–µ—Å—Ç–≤–æ –æ–∫–∞–∑–∞–ª–æ—Å—å –¥–µ–π—Å—Ç–≤–∏—Ç–µ–ª—å–Ω–æ –æ—Ç–ª–∏—á–Ω—ã–º. –ü–æ—Å–ª–µ —ç—Ç–æ–≥–æ —è —Å–∫–∞–∑–∞–ª: ¬´–ê –º–æ–∂–µ—Ç, —è –∏—Ö –ø—Ä–æ–¥–∞–º?¬ª –û–Ω –ø–æ–¥–¥–µ—Ä–∂–∞–ª –º–æ—é –∏–¥–µ—é, –∏ —Ç–∞–∫ –Ω–∞—á–∞–ª–∞—Å—å –º–æ—è –ø—Ä–µ–¥–ø—Ä–∏–Ω–∏–º–∞—Ç–µ–ª—å—Å–∫–∞—è –∏—Å—Ç–æ—Ä–∏—è.

–°–Ω–∞—á–∞–ª–∞ —è –≤–∑—è–ª –Ω–µ–±–æ–ª—å—à–æ–π –¥–æ–ª–≥ —É –º–∞–º—ã, —á—Ç–æ–±—ã –∫—É–ø–∏—Ç—å —Ç–æ–≤–∞—Ä –æ–ø—Ç–æ–º, –∏ —Å —Ç–µ—Ö –ø–æ—Ä —Å—Ç–∞–ª –∑–∞–Ω–∏–º–∞—Ç—å—Å—è –ø—Ä–æ–¥–∞–∂–∞–º–∏. –Ø –ø—Ä–æ–±–æ–≤–∞–ª —Ä–∞–∑–Ω—ã–µ –Ω–∏—à–∏ - –æ–¥–µ–∂–¥—É, –∫—Ä–æ—Å—Å–æ–≤–∫–∏, –∏–≥—Ä—É—à–∫–∏, –Ω–æ –≤ –∫–∞–∫–æ–π-—Ç–æ –º–æ–º–µ–Ω—Ç –ø–æ–Ω—è–ª, —á—Ç–æ –ø–∞—Ä—Ñ—é–º–µ—Ä–∏—è - —ç—Ç–æ —Ç–æ, —á—Ç–æ –º–µ–Ω—è –¥–µ–π—Å—Ç–≤–∏—Ç–µ–ª—å–Ω–æ –≤–¥–æ—Ö–Ω–æ–≤–ª—è–µ—Ç. –¢–∞–∫ —è –≤—ã–±—Ä–∞–ª —ç—Ç—É –Ω–∏—à—É –∏ –æ—Ç–∫—Ä—ã–ª —Å–≤–æ–π –æ–Ω–ª–∞–π–Ω-–º–∞–≥–∞–∑–∏–Ω.

–ö—Ä–æ–º–µ —Ç–æ–≥–æ, —è —É—á—É—Å—å –Ω–∞ IT-—Å–ø–µ—Ü–∏–∞–ª–∏—Å—Ç–∞. –ü–∏—à—É –∫–æ–¥, —Å–æ–∑–¥–∞—é —Å–∞–π—Ç—ã, –∏–∑—É—á–∞—é –Ω–æ–≤—ã–µ —Ç–µ—Ö–Ω–æ–ª–æ–≥–∏–∏ –∏ –ø–æ—Å—Ç–æ—è–Ω–Ω–æ —Ä–∞–∑–≤–∏–≤–∞—é—Å—å –≤ —ç—Ç–æ–π —Å—Ñ–µ—Ä–µ. –ë–ª–∞–≥–æ–¥–∞—Ä—è —ç—Ç–∏–º –∑–Ω–∞–Ω–∏—è–º —è —Å–º–æ–≥ —Å–æ–∑–¥–∞—Ç—å —ç—Ç–æ—Ç —Å–∞–π—Ç –∏ –ø—Ä–æ–¥–æ–ª–∂–∞—é —Ä–∞–∑–≤–∏–≤–∞—Ç—å —Å–≤–æ–π –±–∏–∑–Ω–µ—Å. –í –±—É–¥—É—â–µ–º –ø–ª–∞–Ω–∏—Ä—É—é —Ä–∞—Å—à–∏—Ä—è—Ç—å –ø—Ä–æ–µ–∫—Ç –∏ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å —Å–≤–æ–∏ –Ω–∞–≤—ã–∫–∏ –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è –Ω–æ–≤—ã—Ö –∏ –∏–Ω—Ç–µ—Ä–µ—Å–Ω—ã—Ö —Ä–µ—à–µ–Ω–∏–π.
                </p>
              </div>
            </div>
          </section>

          {/* Where we are */}
          <section 
            ref={el => sectionsRef.current[2] = el}
            className="mb-24 md:mb-32 opacity-0 transform translate-y-8 transition-all duration-1000 ease-out delay-600"
          >
            <div className="grid grid-cols-1 lg:grid-cols-12 gap-8 lg:gap-16 max-w-6xl">
              <div className="lg:col-span-3">
                <h2 className="text-sm uppercase tracking-widest text-gray-500 font-medium">
                  03 ‚Äî –≥–¥–µ –º—ã –Ω–∞—Ö–æ–¥–∏–º—Å—è?
                </h2>
              </div>
              <div className="lg:col-span-9">
                <p className="text-xl md:text-2xl lg:text-3xl leading-relaxed text-gray-300 font-light">
                  –ú—ã –Ω–∞—Ö–æ–¥–∏–º—Å—è –≤ –ê—Å—Ç–∞–Ω–µ.{' '}
                  –î–æ—Å—Ç–∞–≤–∫–∞ –ø–æ –≥–æ—Ä–æ–¥—É –∏ <span className="text-white font-normal">–ø–æ –≤—Å–µ–º—É –ö–∞–∑–∞—Ö—Å—Ç–∞–Ω—É</span> ‚Äî –±—ã—Å—Ç—Ä–æ, —É–¥–æ–±–Ω–æ –∏ —Å –ª—é–±–æ–≤—å—é –∫ –∫–∞–∂–¥–æ–º—É –∑–∞–∫–∞–∑—É.
                  –í–∞—à –∏–¥–µ–∞–ª—å–Ω—ã–π –∞—Ä–æ–º–∞—Ç —É–∂–µ –≤ –ø—É—Ç–∏!
                </p>
              </div>
            </div>
          </section>

          {/* Video Section */}
          <section 
            className="mt-24 flex justify-center items-center opacity-0 transform translate-y-8 transition-all duration-1000 ease-out delay-[1000ms]"
            ref={el => sectionsRef.current[4] = el}
          >
            <div 
              className="relative w-full max-w-4xl aspect-video rounded-xl overflow-hidden shadow-2xl border border-gray-700 cursor-pointer"
              onClick={handleVideoClick}
            >
              <video 
                className="w-full h-full object-cover" 
                autoPlay 
                muted 
                loop 
                playsInline
              >
                <source src="/assets/astana.mp4" type="video/mp4" />
                Your browser does not support the video tag.
              </video>
              <div className="absolute inset-0 border border-white/10 rounded-xl pointer-events-none"></div>
              <div className="absolute bottom-4 right-4 bg-black/60 text-white text-sm px-3 py-1 rounded-full shadow">
                {isPlaying ? "–ú—É–∑—ã–∫–∞ –≤–∫–ª—é—á–µ–Ω–∞ üéµ" : "–ù–∞–∂–º–∏—Ç–µ, —á—Ç–æ–±—ã –≤–∫–ª—é—á–∏—Ç—å –º—É–∑—ã–∫—É"}
              </div>
            </div>

            {/* Audio Tracks */}
            <audio ref={musicRef} loop>
              <source src="/assets/music.mp3" type="audio/mpeg" />
              Your browser does not support the audio element.
            </audio>

            <audio ref={aboutMusicRef} autoPlay loop>
              <source src="/assets/about.mp3" type="audio/mpeg" />
              Your browser does not support the audio element.
            </audio>
          </section>

          {/* Philosophy */}
          <section 
            ref={el => sectionsRef.current[3] = el}
            className="opacity-0 transform translate-y-8 transition-all duration-1000 ease-out delay-800"
          >
            <div className="max-w-4xl">
              <div className="border-t border-gray-800 pt-16">
                <blockquote className="text-lg md:text-xl text-gray-400 italic leading-relaxed">
                  ¬´–ú–∞–≥–∏—è –ø—Ä–æ–∏—Å—Ö–æ–¥–∏—Ç, –∫–æ–≥–¥–∞ –∞–º–±–∏—Ü–∏–∏, –∏–∑—ã—Å–∫–∞–Ω–Ω—ã–π –∞—Ä–æ–º–∞—Ç –∏ –∏–Ω–Ω–æ–≤–∞—Ü–∏–æ–Ω–Ω—ã–µ —Ç–µ—Ö–Ω–æ–ª–æ–≥–∏–∏ —Å–æ–µ–¥–∏–Ω—è—é—Ç—Å—è. –ú—ã ‚Äî –≤–∞—à –∫—Ä–µ–∞—Ç–∏–≤–Ω—ã–π –∫–∞—Ç–∞–ª–∏–∑–∞—Ç–æ—Ä –≤ –º–∏—Ä–µ –ø–∞—Ä—Ñ—é–º–µ—Ä–∏–∏.¬ª
                </blockquote>
              </div>
            </div>
          </section>
        </main>
      </div>
    </div>
  );
};

export default About;
