import React from "react";
import styled from "styled-components";

const AboutContainer = styled.section`
  background-color: #000;
  color: #fff;
  padding: 180px 10vw 120px;
  font-family: 'Inter', sans-serif;
  overflow-x: hidden;
`;

const Header = styled.h1`
  font-size: 7vw;
  font-weight: 600;
  line-height: 1.1;
  margin-bottom: 10vh;
  letter-spacing: -0.03em;
  white-space: pre-line;
`;

const Section = styled.div`
  margin-bottom: 120px;
  max-width: 1100px;
`;

const Title = styled.h2`
  font-size: 2rem;
  font-weight: 500;
  margin-bottom: 24px;
`;

const Paragraph = styled.p`
  font-size: 1.25rem;
  line-height: 2;
  color: #aaa;
`;

const About = () => {
  return (
    <AboutContainer>
      <Header>
        A digital design & development{'\n'}
        studio creating spatial web experiences.
      </Header>

      <Section>
        <Title>What we do</Title>
        <Paragraph>
          We work at the intersection of design, technology, and emotion. Our projects include
          immersive websites, digital experiences, and spatial storytelling for brands and creators.
        </Paragraph>
      </Section>

      <Section>
        <Title>How we think</Title>
        <Paragraph>
          Our process blends curiosity and precision. We collaborate deeply, iterate boldly, and
          design with intention. Every interaction is crafted with care to leave a lasting impression.
        </Paragraph>
      </Section>

      <Section>
        <Title>Why we create</Title>
        <Paragraph>
          We believe the web should be felt. That design can move people. That stories deserve to
          unfold in unexpected ways. Weâ€™re here to build experiences that resonate and endure.
        </Paragraph>
      </Section>
    </AboutContainer>
  );
};

export default About;
